import React, { useState, useEffect } from "react";
import { Container, Form, Button } from "react-bootstrap";
import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000';
const GOOGLE_API_KEY = process.env.REACT_APP_GOOGLE_API_KEY;

const SignUp = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [googleApiLoaded, setGoogleApiLoaded] = useState(false);

  useEffect(() => {
    // Load Google API client
    const loadGoogleApi = () => {
      const script = document.createElement('script');
      script.src = `https://apis.google.com/js/api.js`;
      script.onload = () => {
        window.gapi.load('client', initGoogleClient);
      };
      document.body.appendChild(script);
    };

    const initGoogleClient = () => {
      window.gapi.client.init({
        apiKey: GOOGLE_API_KEY,
        discoveryDocs: ['https://www.googleapis.com/discovery/v1/apis/people/v1/rest'],
      }).then(() => {
        setGoogleApiLoaded(true);
      }).catch(error => {
        console.error("Error initializing Google API client:", error);
      });
    };

    loadGoogleApi();
  }, []);

  const redirect_login = () => {
    window.location.href = "/login";
  };

  const handleSignUp = async (e) => {
    e.preventDefault();

    try {
      const resp = await axios.post(`${BACKEND_URL}/signup`, {
        username,
        email,
        password,
      },
        { withCredentials: true }
      );

      if (resp.status === 201) {
        alert("Successfully Signed Up! Please Sign In to Enjoy the Services!!");
        window.location.href = "/login";
      }
    } catch (err) {
      alert("Error signing up", err);
      console.log(err);
    }
  };

  const handleGoogleSignUp = async () => {
    if (!googleApiLoaded) {
      alert("Google API is still loading. Please try again in a moment.");
      return;
    }

    try {
      // Request permission to access Google API
      const authInstance = window.gapi.auth2.getAuthInstance();
      const googleUser = await authInstance.signIn();
      const profile = googleUser.getBasicProfile();

      const email = profile.getEmail();
      const name = profile.getName();

      try {
        const registerResp = await axios.post(`${BACKEND_URL}/signup`, {
          email,
          username: name,
          password: generateSecurePassword(), // Generate a secure random password
        },
          { withCredentials: true }
        );

        if (registerResp.status === 201) {
          alert("Successfully Signed Up! Please Sign In to Enjoy the Services!!");
          window.location.href = "/login";
        }
      } catch (registerErr) {
        alert("Error signing up with Google", registerErr);
        console.log(registerErr);
      }
    } catch (err) {
      console.error("Google authentication error:", err);
      if (err.error === "popup_closed_by_user") {
        alert("Popup closed before completing login.");
      } else {
        alert("Error signing up with Google. Please try again or use email signup.");
      }
    }
  };

  // Generate a secure random password for Google sign-up
  const generateSecurePassword = () => {
    const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()";
    let password = "";
    for (let i = 0; i < 16; i++) {
      password += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return password;
  };

  return (
    <Container className="signup-box">
      <h1>Sign Up</h1>
      <div className="google-loginbox">
        <Button
          className="google-signup-button"
          onClick={handleGoogleSignUp}
          disabled={!googleApiLoaded}
        >
          Signup with Google
        </Button>
      </div>
      <div className="login-form">
        <Form onSubmit={handleSignUp}>
          <Form.Group controlId="formBasicName">
            <Form.Control
              className="username"
              placeholder="Enter User Name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicEmail">
            <Form.Control
              className="email"
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicPassword">
            <Form.Control
              className="password"
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          <Button className="signup-button" type="submit" variant="primary">
            SignUp
          </Button>
        </Form>
      </div>
      <div className="noaccount">
        <p>
          Already have an account?{" "}
          <span className="gotosignup" onClick={redirect_login}>
            Sign In
          </span>
        </p>
      </div>
    </Container>
  );
};

export default SignUp;
