import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import 'leaflet/dist/leaflet.css';
import './App.css';

import Login from './components/login';
import Register from './components/signup';
import MyLocation from './components/MyLocation';
import AdminDashboard from './components/AdminDashboard';
// import Navbar from './components/Navbar';
import Home from './components/Home';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check for user in localStorage
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const updateUser = (userData) => {
    setUser(userData);
    if (userData) {
      localStorage.setItem('user', JSON.stringify(userData));
    } else {
      localStorage.removeItem('user');
    }
  };

  return (
    <Router>
      <Navbar user={user} setUser={updateUser} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={!user ? <Login setUser={updateUser} /> : <Navigate to="/" />} />
        <Route path="/signup" element={!user ? <Register setUser={updateUser} /> : <Navigate to="/" />} />
        <Route 
          path="/mylocation" 
          element={user ? <MyLocation user={user} /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/admin" 
          element={user && user.isAdmin ? <AdminDashboard user={user} /> : <Navigate to="/" />} 
        />
      </Routes>
    </Router>
  );
}

export default App;
