import axios from 'axios';
const API_URL = process.env.REACT_APP_API_URL || 'http://10.5.18.69:5000';


const apiConfig = {
  baseURL: API_URL,
  endpoints: {
    // Remove redundant auth endpoints, keeping only what's needed
    // Admin-related
    adminBuses: '/admin/buses',
    adminAddBus: '/admin/buses/add',
    adminUpdateBus: (id) => `/admin/buses/${id}/update`,
    adminDeleteBus: (id) => `/api/admin/buses/${id}/delete`,

    adminStops: '/api/admin/stops',
    adminAddStop: '/api/admin/stops/add',
    adminUpdateStop: (id) => `/api/admin/stops/${id}/update`,
    adminDeleteStop: (id) => `/api/admin/stops/${id}/delete`,

    adminRoutes: '/api/admin/routes',
    adminRouteById: (id) => `/api/admin/routes/${id}`,
    adminAddRoute: '/api/admin/routes/add',
    adminUpdateRoute: (id) => `/api/admin/routes/${id}/update`,
    adminDeleteRoute: (id) => `/api/admin/routes/${id}/delete`,

    adminDrivers: '/api/admin/drivers',
    adminAddDriver: '/api/admin/drivers/add',
    adminUpdateDriver: (id) => `/api/admin/drivers/${id}/update`,
    adminDeleteDriver: (id) => `/api/admin/drivers/${id}/delete`,

    adminStats: '/api/admin/statistics',
    adminUserLocations: '/api/admin/users/locations',

    // Location endpoints - keep these as they might be needed
    userLocation: '/api/location/mylocation',
    updateLocation: '/api/location/update',
    busLocations: '/api/location/buses',
    allRoutes: '/api/routes',
  }
};

export const getApiUrl = (endpoint) => {
  if (typeof endpoint === 'function') {
    // If endpoint is a function (like adminUpdateBus), it needs to be called with arguments
    return `${apiConfig.baseURL}${endpoint()}`;
  }

  // Handle direct string endpoints
  return `${apiConfig.baseURL}${endpoint}`;
};

// New helper to handle API calls with better error handling
export const callApi = async (endpoint, options = {}) => {
  try {
    const url = getApiUrl(endpoint);
    const response = await axios({
      url,
      ...options,
      timeout: options.timeout || 15000, // Default timeout of 15 seconds
    });
    return response.data;
  } catch (error) {
    if (error.code === 'ECONNABORTED' || error.message.includes('timeout')) {
      console.error('API request timed out:', endpoint);
      throw new Error('Request timed out. The server might be temporarily unavailable.');
    }
    throw error;
  }
};

export default apiConfig;
