import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap, Polyline, Circle } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import api from '../../utils/api.js';
import '../../css/busStopsView.css';
import axios from 'axios';

// Fix for default marker icons in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
    iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png'
});

// Custom icons for different map elements
const busStopIcon = new L.Icon({
    iconUrl: 'https://cdn-icons-png.flaticon.com/512/1165/1165895.png',
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32]
});

const userIcon = new L.Icon({
    iconUrl: 'https://cdn-icons-png.flaticon.com/512/1077/1077114.png',
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32]
});

const destinationIcon = new L.Icon({
    iconUrl: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32]
});

const nearbyStopIcon = new L.Icon({
    iconUrl: 'https://cdn-icons-png.flaticon.com/512/3448/3448339.png',
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32]
});

const busIcon = new L.Icon({
    iconUrl: 'https://cdn-icons-png.flaticon.com/512/30/30979.png',
    iconSize: [32, 32],
    iconAnchor: [16, 16],
    popupAnchor: [0, -16]
});

// Location control component
const LocationControl = ({ setUserLocation }) => {
    const map = useMap();

    const handleLocationRequest = () => {
        map.locate({ setView: true, maxZoom: 16 });
    };

    useEffect(() => {
        map.on('locationfound', (e) => {
            setUserLocation([e.latlng.lat, e.latlng.lng]);
            map.flyTo(e.latlng, 16);
        });

        map.on('locationerror', (e) => {
            console.error("Location error:", e.message);
            alert("Unable to find your location. Please check your device settings.");
        });

        return () => {
            map.off('locationfound');
            map.off('locationerror');
        };
    }, [map, setUserLocation]);

    return (
        <div className="location-control">
            <button onClick={handleLocationRequest} title="Show my location">
                <i className="fas fa-location-arrow"></i>
            </button>
        </div>
    );
};

// Search control component
const SearchControl = ({ onSearchResult }) => {
    const [searchValue, setSearchValue] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [isSearching, setIsSearching] = useState(false);
    const [showResults, setShowResults] = useState(false);
    const map = useMap();

    const handleSearch = async () => {
        if (!searchValue.trim()) return;

        setIsSearching(true);
        setShowResults(false);

        try {
            const response = await fetch(
                `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchValue)}`
            );

            if (!response.ok) {
                throw new Error('Search failed');
            }

            const data = await response.json();

            if (data && data.length > 0) {
                setSearchResults(data.slice(0, 5)); // Limit to 5 results
                setShowResults(true);
            } else {
                alert('No results found for your search');
            }
        } catch (error) {
            console.error('Error searching location:', error);
            alert('Failed to search location. Please try again.');
        } finally {
            setIsSearching(false);
        }
    };

    const handleResultClick = (result) => {
        const position = [parseFloat(result.lat), parseFloat(result.lon)];
        map.flyTo(position, 16);
        onSearchResult(position, result.display_name);
        setShowResults(false);
        setSearchValue(result.display_name.split(',')[0]); // Just show the first part of the name
    };

    return (
        <div className="search-control">
            <input
                type="text"
                placeholder="Search for a location..."
                value={searchValue}
                onChange={(e) => setSearchValue(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            />
            <button onClick={handleSearch} disabled={isSearching}>
                {isSearching ? '...' : 'Search'}
            </button>

            {showResults && searchResults.length > 0 && (
                <div className="search-results">
                    {searchResults.map((result, index) => (
                        <div 
                            key={index} 
                            className="search-result-item"
                            onClick={() => handleResultClick(result)}
                        >
                            {result.display_name}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

// OSRM routing component
const RouteDisplay = ({ startPoint, endPoint, color = '#3388ff', weight = 4 }) => {
    const [routeCoordinates, setRouteCoordinates] = useState([]);
    const map = useMap();

    useEffect(() => {
        if (!startPoint || !endPoint) return;

        const fetchRoute = async () => {
            try {
                const response = await axios.get(
                    `https://router.project-osrm.org/route/v1/driving/${startPoint[1]},${startPoint[0]};${endPoint[1]},${endPoint[0]}?overview=full&geometries=geojson`
                );

                if (response.data.code === 'Ok' && response.data.routes.length > 0) {
                    const routeGeometry = response.data.routes[0].geometry.coordinates;
                    // OSRM returns coordinates as [lng, lat], we need to flip for Leaflet
                    const coordinates = routeGeometry.map(coord => [coord[1], coord[0]]);
                    setRouteCoordinates(coordinates);

                    // Fit the map to show the whole route
                    if (coordinates.length > 0) {
                        const bounds = L.latLngBounds(coordinates);
                        map.fitBounds(bounds, { padding: [50, 50] });
                    }
                }
            } catch (error) {
                console.error('Error fetching route:', error);
            }
        };

        fetchRoute();
    }, [startPoint, endPoint, map]);

    return routeCoordinates.length > 0 ? (
        <Polyline positions={routeCoordinates} color={color} weight={weight} />
    ) : null;
};

// Initial location marker component
function LocationMarker({ userLocation, setUserLocation }) {
    const map = useMap();

    useEffect(() => {
        if (!userLocation) {
            map.locate({ setView: true, maxZoom: 16 });

            map.on('locationfound', (e) => {
                setUserLocation([e.latlng.lat, e.latlng.lng]);
                map.flyTo(e.latlng, 16);
            });

            map.on('locationerror', (e) => {
                console.log("Location error:", e.message);
                // Default to a location if geolocation fails
                setUserLocation([22.3149, 87.3104]); // IIT Kharagpur coordinates
                map.flyTo([22.3149, 87.3104], 14);
            });
        }
    }, [map, userLocation, setUserLocation]);

    return userLocation ? (
        <Marker position={userLocation} icon={userIcon}>
            <Popup>
                <strong>Your current location</strong>
            </Popup>
        </Marker>
    ) : null;
}

// MapController component to adjust map view
const MapController = ({ busStops, center, zoom }) => {
    const map = useMap();

    useEffect(() => {
        if (center && zoom) {
            map.setView(center, zoom);
        } else if (busStops && busStops.length > 0) {
            // Create bounds to fit all bus stops
            const bounds = L.latLngBounds([]);
            busStops.forEach(stop => {
                if (stop.latitude && stop.longitude) {
                    bounds.extend([parseFloat(stop.latitude), parseFloat(stop.longitude)]);
                }
            });

            // Only fit bounds if we have valid coordinates
            if (bounds.isValid()) {
                map.fitBounds(bounds, { padding: [50, 50] });
            }
        }
    }, [busStops, map, center, zoom]);

    return null;
};

// Component for bus stop markers
const BusStopMarkers = ({ busStops, nearbyStops }) => {
    // Function to check if a stop is in the nearby stops array
    const isNearbyStop = (stopId) => {
        return nearbyStops && nearbyStops.some(stop => stop.id === stopId);
    };

    return (
        <>
            {busStops.map((stop, index) => {
                // Skip invalid coordinates
                if (!stop.latitude || !stop.longitude) {
                    return null;
                }

                const position = [
                    parseFloat(stop.latitude), 
                    parseFloat(stop.longitude)
                ];

                // Validate coordinates
                if (isNaN(position[0]) || isNaN(position[1])) {
                    return null;
                }

                const isNearby = isNearbyStop(stop.id);

                return (
                    <Marker 
                        key={`bus-stop-${stop.id || index}`}
                        position={position}
                        icon={isNearby ? nearbyStopIcon : busStopIcon}
                    >
                        <Popup>
                            <div className="bus-stop-popup">
                                <h3>{stop.name || `Bus Stop ${index + 1}`}</h3>
                                <p><strong>ID:</strong> {stop.id || 'Unknown'}</p>
                                <p><strong>Coordinates:</strong> {stop.latitude}, {stop.longitude}</p>
                                {isNearby && (
                                    <p className="nearby-note"><strong>Nearby your location</strong></p>
                                )}
                            </div>
                        </Popup>
                    </Marker>
                );
            })}
        </>
    );
};

const BusStopsView = ({ userLocation, setUserLocation }) => {
    const [currentLocation, setCurrentLocation] = useState('Your current location');
    const [destinationLocation, setDestinationLocation] = useState('');
    const [searchDestination, setSearchDestination] = useState(null);
    const [mapCenter, setMapCenter] = useState(userLocation);
    const [mapZoom, setMapZoom] = useState(14);
    const [busStops, setBusStops] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchLocation, setSearchLocation] = useState(null);
    const [searchName, setSearchName] = useState('');
    const [nearbyStops, setNearbyStops] = useState([]);
    const [destinationStops, setDestinationStops] = useState([]);
    const [availableRoutes, setAvailableRoutes] = useState([]);
    const [selectedRoute, setSelectedRoute] = useState(null);
    const [isRouteFinding, setIsRouteFinding] = useState(false);
    const [showRangeCircle, setShowRangeCircle] = useState(false);
    const [buses, setBuses] = useState([]);
    const [activeBus, setActiveBus] = useState(null);

    const NEARBY_DISTANCE = 250; // 250 meters radius for nearby stops

    // Fetch bus stops
    useEffect(() => {
        const fetchBusStops = async () => {
            try {
                setIsLoading(true);
                console.log('Fetching bus stops from API');

                const response = await api.get('/bus_stops/getAllBusStops');

                if (response.data && response.data.data) {
                    if (Array.isArray(response.data.data)) {
                        setBusStops(response.data.data);
                        console.log(`Loaded ${response.data.data.length} bus stops`);
                    } else {
                        throw new Error('Bus stops data is not in expected array format');
                    }
                } else {
                    throw new Error('Invalid API response format');
                }
            } catch (err) {
                console.error('Error fetching bus stops:', err);
                setError(`Failed to load bus stops: ${err.message}`);
                setBusStops([]);
            } finally {
                setIsLoading(false);
            }
        };

        fetchBusStops();
        fetchBuses();
    }, []);

    // Fetch buses
    const fetchBuses = async () => {
        try {
            const response = await api.get('/buses/getAllBuses');
            if (response.data && response.data.data) {
                setBuses(response.data.data);
                console.log(`Loaded ${response.data.data.length} buses`);
            }
        } catch (err) {
            console.error('Error fetching buses:', err);
            // Not setting an error state as buses are optional for initial view
            setBuses([]);
        }
    };

    // Calculate distance between two points in meters using Haversine formula
    const calculateDistance = (lat1, lon1, lat2, lon2) => {
        const R = 6371e3; // Earth's radius in meters
        const φ1 = lat1 * Math.PI / 180;
        const φ2 = lat2 * Math.PI / 180;
        const Δφ = (lat2 - lat1) * Math.PI / 180;
        const Δλ = (lon2 - lon1) * Math.PI / 180;

        const a = Math.sin(Δφ/2) * Math.sin(Δφ/2) +
                Math.cos(φ1) * Math.cos(φ2) *
                Math.sin(Δλ/2) * Math.sin(Δλ/2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));

        return R * c; // Distance in meters
    };

    // Find nearby stops when current location changes
    useEffect(() => {
        if (!userLocation) return;

        const findNearbyStops = () => {
            const nearby = busStops.filter(stop => {
                if (!stop.latitude || !stop.longitude) return false;

                const distance = calculateDistance(
                    userLocation[0],
                    userLocation[1],
                    parseFloat(stop.latitude),
                    parseFloat(stop.longitude)
                );

                return distance <= NEARBY_DISTANCE;
            });

            console.log(`Found ${nearby.length} stops within ${NEARBY_DISTANCE}m of your location`);
            setNearbyStops(nearby);
        };

        if (busStops.length > 0) {
            findNearbyStops();
        }
    }, [userLocation, busStops]);

    // Handle search location results for destination
    const handleSearchResult = (position, name) => {
        setSearchDestination(position);
        setDestinationLocation(name.split(',')[0]); // Set first part of the name as destination

        // Find stops near the search location
        const nearby = busStops.filter(stop => {
            if (!stop.latitude || !stop.longitude) return false;

            const distance = calculateDistance(
                position[0],
                position[1],
                parseFloat(stop.latitude),
                parseFloat(stop.longitude)
            );

            return distance <= NEARBY_DISTANCE;
        });

        console.log(`Found ${nearby.length} stops within ${NEARBY_DISTANCE}m of the destination`);
        setDestinationStops(nearby);
        setShowRangeCircle(true);

        // After 5 seconds, hide the range circle
        setTimeout(() => {
            setShowRangeCircle(false);
        }, 5000);
    };

    // Function to find routes between stops
    const findRoutes = async () => {
        if (nearbyStops.length === 0 || destinationStops.length === 0) {
            setError('No bus stops found near your location or destination. Try searching for a different destination or move to a location closer to a bus stop.');
            return;
        }

        setIsRouteFinding(true);
        setError(null);
        setAvailableRoutes([]);

        try {
            // Clear any previous selection
            setSelectedRoute(null);
            setActiveBus(null);

            // This would normally be an API call to your backend
            // For demonstration purposes, we'll create sample routes
            // Based on the available buses and the nearby stops

            setTimeout(() => {
                // Simulate API call delay
                const routes = [];

                buses.forEach(bus => {
                    // For each bus, check if it passes both through a start and end stop
                    // This is simplified logic - in a real app, you'd query your backend
                    const startStop = nearbyStops[0];
                    const endStop = destinationStops[0];

                    if (startStop && endStop && startStop.id !== endStop.id) {
                        routes.push({
                            id: `route-${bus.id}-${startStop.id}-${endStop.id}`,
                            busId: bus.id,
                            busName: bus.name,
                            startStop: startStop,
                            endStop: endStop,
                            intermediateStops: Math.floor(Math.random() * 5) + 1, // Random for demo
                            driverName: 'John Driver', // Normally get from API
                            estimatedTime: `${Math.floor(Math.random() * 30) + 5} mins` // Random for demo
                        });
                    }
                });

                if (routes.length === 0) {
                    setError('No buses available for your selected route. Try different locations.');
                } else {
                    setAvailableRoutes(routes);
                }

                setIsRouteFinding(false);
            }, 1500);

        } catch (err) {
            console.error('Error finding routes:', err);
            setError(`Failed to find routes: ${err.message}`);
            setIsRouteFinding(false);
        }
    };

    // Handle selecting a route and displaying on map
    const handleSelectRoute = (route) => {
        setSelectedRoute(route);

        // Find the bus associated with this route
        const selectedBus = buses.find(bus => bus.id === route.busId);
        if (selectedBus) {
            setActiveBus(selectedBus);
        }

        // Simulate bus location at start stop for demonstration
        const startPos = [parseFloat(route.startStop.latitude), parseFloat(route.startStop.longitude)];
        const endPos = [parseFloat(route.endStop.latitude), parseFloat(route.endStop.longitude)];

        // Fit map to show the entire route
        const bounds = L.latLngBounds([startPos, endPos, userLocation]);
        const mapInstance = document.querySelector('.leaflet-container')?._leaflet_map;
        if (mapInstance) {
            mapInstance.fitBounds(bounds, { padding: [50, 50] });
        }
    };

    return (
        <div className="bus-stops-view">
            <div className="control-panel">
                <div className="location-form">
                    <div className="form-group">
                        <label>From</label>
                        <input 
                            type="text" 
                            value={currentLocation}
                            onChange={(e) => setCurrentLocation(e.target.value)}
                            placeholder="Your current location"
                            disabled={true} // This is auto-detected
                        />
                        <span className="current-location-label">
                            <i className="fas fa-location-arrow"></i> Current
                        </span>
                    </div>

                    <div className="form-group">
                        <label>To</label>
                        <input 
                            type="text" 
                            value={destinationLocation}
                            onChange={(e) => setDestinationLocation(e.target.value)}
                            placeholder="Where do you want to go?"
                        />
                        <p className="small-hint">
                            <i className="fas fa-info-circle"></i> Search for a destination on the map or type a location name
                        </p>
                    </div>

                    <button 
                        className="search-btn" 
                        onClick={findRoutes}
                        disabled={isRouteFinding || !userLocation || !destinationLocation}
                    >
                        {isRouteFinding ? 'Finding Routes...' : 'Find Routes'}
                    </button>
                </div>

                {/* Error message */}
                {error && <div className="error-message">{error}</div>}

                {/* Available routes section */}
                {availableRoutes.length > 0 && (
                    <div className="route-list">
                        <h3>Available Routes ({availableRoutes.length})</h3>
                        {availableRoutes.map(route => (
                            <div key={route.id} className="route-item">
                                <div className="route-item-header">
                                    <div className="route-item-title">
                                        {route.busName}
                                    </div>
                                </div>
                                <div className="route-item-details">
                                    <div>
                                        <i className="fas fa-map-marker-alt"></i> 
                                        From: {route.startStop.name}
                                    </div>
                                    <div>
                                        <i className="fas fa-map-marker"></i> 
                                        To: {route.endStop.name}
                                    </div>
                                    <div>
                                        <i className="fas fa-user"></i> 
                                        Driver: {route.driverName}
                                    </div>
                                    <div>
                                        <i className="fas fa-clock"></i> 
                                        ETA: {route.estimatedTime}
                                    </div>
                                    <div>
                                        <i className="fas fa-route"></i> 
                                        Stops: {route.intermediateStops}
                                    </div>
                                </div>
                                <div className="route-item-actions">
                                    <button 
                                        onClick={() => handleSelectRoute(route)}
                                        className={selectedRoute?.id === route.id ? 'active' : ''}
                                    >
                                        {selectedRoute?.id === route.id ? 'Selected' : 'Show Route'}
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}

                {/* Bus stops list when no active search */}
                {availableRoutes.length === 0 && !isRouteFinding && (
                    <div className="bus-stops-list">
                        <h3>Bus Stops Near You</h3>
                        {isLoading ? (
                            <p>Loading bus stops from database...</p>
                        ) : error && !availableRoutes.length ? (
                            <p className="error-message">{error}</p>
                        ) : nearbyStops.length === 0 ? (
                            <p>No bus stops found near your current location.</p>
                        ) : (
                            <ul>
                                {nearbyStops.map((stop, index) => (
                                    <li key={stop.id || index} onClick={() => {
                                        const position = [parseFloat(stop.latitude), parseFloat(stop.longitude)];
                                        setMapCenter(position);
                                        setMapZoom(17);
                                    }}>
                                        <strong>{index + 1}.</strong> {stop.name || 'Unnamed Stop'} 
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                )}
            </div>

            <div className="map-container">
                {isRouteFinding && (
                    <div className="map-loading-overlay">
                        <div className="spinner"></div>
                        <p>Finding the best routes for you...</p>
                    </div>
                )}

                <MapContainer 
                    center={mapCenter || [22.3149, 87.3104]} 
                    zoom={mapZoom || 14} 
                    style={{ height: "100%", width: "100%" }}
                >
                    <TileLayer
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />

                    {/* Add location controls */}
                    <LocationControl setUserLocation={setUserLocation} />
                    <SearchControl onSearchResult={handleSearchResult} />

                    {/* User's location marker */}
                    <LocationMarker userLocation={userLocation} setUserLocation={setUserLocation} />

                    {/* Map view controller */}
                    <MapController busStops={busStops} center={mapCenter} zoom={mapZoom} />

                    {/* Bus stops markers */}
                    <BusStopMarkers busStops={busStops} nearbyStops={[...nearbyStops, ...destinationStops]} />

                    {/* Destination marker if set */}
                    {searchDestination && (
                        <Marker position={searchDestination} icon={destinationIcon}>
                            <Popup>
                                <strong>Destination</strong><br />
                                {destinationLocation}
                            </Popup>
                        </Marker>
                    )}

                    {/* Range circles when searching */}
                    {showRangeCircle && searchDestination && (
                        <Circle 
                            center={searchDestination} 
                            radius={NEARBY_DISTANCE} 
                            pathOptions={{ color: 'blue', fillColor: 'blue', fillOpacity: 0.1 }}
                        />
                    )}

                    {/* Show range circle around user location */}
                    {showRangeCircle && userLocation && (
                        <Circle 
                            center={userLocation} 
                            radius={NEARBY_DISTANCE} 
                            pathOptions={{ color: 'green', fillColor: 'green', fillOpacity: 0.1 }}
                        />
                    )}

                    {/* OSRM routes when a route is selected */}
                    {selectedRoute && userLocation && (
                        <>
                            {/* Route from user to start stop */}
                            <RouteDisplay
                                startPoint={userLocation}
                                endPoint={[
                                    parseFloat(selectedRoute.startStop.latitude),
                                    parseFloat(selectedRoute.startStop.longitude)
                                ]}
                                color="#4285f4"
                                weight={4}
                            />

                            {/* Route from start to end stop */}
                            <RouteDisplay
                                startPoint={[
                                    parseFloat(selectedRoute.startStop.latitude),
                                    parseFloat(selectedRoute.startStop.longitude)
                                ]}
                                endPoint={[
                                    parseFloat(selectedRoute.endStop.latitude),
                                    parseFloat(selectedRoute.endStop.longitude)
                                ]}
                                color="#ea4335"
                                weight={5}
                            />

                            {/* Simulate bus at start stop */}
                            <Marker 
                                position={[
                                    parseFloat(selectedRoute.startStop.latitude),
                                    parseFloat(selectedRoute.startStop.longitude)
                                ]} 
                                icon={busIcon}
                            >
                                <Popup>
                                    <div>
                                        <h3>{selectedRoute.busName}</h3>
                                        <p><strong>Status:</strong> At {selectedRoute.startStop.name}</p>
                                        <p><strong>Next:</strong> {selectedRoute.endStop.name}</p>
                                        <p><strong>ETA:</strong> {selectedRoute.estimatedTime}</p>
                                    </div>
                                </Popup>
                            </Marker>
                        </>
                    )}
                </MapContainer>

                {/* Legend when route is displayed */}
                {selectedRoute && (
                    <div className="map-legend">
                        <div className="legend-item">
                            <div className="legend-icon" style={{ backgroundColor: '#4285f4' }}></div>
                            <span>Your path to bus stop</span>
                        </div>
                        <div className="legend-item">
                            <div className="legend-icon" style={{ backgroundColor: '#ea4335' }}></div>
                            <span>Bus route</span>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default BusStopsView;
