import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://10.5.18.69:5000';
const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000';

// Create an Axios instance with default config
const api = axios.create({
  baseURL: BACKEND_URL,
  withCredentials: true,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Request interceptor to add auth token
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('jwtToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
}, (error) => {
  return Promise.reject(error);
});

// Response interceptor for error handling
api.interceptors.response.use((response) => {
  return response;
}, (error) => {
  console.error('API Error:', error);

  // Handle 401 by redirecting to login
  if (error.response && error.response.status === 401) {
    localStorage.removeItem('jwtToken');
    localStorage.removeItem('user');
    window.location.href = '/login';
  }

  return Promise.reject(error);
});

// API endpoints configuration
const apiConfig = {
  baseURL: API_URL,
  endpoints: {
    // Authentication (removed unnecessary adminLogin endpoint)
    login: '/api/auth/login',
    register: '/api/auth/register',
    logout: '/api/auth/logout',

    // User-related
    userProfile: '/api/users/profile',
    updateProfile: '/api/users/profile/update',

    // Location endpoints
    userLocation: '/api/location/mylocation',
    updateLocation: '/api/location/update',
    busLocations: '/api/location/buses',
    allRoutes: '/api/routes',

    // Admin-related
    adminBuses: '/api/admin/buses',
    adminAddBus: '/api/admin/buses/add',
    adminUpdateBus: (id) => `/api/admin/buses/${id}/update`,
    adminDeleteBus: (id) => `/api/admin/buses/${id}/delete`,

    adminStops: '/api/admin/stops',
    adminAddStop: '/api/admin/stops/add',
    adminUpdateStop: (id) => `/api/admin/stops/${id}/update`,
    adminDeleteStop: (id) => `/api/admin/stops/${id}/delete`,

    adminRoutes: '/api/admin/routes',
    adminRouteById: (id) => `/api/admin/routes/${id}`,
    adminAddRoute: '/api/admin/routes/add',
    adminUpdateRoute: (id) => `/api/admin/routes/${id}/update`,
    adminDeleteRoute: (id) => `/api/admin/routes/${id}/delete`,

    adminDrivers: '/api/admin/drivers',
    adminAddDriver: '/api/admin/drivers/add',
    adminUpdateDriver: (id) => `/api/admin/drivers/${id}/update`,
    adminDeleteDriver: (id) => `/api/admin/drivers/${id}/delete`,

    adminStats: '/api/admin/statistics',
    adminUserLocations: '/api/admin/users/locations',
  }
};

// Helper to get API URL
