import React, { useState, useEffect } from 'react';
import '../../css/user.css';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import api from '../../utils/api.js';

// Fix for default marker icons in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
    iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png'
});

// Custom icons for bus stops
const busStopIcon = new L.Icon({
    iconUrl: 'https://cdn-icons-png.flaticon.com/512/1165/1165895.png',
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32]
});

// Component to handle location and center changes
function LocationMarker() {
    const [position, setPosition] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const map = useMap();

    useEffect(() => {
        map.locate({ setView: true, maxZoom: 16 });

        map.on('locationfound', (e) => {
            setPosition(e.latlng);
            setIsLoading(false);
            map.flyTo(e.latlng, 16);
        });

        map.on('locationerror', (e) => {
            console.log("Location error:", e.message);
            setIsLoading(false);
            // Default to a location (e.g., KGP campus) if geolocation fails
            setPosition([22.3149, 87.3104]); // IIT Kharagpur coordinates
            map.flyTo([22.3149, 87.3104], 14);
        });
    }, [map]);

    return position === null ? null : (
        <Marker position={position}>
            <Popup>You are here</Popup>
        </Marker>
    );
}

// MapController component to adjust map view when bus stops change
const MapController = ({ busStops }) => {
    const map = useMap();

    useEffect(() => {
        if (busStops && busStops.length > 0) {
            console.log(`MapController: Adjusting map for ${busStops.length} bus stops`);

            // Create bounds to fit all bus stops
            const bounds = L.latLngBounds([]);
            busStops.forEach(stop => {
                if (stop.latitude && stop.longitude) {
                    bounds.extend([parseFloat(stop.latitude), parseFloat(stop.longitude)]);
                }
            });

            // Only fit bounds if we have valid coordinates
            if (bounds.isValid()) {
                map.fitBounds(bounds, { padding: [50, 50] });
            }
        }
    }, [busStops, map]);

    return null; // This component doesn't render anything
};

// Separate component for bus stop markers to ensure proper rendering
const BusStopMarkers = ({ busStops }) => {
    console.log(`BusStopMarkers: Rendering ${busStops.length} markers`);

    return (
        <>
            {busStops.map((stop, index) => {
                // Skip invalid coordinates
                if (!stop.latitude || !stop.longitude) {
                    return null;
                }

                const position = [
                    parseFloat(stop.latitude), 
                    parseFloat(stop.longitude)
                ];

                // Validate coordinates
                if (isNaN(position[0]) || isNaN(position[1])) {
                    return null;
                }

                return (
                    <Marker 
                        key={`bus-stop-${stop.id || index}`}
                        position={position}
                        icon={busStopIcon}
                    >
                        <Popup>
                            <div className="bus-stop-popup">
                                <h3>{stop.name || `Bus Stop ${index + 1}`}</h3>
                                <p><strong>ID:</strong> {stop.id || 'Unknown'}</p>
                                <p><strong>Coordinates:</strong> {stop.latitude}, {stop.longitude}</p>
                                {stop.created_at && (
                                    <p><strong>Added:</strong> {new Date(stop.created_at).toLocaleString()}</p>
                                )}
                            </div>
                        </Popup>
                    </Marker>
                );
            })}
        </>
    );
};

const User = () => {
    const [currentLocation, setCurrentLocation] = useState('');
    const [destination, setDestination] = useState('');
    const [mapCenter, setMapCenter] = useState([22.3149, 87.3104]); // Default to IIT KGP
    const [mapZoom, setMapZoom] = useState(14);
    const [busStops, setBusStops] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [authError, setAuthError] = useState(null);

    // Fetch bus stops using the configured API utility
    useEffect(() => {
        const fetchBusStops = async () => {
            try {
                setIsLoading(true);
                console.log('Fetching bus stops from API');

                // Check if user is logged in
                const token = localStorage.getItem('jwtToken');
                if (!token) {
                    setAuthError('You must be logged in to view bus stops');
                    setIsLoading(false);
                    return;
                }

                const response = await api.get('/bus_stops/getAllBusStops');

                // Log the complete API response to see its structure
                console.log('Complete API Response:', response);
                console.log('Response data:', response.data);

                // Check the exact structure of the response
                if (response.data && response.data.data) {
                    console.log('Bus stops data array:', response.data.data);

                    // Make sure we're getting an array
                    if (Array.isArray(response.data.data)) {
                        setBusStops(response.data.data);
                        console.log(`Loaded ${response.data.data.length} bus stops`);

                        // Detailed logging of each stop
                        response.data.data.forEach((stop, index) => {
                            console.log(`Bus stop ${index + 1}:`, JSON.stringify(stop));
                        });
                    } else {
                        console.error('Bus stops data is not an array:', response.data.data);
                        throw new Error('Bus stops data is not in expected array format');
                    }
                } else {
                    console.error('Invalid API response format:', response.data);
                    throw new Error('Invalid API response format');
                }
            } catch (err) {
                console.error('Error fetching bus stops:', err);

                // Handle auth errors specifically
                if (err.response && err.response.status === 401) {
                    setAuthError('Authentication required. Please log in again.');
                } else if (err.response && err.response.status === 403) {
                    setAuthError('You do not have permission to access bus stops.');
                } else {
                    setError(`Failed to load bus stops: ${err.message}`);
                }

                setBusStops([]);
            } finally {
                setIsLoading(false);
            }
        };

        fetchBusStops();
    }, []);

    // Debug output of current state
    useEffect(() => {
        console.log('Current busStops state:', busStops);
        console.log('Number of bus stops in state:', busStops.length);
    }, [busStops]);

    // Function to handle finding routes
    const handleFindRoutes = () => {
        // This will be implemented later to find routes between locations
        console.log("Finding routes from", currentLocation, "to", destination);
    };

    return (
        <div className="map-interface">
            {/* Left panel for location controls */}
            <div className="location-panel">
                <h2>KGP Bus Tracker</h2>

                {authError && (
                    <div className="auth-error-message">
                        <p>{authError}</p>
                        <button onClick={() => window.location.href = '/login'}>
                            Go to Login
                        </button>
                    </div>
                )}

                <div className="location-form">
                    <div className="form-group">
                        <label>Current Location</label>
                        <input 
                            type="text" 
                            value={currentLocation}
                            onChange={(e) => setCurrentLocation(e.target.value)}
                            placeholder="Enter your current location"
                        />
                    </div>

                    <div className="form-group">
                        <label>Destination</label>
                        <input 
                            type="text" 
                            value={destination}
                            onChange={(e) => setDestination(e.target.value)}
                            placeholder="Where do you want to go?"
                        />
                    </div>

                    <button className="search-btn" onClick={handleFindRoutes}>Find Routes</button>
                </div>

                <div className="routes-container">
                    {/* Routes will be displayed here later */}
                    <p>Available routes will appear here after you search.</p>
                </div>

                {/* Show bus stops in the sidebar with debug info */}
                <div className="bus-stops-list">
                    <h3>Bus Stops from Database ({busStops.length})</h3>
                    {isLoading ? (
                        <p>Loading bus stops from database...</p>
                    ) : error ? (
                        <p className="error-message">{error}</p>
                    ) : busStops.length === 0 ? (
                        <p>No bus stops found in the database.</p>
                    ) : (
                        <>
                            <p className="debug-info">Found {busStops.length} bus stops</p>
                            <ul>
                                {busStops.map((stop, index) => (
                                    <li key={stop.id || index} onClick={() => {
                                        const position = [parseFloat(stop.latitude), parseFloat(stop.longitude)];
                                        setMapCenter(position);
                                        setMapZoom(17);
                                    }}>
                                        <strong>{index + 1}.</strong> {stop.name || 'Unnamed Stop'} 
                                        {stop.id ? ` (ID: ${stop.id})` : ''}
                                    </li>
                                ))}
                            </ul>
                        </>
                    )}
                </div>
            </div>

            {/* Right panel for the map */}
            <div className="map-container">
                <MapContainer 
                    center={mapCenter} 
                    zoom={mapZoom} 
                    style={{ height: "100%", width: "100%" }}
                >
                    <TileLayer
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <LocationMarker />
                    <MapController busStops={busStops} />
                    <BusStopMarkers busStops={busStops} />
                </MapContainer>

                {/* Add a debug panel to show raw data */}
                {process.env.NODE_ENV === 'development' && (
                    <div className="map-debug-panel">
                        <button onClick={() => console.log('Current bus stops:', busStops)}>
                            Log Bus Stops ({busStops.length})
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default User;
