import React, { useState, useEffect, useRef } from "react";
import { GoogleMap, Marker, InfoWindow, DirectionsRenderer, useJsApiLoader } from "@react-google-maps/api";
import api from "@/services/api";

const containerStyle = {
    width: "100%",
    height: "100vh",
};

const defaultCenter = { lat: 28.7041, lng: 77.1025 };

const BusStopSearch = () => {
    const { isLoaded } = useJsApiLoader({
        googleMapsApiKey: "YOUR_GOOGLE_MAPS_API_KEY",
        libraries: ["places"],
    });

    const mapRef = useRef(null);
    const [map, setMap] = useState(null);
    const [busStops, setBusStops] = useState([]);
    const [userLocation, setUserLocation] = useState(null);
    const [searchMarker, setSearchMarker] = useState(null);
    const [selectedBusStop, setSelectedBusStop] = useState(null);
    const [busStopToRedMarkerDirections, setBusStopToRedMarkerDirections] = useState(null);

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(
            (position) => {
                const userPos = {
                    lat: position.coords.latitude,
                    lng: position.coords.longitude,
                };
                setUserLocation(userPos);
                setSearchMarker(userPos); // Set red marker to user's location initially
            },
            () => {
                console.log("Error fetching user location");
            }
        );
    }, []);

    useEffect(() => {
        const fetchBusStops = async () => {
            try {
                const response = await api.get("/bus_stops/getAllBusStops");
                setBusStops(response.data);
            } catch (error) {
                console.error("Error fetching bus stops", error);
            }
        };
        fetchBusStops();
    }, []);

    const handleMapClick = (event) => {
        const clickedLocation = {
            lat: event.latLng.lat(),
            lng: event.latLng.lng(),
        };
        setSearchMarker(clickedLocation);
        setBusStopToRedMarkerDirections(null); // Clear previous path
    };

    const getDirections = (busStop) => {
        if (!searchMarker) return;
        const directionsService = new google.maps.DirectionsService();
        directionsService.route(
            {
                origin: { lat: busStop.lat, lng: busStop.lng },
                destination: searchMarker,
                travelMode: google.maps.TravelMode.WALKING,
            },
            (result, status) => {
                if (status === google.maps.DirectionsStatus.OK) {
                    setBusStopToRedMarkerDirections(result);
                } else {
                    console.error("Error fetching directions", status);
                }
            }
        );
    };

    return isLoaded ? (
        <GoogleMap
            mapContainerStyle={containerStyle}
            center={searchMarker || userLocation || defaultCenter}
            zoom={15}
            onLoad={(map) => {
                setMap(map);
            }}
            onClick={handleMapClick}
            options={{
                streetViewControl: true,
                fullscreenControl: true,
                zoomControl: true,
                mapTypeControl: true,
            }}
        >
            {/* User's Location Marker */}
            {userLocation && (
                <Marker position={userLocation} icon={{ url: "http://maps.google.com/mapfiles/ms/icons/blue-dot.png" }}>
                    <InfoWindow>
                        <div>
                            <h4>Your Location</h4>
                            <p>Lat: {userLocation.lat.toFixed(5)}</p>
                            <p>Lng: {userLocation.lng.toFixed(5)}</p>
                        </div>
                    </InfoWindow>
                </Marker>
            )}

            {/* Red Search Marker (Always Visible) */}
            {searchMarker && (
                <Marker position={searchMarker} icon={{ url: "http://maps.google.com/mapfiles/ms/icons/red-dot.png" }}>
                    <InfoWindow>
                        <div>
                            <h4>Search Location</h4>
                            <p>Lat: {searchMarker.lat.toFixed(5)}</p>
                            <p>Lng: {searchMarker.lng.toFixed(5)}</p>
                        </div>
                    </InfoWindow>
                </Marker>
            )}

            {/* Bus Stops */}
            {busStops.map((busStop, index) => (
                <Marker
                    key={index}
                    position={{ lat: busStop.lat, lng: busStop.lng }}
                    icon={{ url: "http://maps.google.com/mapfiles/ms/icons/yellow-dot.png" }}
                    onClick={() => {
                        setSelectedBusStop(busStop);
                        getDirections(busStop);
                    }}
                />
            ))}

            {/* Bus Stop InfoWindow */}
            {selectedBusStop && (
                <InfoWindow
                    position={{ lat: selectedBusStop.lat, lng: selectedBusStop.lng }}
                    onCloseClick={() => setSelectedBusStop(null)}
                >
                    <div>
                        <h4>{selectedBusStop.name}</h4>
                        <p>Lat: {selectedBusStop.lat.toFixed(5)}</p>
                        <p>Lng: {selectedBusStop.lng.toFixed(5)}</p>
                    </div>
                </InfoWindow>
            )}

            {/* Directions Renderer */}
            {busStopToRedMarkerDirections && <DirectionsRenderer directions={busStopToRedMarkerDirections} />}
        </GoogleMap>
    ) : (
        <p>Loading map...</p>
    );
};

export default BusStopSearch;
