import React, { useState, useEffect, useRef, useCallback } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents, useMap, Polyline } from 'react-leaflet';
import L from 'leaflet';
import api from '../../utils/api';
import 'leaflet-routing-machine';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import '../../css/user.css';

// Bus stop icon
const busStopIcon = new L.Icon({
    iconUrl: '/bus-stop.png', // Make sure this file exists in public folder
    iconSize: [24, 24],
    iconAnchor: [12, 24],
    popupAnchor: [0, -24]
});

// User location icon - BLUE color as requested
const userIcon = new L.Icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
});

// Search marker icon - RED color as requested
const searchMarkerIcon = new L.Icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
});

// Enhanced configuration for simpler Leaflet routing
function configureRoutingMachine() {
  if (typeof L !== 'undefined' && L.Routing) {
    // Set basic configuration options for simplified routing
    L.Routing.timeout = 5 * 1000; // 5 second timeout

    // Disable unnecessary features
    if (L.Routing.Control.prototype.options) {
      L.Routing.Control.prototype.options.showAlternatives = false;
      L.Routing.Control.prototype.options.useZoomParameter = false;
      L.Routing.Control.prototype.options.routeWhileDragging = false;
      L.Routing.Control.prototype.options.addWaypoints = false;
      L.Routing.Control.prototype.options.createMarker = null;
    }
  }
}

// Create a utility function to safely remove a routing control
const safelyRemoveRoutingControl = (routingControl, map) => {
  if (!routingControl || !map) return;

  try {
    // First try standard removal
    map.removeControl(routingControl);
  } catch (error) {
    console.warn("Safe removal failed, using alternative cleanup:", error);

    // Manual cleanup if standard removal fails
    if (routingControl._container && routingControl._container.parentNode) {
      routingControl._container.parentNode.removeChild(routingControl._container);
    }

    // Clear any route references
    if (routingControl._plan) {
      routingControl._plan._waypoints = [];
      routingControl._plan._dragMarkers = [];
    }
  }
};

// Route caching system to avoid repeated requests for the same route
const routeCache = new Map();

// Calculate cache key for routes
const getRouteCacheKey = (start, end) => {
  return `${start[0]},${start[1]}_${end[0]},${end[1]}`;
};

// Component that draws a simple straight line between two points as fallback
const DirectPathLine = ({ startPoint, endPoint, color = '#ff6b6b', weight = 3 }) => {
  if (!startPoint || !endPoint) return null;

  return (
    <Polyline 
      positions={[
        [startPoint[0], startPoint[1]],
        [endPoint[0], endPoint[1]]
      ]}
      color={color}
      weight={weight}
      dashArray="5, 10"
      opacity={0.7}
    />
  );
};

// Component to handle routing between points with animation
const RoutingControl = ({ startPoint, endPoint, color = '#3388ff', weight = 4, setIsPathLoading, onRoutingFailed }) => {
  const map = useMap();
  const routingControlRef = useRef(null);
  const animationMarkerRef = useRef(null);
  const [routingFailed, setRoutingFailed] = useState(false);

  // Handle cleanup on unmount
  useEffect(() => {
    return () => {
      if (routingControlRef.current && map) {
        safelyRemoveRoutingControl(routingControlRef.current, map);
        routingControlRef.current = null;
      }
      // Remove animation marker if it exists
      if (animationMarkerRef.current) {
        map.removeLayer(animationMarkerRef.current);
        animationMarkerRef.current = null;
      }
    };
  }, [map]);

  useEffect(() => {
    // Clear previous routing control
    if (routingControlRef.current && map) {
      safelyRemoveRoutingControl(routingControlRef.current, map);
      routingControlRef.current = null;
    }

    // Clear previous animation marker
    if (animationMarkerRef.current) {
      map.removeLayer(animationMarkerRef.current);
      animationMarkerRef.current = null;
    }

    // Reset routing failed state for new route attempt
    setRoutingFailed(false);

    // If we don't have both points, don't create a new routing control
    if (!startPoint || !endPoint) {
      setIsPathLoading(false);
      return;
    }

    // Indicate loading has started
    setIsPathLoading(true);

    try {
      // Create animation marker (will be invisible until route is found)
      const animIcon = L.divIcon({
        html: '<div class="pulse-marker"></div>',
        className: 'animated-marker-icon',
        iconSize: [12, 12],
        iconAnchor: [6, 6]
      });

      animationMarkerRef.current = L.marker([startPoint[0], startPoint[1]], {
        icon: animIcon,
        zIndexOffset: 1000, // Ensure it's above other markers
        opacity: 0 // Start invisible
      }).addTo(map);

      // Create waypoints from the startPoint and endPoint
      const waypoints = [
        L.latLng(startPoint[0], startPoint[1]),
        L.latLng(endPoint[0], endPoint[1])
      ];

      // Create the routing control with simplified options for better performance
      const routingControl = L.Routing.control({
        waypoints: waypoints,
        routeWhileDragging: false,
        showAlternatives: false,
        fitSelectedRoutes: false,
        show: false, // Don't show the routing interface
        lineOptions: {
          styles: [{ color: color, opacity: 0.7, weight: weight }],
          extendToWaypoints: true,
          missingRouteTolerance: 10
        },
        createMarker: () => null, // No markers from routing
        addWaypoints: false
      });

      // Handle successful route finding with animation
      routingControl.on('routesfound', function(e) {
        console.log("Route found with", e.routes.length, "options");
        setIsPathLoading(false);

        // If we have route coordinates, animate a marker along the path
        if (e.routes && e.routes.length > 0 && e.routes[0].coordinates) {
          const coordinates = e.routes[0].coordinates;
          console.log("Animating along path with", coordinates.length, "points");

          // Make animation marker visible
          if (animationMarkerRef.current) {
            animationMarkerRef.current.setOpacity(1);

            // Animate the marker along the route
            coordinates.forEach((coord, index) => {
              setTimeout(() => {
                if (animationMarkerRef.current) {
                  animationMarkerRef.current.setLatLng([coord.lat, coord.lng]);
                }
              }, 50 * index); // Speed up animation (50ms per point)
            });

            // Hide marker after animation completes
            setTimeout(() => {
              if (animationMarkerRef.current) {
                animationMarkerRef.current.setOpacity(0);
              }
            }, 50 * coordinates.length + 500);
          }
        }
      });

      // Handle routing errors more gracefully
      routingControl.on('routingerror', function(e) {
        console.warn("Routing error - falling back to direct path:", e);
        setRoutingFailed(true);
        if (onRoutingFailed) onRoutingFailed();
        setIsPathLoading(false);
      });

      // Add the control to the map with a slight delay
      setTimeout(() => {
        try {
          routingControl.addTo(map);
          routingControlRef.current = routingControl;
        } catch (addError) {
          console.error("Error adding routing control:", addError);
          setRoutingFailed(true);
          if (onRoutingFailed) onRoutingFailed();
          setIsPathLoading(false);
        }
      }, 100);

      // Add a timeout in case routing takes too long
      const timeout = setTimeout(() => {
        if (!routingFailed) {
          console.warn("Routing timeout - falling back to direct path");
          setRoutingFailed(true);
          if (onRoutingFailed) onRoutingFailed();
          setIsPathLoading(false);
        }
      }, 6000); // 6 second timeout

      return () => {
        clearTimeout(timeout);
        // Clean up safely
        if (routingControlRef.current && map) {
          safelyRemoveRoutingControl(routingControlRef.current, map);
          routingControlRef.current = null;
        }
        if (animationMarkerRef.current) {
          map.removeLayer(animationMarkerRef.current);
          animationMarkerRef.current = null;
        }
        setIsPathLoading(false);
      };
    } catch (error) {
      console.error("Error setting up routing:", error);
      setRoutingFailed(true);
      if (onRoutingFailed) onRoutingFailed();
      setIsPathLoading(false);
    }
  }, [map, startPoint, endPoint, color, weight, setIsPathLoading, onRoutingFailed]);

  return null;
};

// Component to update map center when user location changes
const MapController = ({ center, zoom }) => {
    const map = useMap();

    useEffect(() => {
        if (center) {
            map.setView(center, zoom || map.getZoom());
        }
    }, [map, center, zoom]);

    return null;
};

// Component to handle map clicks
const MapClickHandler = ({ setCoordinates }) => {
    useMapEvents({
        click: (e) => {
            const { lat, lng } = e.latlng;
            setCoordinates({
                latitude: lat.toFixed(6),
                longitude: lng.toFixed(6)
            });
        }
    });

    return null;
};

// Button to center on user's location
const LocationButton = ({ userLocation, onClick }) => {
    return (
        <button 
            className="location-button" 
            onClick={onClick}
            disabled={!userLocation}
            title="Center map on your location"
        >
            <i className="fas fa-location-arrow"></i> Your Current Location
        </button>
    );
};

const BusStopSearch = ({ userLocation, setUserLocation }) => {
    const [busStops, setBusStops] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [mapCenter, setMapCenter] = useState(userLocation || [22.3190, 87.3091]); // Default to user location if available
    const [zoom, setZoom] = useState(15);
    const [coordinates, setCoordinates] = useState({
        latitude: '',
        longitude: ''
    });
    // Initialize searchMarker with current coordinates or map center
    const [searchMarker, setSearchMarker] = useState(userLocation || [22.3190, 87.3091]);
    const [searchResults, setSearchResults] = useState([]);
    const [hasSearched, setHasSearched] = useState(false);
    const [isPathLoading, setIsPathLoading] = useState(false);
    const [selectedStop, setSelectedStop] = useState(null);
    const [routingKey, setRoutingKey] = useState(0);
    const [showDirectPath, setShowDirectPath] = useState(false);
    const [directPathKey, setDirectPathKey] = useState(0);
    const [routingFailMessage, setRoutingFailMessage] = useState(null);

    const mapRef = useRef(null);
    const markerRefs = useRef({});

    // Register marker references for later access
    const registerMarker = (id, markerRef) => {
        if (markerRef) {
            markerRefs.current[id] = markerRef;
        }
    };

    // Initialize coordinates with user's location as soon as it's available
    useEffect(() => {
        if (userLocation) {
            setCoordinates({
                latitude: userLocation[0].toFixed(6),
                longitude: userLocation[1].toFixed(6)
            });
            setMapCenter(userLocation);
            setSearchMarker(userLocation); // Also update the search marker when user location changes
        }
    }, [userLocation]);

    // Fetch all bus stops directly from the database
    const fetchBusStops = async () => {
        try {
            console.log('Fetching bus stops from API');
            const response = await api.get('/bus_stops/getAllBusStops');
            console.log('Raw API response:', response.data);

            // Extract the actual bus stops array from the response
            // The API returns { statusCode, data, message } structure
            if (response.data && response.data.data) {
                return response.data.data; // Return just the array of bus stops
            } else {
                console.error('Invalid response format:', response.data);
                return [];
            }
        } catch (error) {
            console.error('Error fetching bus stops from database:', error);
            throw error;
        }
    };

    // Load bus stops on component mount
    useEffect(() => {
        const loadBusStops = async () => {
            try {
                setLoading(true);
                const data = await fetchBusStops();

                // Ensure data is an array before mapping
                const busStopsArray = Array.isArray(data) ? data : [];

                // Clean and format the data
                const cleanedData = busStopsArray.map(stop => ({
                    ...stop,
                    latitude: parseFloat(stop.latitude),
                    longitude: parseFloat(stop.longitude)
                }));

                setBusStops(cleanedData);
                setError(null);
            } catch (err) {
                console.error("Error loading bus stops:", err);
                setError("Failed to load bus stops. Please try again later.");
            } finally {
                setLoading(false);
            }
        };

        loadBusStops();
    }, []);

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCoordinates(prev => ({
            ...prev,
            [name]: value
        }));
    };

    // Center map on user location with maximum zoom
    const handleCenterOnUser = () => {
        if (userLocation) {
            setMapCenter(userLocation);
            setZoom(19); // Maximum zoom

            // Also update the coordinate inputs to match user location
            setCoordinates({
                latitude: userLocation[0].toFixed(6),
                longitude: userLocation[1].toFixed(6)
            });
        }
    };

    // Function to search for nearby bus stops
    const handleSearch = () => {
        try {
            const lat = parseFloat(coordinates.latitude);
            const lng = parseFloat(coordinates.longitude);

            // Validate coordinates
            if (isNaN(lat) || isNaN(lng) || 
                lat < -90 || lat > 90 || 
                lng < -180 || lng > 180) {
                setError("Please enter valid coordinates");
                return;
            }

            setError(null);
            setSearchMarker([lat, lng]);

            // Clear previous routes by clearing selected stop and changing routing key
            setSelectedStop(null);
            setRoutingKey(prevKey => prevKey + 1);
            setShowDirectPath(false);
            setDirectPathKey(prevKey => prevKey + 1);
            setRoutingFailMessage(null);

            // Clear routes cache when performing a new search
            // This helps avoid memory issues with too many cached routes
            if (typeof routeCache !== 'undefined' && routeCache.size > 20) {
                routeCache.clear();
            }

            // Calculate distance from specified point to all bus stops
            const results = busStops.map(stop => {
                // Calculate distance in meters using Haversine formula
                const distance = calculateDistance(
                    lat, lng,
                    stop.latitude, stop.longitude
                );

                return {
                    ...stop,
                    distance: distance
                };
            });

            // Sort by distance (closest first)
            const sortedResults = [...results].sort((a, b) => a.distance - b.distance);
            setSearchResults(sortedResults);
            setHasSearched(true);

            // Center map on search marker
            setMapCenter([lat, lng]);
            setZoom(16);
        } catch (error) {
            console.error("Search error:", error);
            setError("An error occurred during search");
        }
    };

    // Handle click on search result item
    const handleStopSelect = (stop) => {
        // Set the map center and zoom
        setMapCenter([stop.latitude, stop.longitude]);
        setZoom(18);

        // Clear previous path and set the new selected stop
        setSelectedStop(stop);
        setRoutingKey(prevKey => prevKey + 1);
        setShowDirectPath(false);
        setDirectPathKey(prevKey => prevKey + 1);
        setRoutingFailMessage(null);

        // Open the popup for this marker
        const marker = markerRefs.current[stop.id];
        if (marker) {
            marker.openPopup();
        }
    };

    // Calculate distance between two coordinates using Haversine formula
    const calculateDistance = (lat1, lon1, lat2, lon2) => {
        const R = 6371e3; // Earth's radius in meters
        const φ1 = lat1 * Math.PI / 180;
        const φ2 = lat2 * Math.PI / 180;
        const Δφ = (lat2 - lat1) * Math.PI / 180;
        const Δλ = (lon2 - lon1) * Math.PI / 180;

        const a = Math.sin(Δφ/2) * Math.sin(Δφ/2) +
                Math.cos(φ1) * Math.cos(φ2) *
                Math.sin(Δλ/2) * Math.sin(Δλ/2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));

        return R * c; // Distance in meters
    };

    // Format distance for display
    const formatDistance = (meters) => {
        if (meters < 1000) {
            return `${Math.round(meters)} meters`;
        } else {
            return `${(meters / 1000).toFixed(2)} km`;
        }
    };

    useEffect(() => {
        // Ensure loading screen doesn't stay forever - reduce timeout
        if (isPathLoading) {
            const timeout = setTimeout(() => {
                setIsPathLoading(false);
            }, 7000); // Reduced maximum timeout to 7 seconds

            return () => clearTimeout(timeout);
        }
    }, [isPathLoading]);

    // Initialize Leaflet routing on component mount
    useEffect(() => {
        // Configure Leaflet routing machine with simplified options
        if (typeof L !== 'undefined') {
            configureRoutingMachine();

            // Add a simple pre-initialization to warm up the routing engine
            try {
                const dummyRouting = L.Routing.control({
                    waypoints: [
                        L.latLng(22.3190, 87.3091),
                        L.latLng(22.3191, 87.3092)
                    ],
                    show: false,
                    createMarker: () => null,
                });

                // Remove after a short delay
                setTimeout(() => {
                    try {
                        if (dummyRouting._container && dummyRouting._container.parentNode) {
                            dummyRouting._container.parentNode.removeChild(dummyRouting._container);
                        }
                    } catch (e) {
                        console.warn("Cleanup of dummy routing failed:", e);
                    }
                }, 500);
            } catch (e) {
                console.warn("Failed to initialize routing:", e);
            }
        }
    }, []);

    // Clear any routes when component unmounts
    useEffect(() => {
        return () => {
            // Clear any cached data
            if (window._routeCache) {
                window._routeCache.clear();
            }
        };
    }, []);

    // Handle routing failure
    const handleRoutingFailed = () => {
        setShowDirectPath(true);
        setDirectPathKey(prevKey => prevKey + 1);
        setRoutingFailMessage("OSRM route not available. Showing direct path instead.");

        // Auto-hide the message after 5 seconds
        setTimeout(() => {
            setRoutingFailMessage(null);
        }, 5000);
    };

    useEffect(() => {
        // Ensure loading screen doesn't stay forever
        if (isPathLoading) {
            const timeout = setTimeout(() => {
                setIsPathLoading(false);
                // If still loading after timeout, show direct path
                if (selectedStop && userLocation) {
                    handleRoutingFailed();
                }
            }, 5000); // Further reduced maximum timeout to 5 seconds

            return () => clearTimeout(timeout);
        }
    }, [isPathLoading, selectedStop, userLocation]);

    // Enhanced cleanup when component unmounts
    useEffect(() => {
        return () => {
            // Clear any pending routing operations
            if (routeCache.size > 0) {
                routeCache.clear();
            }

            // Reset all states to prevent memory leaks
            setSelectedStop(null);
            setShowDirectPath(false);
            setIsPathLoading(false);
        };
    }, []);

    // Handle click on map to create a route
    const handleMapClick = useCallback((latlng) => {
        // Set the clicked coordinates as the search marker
        setSearchMarker([latlng.lat, latlng.lng]);
        setCoordinates({
            latitude: latlng.lat.toFixed(6),
            longitude: latlng.lng.toFixed(6)
        });

        // Clear any previous routes
        setSelectedStop(null);
        setRoutingKey(prevKey => prevKey + 1);
        setShowDirectPath(false);
        setDirectPathKey(prevKey => prevKey + 1);
        setRoutingFailMessage(null);

        // Find the closest bus stop to the clicked location
        if (busStops.length > 0) {
            // Calculate distances to all bus stops
            const stopsWithDistances = busStops.map(stop => {
                const distance = calculateDistance(
                    latlng.lat, latlng.lng,
                    stop.latitude, stop.longitude
                );
                return { ...stop, distance };
            });

            // Sort by distance and get the closest
            const closestStop = [...stopsWithDistances].sort((a, b) => a.distance - b.distance)[0];

            // If we found a closest stop within 2km, select it
            if (closestStop && closestStop.distance < 2000) {
                // Update selected stop to the closest one
                setSelectedStop(closestStop);

                // Update the search results to show nearby stops
                const sortedResults = [...stopsWithDistances].sort((a, b) => a.distance - b.distance);
                setSearchResults(sortedResults);
                setHasSearched(true);
            }
        }
    }, [busStops, calculateDistance, setCoordinates, setSearchMarker]);

    return (
        <div className="bus-stop-search">
            {/* Full-page loading overlay for OSRM loading */}
            {isPathLoading && (
                <div className="full-page-loading-overlay">
                    <div className="spinner"></div>
                    <div>Loading route. Please wait...</div>
                </div>
            )}

            {/* Routing fail message */}
            {routingFailMessage && (
                <div className="routing-fail-message">
                    {routingFailMessage}
                </div>
            )}

            <div className="location-panel">
                <div className="location-panel-top">
                    <h2>Search Bus Stops</h2>
                    {error && <div className="error-message">{error}</div>}

                    <div className="search-form">
                        <div className="form-group">
                            <label htmlFor="latitude">Latitude:</label>
                            <input 
                                type="text" 
                                id="latitude" 
                                name="latitude" 
                                value={coordinates.latitude} 
                                onChange={handleInputChange} 
                                placeholder="e.g. 22.3190"
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="longitude">Longitude:</label>
                            <input 
                                type="text" 
                                id="longitude" 
                                name="longitude" 
                                value={coordinates.longitude} 
                                onChange={handleInputChange} 
                                placeholder="e.g. 87.3091"
                            />
                        </div>

                        <p className="help-text">Click on the map to set coordinates or use your current location</p>

                        <button 
                            className="search-btn"
                            onClick={handleSearch}
                            disabled={loading}
                        >
                            {loading ? 'Loading...' : 'Find Nearest Bus Stops'}
                        </button>
                    </div>
                </div>

                <div className="location-panel-bottom">
                    {hasSearched && searchResults.length > 0 && (
                        <div className="search-results">
                            <h3>Nearest Bus Stops</h3>
                            <div className="results-list">
                                {searchResults.map((stop, index) => (
                                    <div 
                                        key={stop.id} 
                                        className="result-item"
                                        onClick={() => handleStopSelect(stop)}
                                    >
                                        <div className="result-index">{index + 1}</div>
                                        <div className="result-details">
                                            <h4>{stop.name}</h4>
                                            <p className="distance">Distance: {formatDistance(stop.distance)}</p>
                                            <p className="coordinates">
                                                {stop.latitude.toFixed(6)}, {stop.longitude.toFixed(6)}
                                            </p>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}

                    {hasSearched && searchResults.length === 0 && (
                        <div className="no-results">
                            <p>No bus stops found near the selected location.</p>
                        </div>
                    )}
                </div>
            </div>

            <div className="map-container">
                <MapContainer
                    center={mapCenter}
                    zoom={zoom}
                    style={{ height: "100%", width: "100%" }}
                    ref={mapRef}
                >
                    <TileLayer
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />

                    <MapController center={mapCenter} zoom={zoom} />
                    <ClickToRouteEvents 
                        handleMapClick={handleMapClick} 
                        setCoordinates={setCoordinates} 
                        setSearchMarker={setSearchMarker} 
                    />

                    {/* Display user location */}
                    {userLocation && (
                        <Marker position={userLocation} icon={userIcon}>
                            <Popup>
                                <div>
                                    <strong>Your Location</strong><br />
                                    {userLocation[0].toFixed(6)}, {userLocation[1].toFixed(6)}
                                </div>
                            </Popup>
                        </Marker>
                    )}

                    {/* Always display search marker */}
                    {searchMarker && (
                        <Marker position={searchMarker} icon={searchMarkerIcon}>
                            <Popup>
                                <div>
                                    <strong>Search Location</strong><br />
                                    {searchMarker[0].toFixed(6)}, {searchMarker[1].toFixed(6)}
                                </div>
                            </Popup>
                        </Marker>
                    )}

                    {/* Display all bus stops */}
                    {busStops.map(stop => (
                        <Marker
                            key={stop.id}
                            position={[stop.latitude, stop.longitude]}
                            icon={busStopIcon}
                            ref={(ref) => registerMarker(stop.id, ref)}
                        >
                            <Popup>
                                <div>
                                    <strong>{stop.name}</strong><br />
                                    {searchMarker && (
                                        <span>
                                            Distance: {formatDistance(
                                                calculateDistance(
                                                    searchMarker[0], searchMarker[1],
                                                    stop.latitude, stop.longitude
                                                )
                                            )}
                                        </span>
                                    )}
                                </div>
                            </Popup>
                        </Marker>
                    ))}

                    {/* Add routing control for selected bus stop - use key to force re-creation */}
                    {searchMarker && selectedStop && !showDirectPath && (
                        <RoutingControl
                            key={`route-${routingKey}`}
                            startPoint={searchMarker} // Using search marker as start point
                            endPoint={[selectedStop.latitude, selectedStop.longitude]}
                            color="#ff6b6b"
                            weight={4}
                            setIsPathLoading={setIsPathLoading}
                            onRoutingFailed={handleRoutingFailed}
                        />
                    )}

                    {/* Add direct path as fallback when OSRM fails or for faster display */}
                    {searchMarker && selectedStop && showDirectPath && (
                        <DirectPathLine
                            key={`direct-${directPathKey}`}
                            startPoint={searchMarker} // Using search marker as start point
                            endPoint={[selectedStop.latitude, selectedStop.longitude]}
                            color="#ff6b6b"
                            weight={3}
                        />
                    )}

                    {/* Button inside map container for centering on user location */}
                    <div className="map-controls">
                        <LocationButton
                            userLocation={userLocation}
                            onClick={handleCenterOnUser}
                        />
                    </div>
                </MapContainer>
            </div>
        </div>
    );
};

// Updated component to handle map clicks with routing
const ClickToRouteEvents = ({ handleMapClick, setCoordinates, setSearchMarker }) => {
    useMapEvents({
        click: (e) => {
            const { lat, lng } = e.latlng;

            // Update coordinates and search marker
            setCoordinates({
                latitude: lat.toFixed(6),
                longitude: lng.toFixed(6)
            });
            setSearchMarker([lat, lng]);

            // Trigger route finding to nearest bus stop
            handleMapClick(e.latlng);
        }
    });

    return null;
};

export default BusStopSearch;
