import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../utils/api';
import '../../css/availableBuses.css';

const AvailableBuses = ({ fromStop, toStop }) => {
  const [buses, setBuses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBuses = async () => {
      if (!fromStop || !toStop) {
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError(null);

        const response = await api.get('/bus-stops/getBusesByStops', {
          params: {
            fromStopId: fromStop.id,
            toStopId: toStop.id
          }
        });

        if (response.data && response.data.data) {
          setBuses(response.data.data);
        } else {
          setBuses([]);
        }
      } catch (err) {
        console.error('Error fetching buses between stops:', err);
        setError('Failed to fetch available buses. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchBuses();
  }, [fromStop, toStop]);

  const handleTrackBus = (bus) => {
    // Store the selected bus in localStorage to retrieve it in the BusTracking component
    localStorage.setItem('selectedBusToTrack', JSON.stringify({
      id: bus.id,
      name: bus.name
    }));

    // Navigate to the bus tracking page
    navigate('/bus-tracking');
  };

  if (loading) {
    return <div className="buses-loading">Loading available buses...</div>;
  }

  if (error) {
    return <div className="buses-error">{error}</div>;
  }

  if (!fromStop || !toStop) {
    return <div className="buses-message">Please select both from and to stops to see available buses.</div>;
  }

  return (
    <div className="available-buses-container">
      <h3>Available Buses from {fromStop.name} to {toStop.name}</h3>

      {buses.length === 0 ? (
        <div className="no-buses-message">
          No buses are currently available for this route.
        </div>
      ) : (
        <div className="buses-list">
          {buses.map(bus => (
            <div key={bus.id} className="bus-item">
              <div className="bus-details">
                <h4>{bus.name}</h4>
                <div className="bus-times">
                  <div className="time-info">
                    <span className="departure">Departure: {bus.times.departureTime}</span>
                    <span className="arrival">Arrival: {bus.times.arrivalTime}</span>
                  </div>
                  <div className="duration">Duration: {bus.times.durationMinutes} minutes</div>
                </div>
                <div className="route-stops">
                  <div>From: {bus.route.fromStop.name}</div>
                  <div>To: {bus.route.toStop.name}</div>
                </div>
              </div>
              <div className="bus-actions">
                <button 
                  className="track-bus-button"
                  onClick={() => handleTrackBus(bus)}
                >
                  <i className="fas fa-map-marker-alt"></i> Track This Bus
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AvailableBuses;
