import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import apiConfig, { getApiUrl } from '../utils/api2.js';
import BusManagement from './admin/BusManagement';
import StopManagement from './admin/StopManagement';
import RouteManagement from './admin/RouteManagement';
import DriverManagement from './admin/DriverManagement';
import SystemStatistics from './admin/SystemStatistics';
import UserLocations from './admin/UserLocations';

function AdminDashboard() {
  const [activeTab, setActiveTab] = useState('overview');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();
  const { id } = useParams();

  // Create authenticated axios instance specifically for admin API calls
  const createAuthenticatedAxiosInstance = () => {
    const token = localStorage.getItem('jwtToken');
    return axios.create({
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      withCredentials: true
    });
  };

  // Fetch the admin user data using the same approach as App.js
  useEffect(() => {
    const fetchAdminData = async () => {
      try {
        // First try to get user from localStorage
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
          const parsedUser = JSON.parse(storedUser);
          if (parsedUser.role === 'admin') {
            setUser(parsedUser);
            setLoading(false);
            return;
          }
        }

        // If no stored user or not admin, fetch from API
        const authAxios = createAuthenticatedAxiosInstance();

        // Get user data from backend using the id param
        const response = await authAxios.get(`/logged_in/${id}/admin`);

        if (response.data && response.data.user) {
          const userData = response.data.user;
          // Store user in localStorage for future requests
          localStorage.setItem('user', JSON.stringify(userData));
          setUser(userData);
        } else {
          throw new Error("Invalid user data received");
        }
      } catch (err) {
        console.error("Error fetching admin data:", err);
        setError("Failed to load admin dashboard. Please log in again.");

        // If there's an auth error, redirect to login
        if (err.response && (err.response.status === 401 || err.response.status === 403)) {
          setTimeout(() => navigate('/login'), 2000);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchAdminData();
  }, [id, navigate]);

  // Show loading state
  if (loading) {
    return <div className="loading">Loading admin dashboard...</div>;
  }

  // Show error message
  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => navigate('/login')}>Return to Login</button>
      </div>
    );
  }

  // Show unauthorized message if user is not an admin
  if (!user || user.role !== 'admin') {
    return (
      <div className="unauthorized">
        <h2>Unauthorized Access</h2>
        <p>You must be an administrator to access this page.</p>
        <button onClick={() => navigate('/login')}>Go to Login</button>
      </div>
    );
  }

  // Create context for child components to access authenticated axios
  const adminApi = {
    axios: createAuthenticatedAxiosInstance(),
    getUrl: getApiUrl,
    endpoints: apiConfig.endpoints
  };

  // Render admin dashboard if user is authenticated and is an admin
  const renderContent = () => {
    switch (activeTab) {
      case 'buses':
        return <BusManagement user={user} api={adminApi} />;
      case 'stops':
        return <StopManagement user={user} api={adminApi} />;
      case 'routes':
        return <RouteManagement user={user} api={adminApi} />;
      case 'drivers':
        return <DriverManagement user={user} api={adminApi} />;
      case 'stats':
        return <SystemStatistics user={user} api={adminApi} />;
      case 'locations':
        return <UserLocations user={user} api={adminApi} />;
      default:
        return <AdminOverview user={user} navigate={navigate} api={adminApi} />;
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="admin-header">
        <h1>KGP Bus Admin Dashboard</h1>
        <div className="user-info">
          <span>Welcome, {user.email}</span>
          <button onClick={() => {
            axios.post('/logout', {}, { withCredentials: true })
              .then(() => {
                localStorage.removeItem('jwtToken');
                localStorage.removeItem('user');
                navigate('/login');
              })
              .catch(err => {
                console.error("Logout error:", err);
                localStorage.removeItem('jwtToken');
                localStorage.removeItem('user');
                navigate('/login');
              });
          }}>Logout</button>
        </div>
      </div>

      <div className="admin-container">
        <div className="admin-sidebar">
          <nav>
            <ul>
              <li 
                className={activeTab === 'overview' ? 'active' : ''} 
                onClick={() => setActiveTab('overview')}>
                  Dashboard Overview
              </li>
              <li 
                className={activeTab === 'buses' ? 'active' : ''} 
                onClick={() => setActiveTab('buses')}>
                  Manage Buses
              </li>
              <li 
                className={activeTab === 'stops' ? 'active' : ''} 
                onClick={() => setActiveTab('stops')}>
                  Manage Bus Stops
              </li>
              <li 
                className={activeTab === 'routes' ? 'active' : ''} 
                onClick={() => setActiveTab('routes')}>
                  Manage Routes
              </li>
              <li 
                className={activeTab === 'drivers' ? 'active' : ''} 
                onClick={() => setActiveTab('drivers')}>
                  Manage Drivers
              </li>
              <li 
                className={activeTab === 'stats' ? 'active' : ''} 
                onClick={() => setActiveTab('stats')}>
                  System Statistics
              </li>
              <li 
                className={activeTab === 'locations' ? 'active' : ''} 
                onClick={() => setActiveTab('locations')}>
                  Track User Locations
              </li>
            </ul>
          </nav>
        </div>

        <div className="admin-content">
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

// Simple overview component for the dashboard home
function AdminOverview({ user, navigate, api }) {
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalBuses: 0,
    totalStops: 0,
    totalRoutes: 0,
    totalDrivers: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setLoading(true);
        const response = await api.axios.get(
          api.getUrl(api.endpoints.adminStats)
        );

        if (response.data) {
          setStats({
            totalUsers: response.data.totalUsers || 0,
            totalBuses: response.data.totalBuses || 0,
            totalStops: response.data.totalStops || 0,
            totalRoutes: response.data.totalRoutes || 0,
            totalDrivers: response.data.totalDrivers || 0,
          });
        }
      } catch (err) {
        console.error("Error fetching dashboard stats:", err);
        // Fallback to demo values
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, [api]);

  if (loading) {
    return <div>Loading statistics...</div>;
  }

  return (
    <div className="admin-overview">
      <h2>System Overview</h2>
      <div className="dashboard-stats">
        <div className="stat-card">
          <h3>Buses</h3>
          <div className="stat">{stats.totalBuses}</div>
          <div className="stat-action">
            <button onClick={() => navigate('/admin/buses')}>Manage</button>
          </div>
        </div>
        <div className="stat-card">
          <h3>Bus Stops</h3>
          <div className="stat">{stats.totalStops}</div>
          <div className="stat-action">
            <button onClick={() => navigate('/admin/stops')}>Manage</button>
          </div>
        </div>
        <div className="stat-card">
          <h3>Routes</h3>
          <div className="stat">{stats.totalRoutes}</div>
          <div className="stat-action">
            <button onClick={() => navigate('/admin/routes')}>Manage</button>
          </div>
        </div>
        <div className="stat-card">
          <h3>Drivers</h3>
          <div className="stat">{stats.totalDrivers}</div>
          <div className="stat-action">
            <button onClick={() => navigate('/admin/drivers')}>Manage</button>
          </div>
        </div>
      </div>

      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <div className="action-buttons">
          <button onClick={() => navigate('/admin/buses')}>Add New Bus</button>
          <button onClick={() => navigate('/admin/stops')}>Add New Stop</button>
          <button onClick={() => navigate('/admin/routes')}>Create New Route</button>
          <button onClick={() => navigate('/admin/drivers')}>Add New Driver</button>
