import React, { useState, useEffect, useRef, useCallback } from 'react';
import { GoogleMap, useJsApiLoader, Marker, InfoWindow, DirectionsRenderer } from '@react-google-maps/api';
import api from '../../utils/api';
import '../../css/user.css';

// Default map container styles
const mapContainerStyle = {
    width: '100%',
    height: '100%'
};

// Map options with default controls enabled
const defaultOptions = {
    mapTypeControl: true,
    streetViewControl: true,
    fullscreenControl: true,
    zoomControl: true,
    myLocationButtonControl: true,
    disableDefaultUI: false,
};

// Component to handle routing between points using Google Directions API
const DirectionsService = ({ startPoint, endPoint, setDirections }) => {
    useEffect(() => {
        if (!startPoint || !endPoint) return;

        const directionsService = new window.google.maps.DirectionsService();

        directionsService.route(
            {
                origin: startPoint,
                destination: endPoint,
                travelMode: window.google.maps.TravelMode.WALKING,
            },
            (result, status) => {
                if (status === window.google.maps.DirectionsStatus.OK) {
                    setDirections(result);
                } else {
                    console.error(`Directions request failed: ${status}`);
                }
            }
        );

        return () => {
            setDirections(null);
        };
    }, [startPoint, endPoint, setDirections]);

    return null;
};

const BusStopSearch = ({ userLocation, setUserLocation }) => {
    // Load the Google Maps JS API
    const { isLoaded, loadError } = useJsApiLoader({
        googleMapsApiKey: process.env.REACT_APP_GOOGLE_API_KEY,
        libraries: ['places'],
    });

    const [busStops, setBusStops] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [mapCenter, setMapCenter] = useState(
        userLocation ? { lat: userLocation[0], lng: userLocation[1] } : { lat: 22.3190, lng: 87.3091 }
    );
    const [zoom, setZoom] = useState(15);
    const [coordinates, setCoordinates] = useState({
        latitude: userLocation ? userLocation[0].toFixed(6) : '22.3190',
        longitude: userLocation ? userLocation[1].toFixed(6) : '87.3091',
    });

    // Initialize the red marker with larger size and different icon for better visibility
    const [searchMarker, setSearchMarker] = useState(
        userLocation ? { lat: userLocation[0], lng: userLocation[1] } : { lat: 22.3190, lng: 87.3091 }
    );

    const [searchResults, setSearchResults] = useState([]);
    const [hasSearched, setHasSearched] = useState(false);
    const [selectedStop, setSelectedStop] = useState(null);
    const [selectedMarker, setSelectedMarker] = useState(null);
    const [busStopToRedMarkerDirections, setBusStopToRedMarkerDirections] = useState(null);

    const mapRef = useRef(null);

    // Initialize coordinates with user's location as soon as it's available
    useEffect(() => {
        if (isLoaded && userLocation) {
            setCoordinates(prev => ({
                ...prev,
                latitude: userLocation[0].toFixed(6),
                longitude: userLocation[1].toFixed(6)
            }));
            setMapCenter({ lat: userLocation[0], lng: userLocation[1] });
            setSearchMarker({ lat: userLocation[0], lng: userLocation[1] });
        }
    }, [isLoaded, userLocation]);


    // Function to fetch bus stops
    const fetchBusStops = async () => {
        try {
            console.log('Fetching bus stops from API');
            const response = await api.get('/bus_stops/getAllBusStops');
            console.log('Raw API response:', response.data);

            if (response.data && response.data.data) {
                return response.data.data;
            } else {
                console.error('Invalid response format:', response.data);
                return [];
            }
        } catch (error) {
            console.error('Error fetching bus stops from database:', error);
            throw error;
        }
    };

    // Load bus stops on component mount
    useEffect(() => {
        const loadBusStops = async () => {
            try {
                setLoading(true);
                const data = await fetchBusStops();

                const busStopsArray = Array.isArray(data) ? data : [];

                const cleanedData = busStopsArray.map(stop => ({
                    ...stop,
                    latitude: parseFloat(stop.latitude),
                    longitude: parseFloat(stop.longitude)
                }));

                setBusStops(cleanedData);
                setError(null);
            } catch (err) {
                console.error("Error loading bus stops:", err);
                setError("Failed to load bus stops. Please try again later.");
            } finally {
                setLoading(false);
            }
        };

        loadBusStops();
    }, []);

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCoordinates(prev => ({
            ...prev,
            [name]: value
        }));
    };

    // Center map on user location with maximum zoom
    const handleCenterOnUser = () => {
        if (userLocation) {
            setMapCenter({ lat: userLocation[0], lng: userLocation[1] });
            setZoom(19);
            setCoordinates({
                latitude: userLocation[0].toFixed(6),
                longitude: userLocation[1].toFixed(6)
            });
        }
    };

    // Function to search for nearby bus stops
    const handleSearch = () => {
        try {
            const lat = parseFloat(coordinates.latitude);
            const lng = parseFloat(coordinates.longitude);

            if (isNaN(lat) || isNaN(lng) ||
                lat < -90 || lat > 90 ||
                lng < -180 || lng > 180) {
                setError("Please enter valid coordinates");
                return;
            }

            setError(null);
            setSearchMarker({ lat, lng });

            // Clear any existing directions when performing a new search
            setBusStopToRedMarkerDirections(null);

            const results = busStops.map(stop => {
                const distance = calculateDistance(
                    lat, lng,
                    stop.latitude, stop.longitude
                );

                return {
                    ...stop,
                    distance: distance
                };
            });

            const sortedResults = [...results].sort((a, b) => a.distance - b.distance);
            setSearchResults(sortedResults);
            setHasSearched(true);

            setMapCenter({ lat, lng });
            setZoom(16);
        } catch (error) {
            console.error("Search error:", error);
            setError("An error occurred during search");
        }
    };

    // Handle click on search result item
    const handleStopSelect = (stop) => {
        setMapCenter({ lat: stop.latitude, lng: stop.longitude });
        setZoom(18);
        setSelectedStop(stop);
        setSelectedMarker(stop.id);

        // Clear any previous directions before creating new ones
        setBusStopToRedMarkerDirections(null);

        // Draw path from selected bus stop to red marker
        if (searchMarker) {
            const directionsService = new window.google.maps.DirectionsService();

            directionsService.route(
                {
                    origin: { lat: stop.latitude, lng: stop.longitude },
                    destination: searchMarker,
                    travelMode: window.google.maps.TravelMode.WALKING,
                },
                (result, status) => {
                    if (status === window.google.maps.DirectionsStatus.OK) {
                        setBusStopToRedMarkerDirections(result);
                    } else {
                        console.error(`Directions request failed: ${status}`);
                    }
                }
            );
        }
    };

    // Calculate distance between two coordinates using Haversine formula
    const calculateDistance = (lat1, lon1, lat2, lon2) => {
        const R = 6371e3;
        const φ1 = lat1 * Math.PI / 180;
        const φ2 = lat2 * Math.PI / 180;
        const Δφ = (lat2 - lat1) * Math.PI / 180;
        const Δλ = (lon2 - lon1) * Math.PI / 180;

        const a = Math.sin(Δφ / 2) * Math.sin(Δφ / 2) +
            Math.cos(φ1) * Math.cos(φ2) *
            Math.sin(Δλ / 2) * Math.sin(Δλ / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

        return R * c;
    };

    // Format distance for display
    const formatDistance = (meters) => {
        if (meters < 1000) {
            return `${Math.round(meters)} meters`;
        } else {
            return `${(meters / 1000).toFixed(2)} km`;
        }
    };

    // Handle map click to set coordinates
    const onMapClick = useCallback((e) => {
        const lat = e.latLng.lat();
        const lng = e.latLng.lng();

        console.log("Map clicked at:", lat, lng); // Debug log to verify click is registered

        // Update coordinates input fields
        setCoordinates({
            latitude: lat.toFixed(6),
            longitude: lng.toFixed(6)
        });

        // Update red marker position
        setSearchMarker({ lat, lng });

        // Clear any existing directions when clicking a new location
        setBusStopToRedMarkerDirections(null);

        // Close any open popups
        setSelectedMarker(null);
    }, []);

    // Store map instance when it loads
    const onLoad = useCallback((map) => {
        mapRef.current = map;
    }, []);

    // Clean up function when component unmounts
    const onUnmount = useCallback(() => {
        mapRef.current = null;
    }, []);

    if (loadError) {
        return <div className="error-message">Error loading Google Maps: {loadError.message}</div>;
    }

    if (!isLoaded) {
        return <div className="loading-message">Loading Google Maps...</div>;
    }

    return (
        <div className="bus-stop-search">
            <div className="location-panel">
                <div className="location-panel-top">
                    <h2>Search Bus Stops</h2>
                    {error && <div className="error-message">{error}</div>}

                    <div className="search-form">
                        <div className="form-group">
                            <label htmlFor="latitude">Latitude:</label>
                            <input
                                type="text"
                                id="latitude"
                                name="latitude"
                                value={coordinates.latitude}
                                onChange={handleInputChange}
                                placeholder="e.g. 22.3190"
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="longitude">Longitude:</label>
                            <input
                                type="text"
                                id="longitude"
                                name="longitude"
                                value={coordinates.longitude}
                                onChange={handleInputChange}
                                placeholder="e.g. 87.3091"
                            />
                        </div>

                        <p className="help-text">Click on the map to set coordinates or use your current location</p>

                        <button
                            className="search-btn"
                            onClick={handleSearch}
                            disabled={loading}
                        >
                            {loading ? 'Loading...' : 'Find Nearest Bus Stops'}
                        </button>
                    </div>
                </div>

                <div className="location-panel-bottom">
                    {hasSearched && searchResults.length > 0 && (
                        <div className="search-results">
                            <h3>Nearest Bus Stops</h3>
                            <div className="results-list">
                                {searchResults.map((stop, index) => (
                                    <div
                                        key={stop.id}
                                        className="result-item"
                                        onClick={() => handleStopSelect(stop)}
                                    >
                                        <div className="result-index">{index + 1}</div>
                                        <div className="result-details">
                                            <h4>{stop.name}</h4>
                                            <p className="distance">Distance: {formatDistance(stop.distance)}</p>
                                            <p className="coordinates">
                                                {stop.latitude.toFixed(6)}, {stop.longitude.toFixed(6)}
                                            </p>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}

                    {hasSearched && searchResults.length === 0 && (
                        <div className="no-results">
                            <p>No bus stops found near the selected location.</p>
                        </div>
                    )}
                </div>
            </div>

            <div className="map-container">
                <GoogleMap
                    mapContainerStyle={mapContainerStyle}
                    center={mapCenter}
                    zoom={zoom}
                    options={defaultOptions}
                    onClick={onMapClick}
                    onLoad={onLoad}
                    onUnmount={onUnmount}
                >
                    {/* Display user location */}
                    {isLoaded && userLocation && (
                        <Marker
                            position={{ lat: userLocation[0], lng: userLocation[1] }}
                            icon={{
                                url: 'https://maps.google.com/mapfiles/ms/icons/blue-dot.png',
                                scaledSize: new window.google.maps.Size(32, 32),
                                origin: new window.google.maps.Point(0, 0),
                                anchor: new window.google.maps.Point(16, 32),
                            }}
                            onClick={() => setSelectedMarker('user')}
                        >
                            {selectedMarker === 'user' && (
                                <InfoWindow
                                    onCloseClick={() => setSelectedMarker(null)}
                                    options={{ pixelOffset: new window.google.maps.Size(0, -32) }}
                                >
                                    <div>
                                        <strong>Your Location</strong><br />
                                        {userLocation[0].toFixed(6)}, {userLocation[1].toFixed(6)}
                                    </div>
                                </InfoWindow>
                            )}
                        </Marker>
                    )}

                    {/* FIXED RED MARKER - Using the same approach as the blue marker */}
                    <Marker
                        position={searchMarker}
                        icon={{
                            url: 'https://maps.google.com/mapfiles/ms/icons/red-dot.png', // Using the standard red dot icon
                            scaledSize: new window.google.maps.Size(32, 32),
                            origin: new window.google.maps.Point(0, 0),
                            anchor: new window.google.maps.Point(16, 32),
                        }}
                        // zIndex={1000} // Ensure it appears on top
                        onClick={() => setSelectedMarker('search')}
                    >
                        {selectedMarker === 'search' && (
                            <InfoWindow
                                onCloseClick={() => setSelectedMarker(null)}
                                options={{ pixelOffset: new window.google.maps.Size(0, -32) }}
                            >
                                <div>
                                    <strong>Clicked Location</strong><br />
                                    {searchMarker.lat.toFixed(6)}, {searchMarker.lng.toFixed(6)}
                                </div>
                            </InfoWindow>
                        )}
                    </Marker>

                    {/* Display all bus stops */}
                    {busStops.map(stop => (
                        <Marker
                            key={stop.id}
                            position={{ lat: stop.latitude, lng: stop.longitude }}
                            icon={{
                                url: 'https://maps.google.com/mapfiles/ms/icons/yellow-dot.png',
                                scaledSize: new window.google.maps.Size(32, 32),
                                origin: new window.google.maps.Point(0, 0),
                                anchor: new window.google.maps.Point(16, 32),
                            }}
                            onClick={() => {
                                // Close any previously open popups then show this one
                                setSelectedMarker(stop.id);

                                // If we click on a bus stop marker directly, also select it 
                                // to show the path to red marker
                                handleStopSelect(stop);
                            }}
                        >
                            {selectedMarker === stop.id && (
                                <InfoWindow
                                    onCloseClick={() => setSelectedMarker(null)}
                                    options={{ pixelOffset: new window.google.maps.Size(0, -32) }}
                                >
                                    <div>
                                        <strong>{stop.name}</strong><br />
                                        {searchMarker && (
                                            <span>
                                                Distance: {formatDistance(
                                                    calculateDistance(
                                                        searchMarker.lat, searchMarker.lng,
                                                        stop.latitude, stop.longitude
                                                    )
                                                )}
                                            </span>
                                        )}
                                    </div>
                                </InfoWindow>
                            )}
                        </Marker>
                    ))}

                    {/* Route from selected bus stop to red marker (search location) */}
                    {busStopToRedMarkerDirections && (
                        <DirectionsRenderer
                            directions={busStopToRedMarkerDirections}
                            options={{
                                suppressMarkers: true, // Don't show default markers
                                polylineOptions: {
                                    strokeColor: '#FF69B4', // Pink color for the path
                                    strokeWeight: 4,
                                    strokeOpacity: 0.7,
                                }
                            }}
                        />
                    )}
                </GoogleMap>
            </div>
        </div>
    );
};

export default BusStopSearch;
