import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import api from '../../utils/api.js';
import '../../css/busTracking.css';

// Custom bus icon
const busIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/30/30979.png',
  iconSize: [32, 32],
  iconAnchor: [16, 16],
  popupAnchor: [0, -16]
});

// User location icon
const userIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/1077/1077114.png',
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

// Bus stop icon
const busStopIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/1165/1165895.png',
  iconSize: [24, 24],
  iconAnchor: [12, 24],
  popupAnchor: [0, -24]
});

// Component to handle routing between points
const RoutingControl = ({ startPoint, endPoint, color = '#3388ff', weight = 4 }) => {
  const map = useMap();
  const routingControlRef = useRef(null);

  useEffect(() => {
    if (!startPoint || !endPoint) return;

    // Clear previous routing if it exists
    if (routingControlRef.current) {
      map.removeControl(routingControlRef.current);
      routingControlRef.current = null;
    }

    try {
      // Create new routing control
      const routingControl = L.Routing.control({
        waypoints: [
          L.latLng(startPoint[0], startPoint[1]),
          L.latLng(endPoint[0], endPoint[1])
        ],
        routeWhileDragging: false,
        showAlternatives: false,
        addWaypoints: false,
        fitSelectedRoutes: false,
        show: false,
        lineOptions: {
          styles: [{ color, opacity: 0.7, weight }],
          extendToWaypoints: true,
          missingRouteTolerance: 0
        },
        createMarker: () => null // No markers from routing
      });

      routingControl.addTo(map);
      routingControlRef.current = routingControl;

      // Set up cleanup function
      return () => {
        if (routingControlRef.current) {
          map.removeControl(routingControlRef.current);
        }
      };
    } catch (error) {
      console.error("Error setting up routing:", error);
    }
  }, [map, startPoint, endPoint, color, weight]);

  return null;
};

// Component to recenter map when needed
const MapViewController = ({ center, zoom, busLocation }) => {
  const map = useMap();

  useEffect(() => {
    if (center && zoom) {
      map.setView(center, zoom);
    } else if (busLocation) {
      map.setView(busLocation, map.getZoom());
    }
  }, [map, center, zoom, busLocation]);

  return null;
};

// Custom location control component
const LocationButton = ({ setUserLocation }) => {
  const map = useMap();

  const handleLocationRequest = () => {
    map.locate({ setView: true, maxZoom: 16 });
  };

  // Set up event handlers for geolocation
  useEffect(() => {
    map.on('locationfound', (e) => {
      setUserLocation([e.latlng.lat, e.latlng.lng]);
      map.flyTo(e.latlng, 16);
    });

    map.on('locationerror', (e) => {
      console.error("Location error:", e.message);
      alert("Unable to find your location. Please check your device settings.");
    });

    return () => {
      map.off('locationfound');
      map.off('locationerror');
    };
  }, [map, setUserLocation]);

  return (
    <div className="location-control">
      <button onClick={handleLocationRequest} title="Show my location">
        <i className="fas fa-location-arrow"></i>
      </button>
    </div>
  );
};

