import React, { useState, useEffect } from "react";
import { Container, Form, Button } from "react-bootstrap";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import api from '../utils/api';

const GOOGLE_CLIENT_ID = process.env.REACT_APP_GOOGLE_CLIENT_ID;

const SignUp = ({ setUser }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);
  const [redirectCountdown, setRedirectCountdown] = useState(null);

  // Handle redirect countdown
  useEffect(() => {
    let timer;
    if (redirectCountdown !== null && redirectCountdown > 0) {
      timer = setTimeout(() => {
        setRedirectCountdown(redirectCountdown - 1);
      }, 1000);
    } else if (redirectCountdown === 0) {
      // Redirect when countdown reaches zero
      window.location.href = "/login";
    }

    return () => {
      if (timer) clearTimeout(timer);
    };
  }, [redirectCountdown]);

  const redirect_login = () => {
    window.location.href = "/login";
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setLoading(true);

    // Basic validation
    if (!username.trim()) {
      setError("Username is required");
      setLoading(false);
      return;
    }

    if (!email.trim()) {
      setError("Email is required");
      setLoading(false);
      return;
    }

    if (!password.trim() || password.length < 6) {
      setError("Password must be at least 6 characters");
      setLoading(false);
      return;
    }

    try {
      const resp = await api.post('/signup', {
        username,
        email,
        password,
      });

      if (resp.status === 201) {
        setSuccess("Successfully signed up! Redirecting to login page in 5 seconds...");
        setRedirectCountdown(5);
      }
    } catch (err) {
      console.log(err);
      setError(err.response?.data?.message || "Error signing up. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const responseGoogle = async (response) => {
    setError("");
    setSuccess("");
    setLoading(true);

    try {
      const decoded = jwtDecode(response.credential);
      const { email, name } = decoded;

      const registerResp = await api.post('/login_google', {
        email,
        username: name,
      });

      if (registerResp.status === 200) {
        const token = registerResp.data.token;
        localStorage.setItem('jwtToken', token);
        localStorage.setItem('user', JSON.stringify(registerResp.data.user));

        // Update user state in parent component if setUser is available
        if (setUser) {
          setUser(registerResp.data.user);
        }

        setSuccess("Successfully logged in with Google! Redirecting...");

        // Short delay before redirect for better UX
        setTimeout(() => {
          const { role, id } = registerResp.data.user;
          window.location.replace(`/logged_in/${role}/${id}`);
        }, 1500);
      }
    } catch (err) {
      console.error("Google sign up error:", err);
      setError("Error signing in with Google. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="signup-box">
      <h1>Sign Up</h1>

      {/* Error message */}
      {error && <Alert variant="danger">{error}</Alert>}

      {/* Success message with countdown */}
      {success && (
        <Alert variant="success">
          {success}
          {redirectCountdown !== null && (
            <div className="countdown-timer mt-2">
              Redirecting in {redirectCountdown} seconds...
            </div>
          )}
        </Alert>
      )}

      <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
        <div className="google-loginbox">
          <GoogleLogin
            onSuccess={responseGoogle}
            buttonText="Signin with Google"
            onError={(err) => {
              if (err.error === "popup_closed_by_user") {
                setError("Popup closed before completing login.");
              } else if (err.error === "idpiframe_initialization_failed") {
                setError("Google login initialization failed. Please check your internet connection.");
              } else {
                setError("Google login error. Please try again.");
                console.error("Google login error:", err);
              }
            }}
            disabled={loading || redirectCountdown !== null}
          />
        </div>
      </GoogleOAuthProvider>

      <div className="login-form">
        <Form onSubmit={handleSignUp}>
          <Form.Group controlId="formBasicName">
            <Form.Control
              className="username"
              placeholder="Enter User Name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              disabled={loading || redirectCountdown !== null}
            />
          </Form.Group>

          <Form.Group controlId="formBasicEmail">
            <Form.Control
              className="email"
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              disabled={loading || redirectCountdown !== null}
            />
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Control
              className="password"
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              disabled={loading || redirectCountdown !== null}
            />
            <Button 
              variant="link" 
              onClick={() => setShowPassword(!showPassword)}
              className="show-password-toggle"
              disabled={loading || redirectCountdown !== null}
            >
              {showPassword ? "Hide" : "Show"} Password
            </Button>
          </Form.Group>

          <Button 
            className="signup-button" 
            type="submit" 
            variant="primary"
            disabled={loading || redirectCountdown !== null}
          >
            {loading ? "Signing up..." : "Sign Up"}
          </Button>
        </Form>
      </div>

      <div className="noaccount">
        <p>
          Already have an account?{" "}
          <span className="gotosignup" onClick={redirect_login} style={{ cursor: 'pointer' }}>
            Sign In
          </span>
        </p>
      </div>
    </Container>
  );
};

export default SignUp;
