import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents, useMap, Polyline } from 'react-leaflet';
import L from 'leaflet';
import api from '../../utils/api';
import 'leaflet-routing-machine';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import '../../css/user.css';

// Bus stop icon
const busStopIcon = new L.Icon({
    iconUrl: '/bus-stop.png', // Make sure this file exists in public folder
    iconSize: [24, 24],
    iconAnchor: [12, 24],
    popupAnchor: [0, -24]
});

// User location icon - BLUE color as requested
const userIcon = new L.Icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
});

// Search marker icon - RED color as requested
const searchMarkerIcon = new L.Icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
});

// Enhanced configuration for faster OSRM routing
// Enhanced configuration for faster OSRM routing
function configureRoutingMachine() {
    if (typeof L !== 'undefined' && L.Routing) {
        // Optimize global routing options for speed
        L.Routing.Itinerary.prototype.options.createGeocoderPane = false;
        L.Routing.timeout = 5 * 1000; // Reduced timeout to 5 seconds

        // Disable unnecessary features
        if (L.Routing.Control.prototype.options) {
            L.Routing.Control.prototype.options.showAlternatives = false;
            L.Routing.Control.prototype.options.useZoomParameter = false;
            L.Routing.Control.prototype.options.routeWhileDragging = false;
            L.Routing.Control.prototype.options.addWaypoints = false;
            L.Routing.Control.prototype.options.waypoints = [];
            L.Routing.Control.prototype.options.createMarker = null;
            L.Routing.Control.prototype.options.geocoder = null;
            L.Routing.Control.prototype.options.fitSelectedRoutes = false;
        }

        // Override the error handling globally
        if (L.Routing.ErrorControl && L.Routing.ErrorControl.prototype) {
            L.Routing.ErrorControl.prototype._routingErrorHandler = function (e) {
                console.warn("Handled routing error:", e);
                // No UI updates or errors thrown
            };
        }

        // COMPLETELY REPLACE the problematic _clearLines method
        if (L.Routing.Line && L.Routing.Line.prototype) {
            L.Routing.Line.prototype._clearLines = function () {
                try {
                    // Most defensive approach possible to prevent errors
                    if (!this._map) {
                        this._route = null;
                        return;
                    }

                    if (!this._route) {
                        return;
                    }

                    // Handle different possible structures of _route
                    if (this._route._layers) {
                        // Standard approach
                        try {
                            this._map.removeLayer(this._route);
                        } catch (e) {
                            console.warn("Protected from standard removeLayer error:", e);
                        }
                    } else if (typeof this._route.remove === 'function') {
                        // Alternative: if _route has its own remove method
                        try {
                            this._route.remove();
                        } catch (e) {
                            console.warn("Protected from remove() error:", e);
                        }
                    } else {
                        // Last resort: try direct removeLayer
                        try {
                            this._map.removeLayer(this._route);
                        } catch (e) {
                            console.warn("Protected from direct removeLayer error:", e);
                        }
                    }

                    // Make sure to null the reference regardless
                    this._route = null;
                } catch (e) {
                    console.warn("Complete protection from _clearLines error:", e);
                    // Final safety - null the reference even if everything else fails
                    this._route = null;
                }
            };
        }
    }
}

// Route caching system to avoid repeated requests for the same route
const routeCache = new Map();

// Calculate cache key for routes
const getRouteCacheKey = (start, end) => {
    return `${start[0]},${start[1]}_${end[0]},${end[1]}`;
};

// Create a utility function to safely remove a routing control
const safelyRemoveRoutingControl = (routingControl, map) => {
    if (!routingControl || !map) return;

    try {
        // First try to clear any route layers directly
        if (routingControl._router && routingControl._router._routes) {
            try {
                // Try to remove each route layer individually
                routingControl._router._routes.forEach(route => {
                    if (route && route.line) {
                        try {
                            if (typeof route.line.remove === 'function') {
                                route.line.remove();
                            } else if (map.hasLayer(route.line)) {
                                map.removeLayer(route.line);
                            }
                        } catch (e) {
                            console.warn("Error removing route line:", e);
                        }
                    }
                });
                // Clear the routes array
                routingControl._router._routes = [];
            } catch (e) {
                console.warn("Error clearing router routes:", e);
            }
        }

        // Try to remove the line layer directly if it exists
        if (routingControl._line) {
            try {
                // Try using the patched _clearLines method
                if (typeof routingControl._line._clearLines === 'function') {
                    routingControl._line._clearLines();
                }
                // Backup: try to access the route directly
                else if (routingControl._line._route && map.hasLayer(routingControl._line._route)) {
                    map.removeLayer(routingControl._line._route);
                }
            } catch (e) {
                console.warn("Error clearing lines:", e);
            }
        }

        // Clear all waypoints
        try {
            if (routingControl._plan && typeof routingControl._plan.setWaypoints === 'function') {
                routingControl._plan.setWaypoints([]);
            }
        } catch (e) {
            console.warn("Error clearing waypoints:", e);
        }

        // Finally remove the control from the map
        try {
            map.removeControl(routingControl);
        } catch (e) {
            console.warn("Error removing control:", e);
        }
    } catch (error) {
        console.warn("Safe removal failed:", error);
    }
};

// Enhanced RoutingControl component that tries proper routing with fallback
const RoutingControl = ({ startPoint, endPoint, color = '#ff6b6b', weight = 4, setIsPathLoading, onRoutingFailed }) => {
    const map = useMap();
    const routingControlRef = useRef(null);
    const polylineRef = useRef(null);
    const timeoutRef = useRef(null);

    useEffect(() => {
        // Start loading
        setIsPathLoading(true);

        // Clean up any existing routes
        if (routingControlRef.current && map) {
            try {
                map.removeControl(routingControlRef.current);
            } catch (e) {
                console.warn("Error removing routing control:", e);
            }
            routingControlRef.current = null;
        }

        if (polylineRef.current && map) {
            map.removeLayer(polylineRef.current);
            polylineRef.current = null;
        }

        // If we don't have both points, don't create a route
        if (!startPoint || !endPoint) {
            setIsPathLoading(false);
            return;
        }

        // Set a timeout to show fallback route if OSRM takes too long
        timeoutRef.current = setTimeout(() => {
            // If we already have a route, don't create a fallback
            if (routingControlRef.current) return;

            console.log("Routing service timed out, falling back to direct line");
            createFallbackRoute();
        }, 3000);

        // Try to use proper routing first
        try {
            const routingControl = L.Routing.control({
                waypoints: [
                    L.latLng(startPoint[0], startPoint[1]),
                    L.latLng(endPoint[0], endPoint[1])
                ],
                lineOptions: {
                    styles: [{ color, opacity: 0.7, weight }],
                    extendToWaypoints: true,
                    missingRouteTolerance: 0
                },
                routeWhileDragging: false,
                showAlternatives: false,
                addWaypoints: false,
                fitSelectedRoutes: false,
                show: false,
                createMarker: () => null // No markers from routing
            });

            // Add event listeners for routing success/failure
            routingControl.on('routesfound', () => {
                console.log("Routes found successfully");
                // Clear timeout since we got a successful route
                if (timeoutRef.current) {
                    clearTimeout(timeoutRef.current);
                    timeoutRef.current = null;
                }

                // Remove any fallback route if it was created
                if (polylineRef.current && map) {
                    map.removeLayer(polylineRef.current);
                    polylineRef.current = null;
                }

                setIsPathLoading(false);
            });

            routingControl.on('routingerror', (e) => {
                console.log("Routing error, falling back to direct line", e);
                // If routing fails, create a direct line instead
                createFallbackRoute();

                // Clear the routing control
                try {
                    map.removeControl(routingControl);
                } catch (e) {
                    console.warn("Error removing routing control after error:", e);
                }
                routingControlRef.current = null;
            });

            routingControl.addTo(map);
            routingControlRef.current = routingControl;
        } catch (e) {
            console.error("Error setting up routing:", e);
            createFallbackRoute();
        }

        // Function to create a direct line as fallback
        function createFallbackRoute() {
            if (polylineRef.current) return; // Don't create if already exists

            const routeCoordinates = [
                [startPoint[0], startPoint[1]],
                [endPoint[0], endPoint[1]]
            ];

            const polyline = L.polyline(routeCoordinates, {
                color: color,
                weight: weight,
                opacity: 0.7,
                dashArray: '5, 10', // Use dashed line to indicate this is a fallback
                lineJoin: 'round'
            });

            polyline.addTo(map);
            polylineRef.current = polyline;
            setIsPathLoading(false);
        }

        // Cleanup function
        return () => {
            if (timeoutRef.current) {
                clearTimeout(timeoutRef.current);
            }

            if (routingControlRef.current && map) {
                try {
                    map.removeControl(routingControlRef.current);
                } catch (e) {
                    console.warn("Error removing routing control during cleanup:", e);
                }
            }

            if (polylineRef.current && map) {
                map.removeLayer(polylineRef.current);
            }

            setIsPathLoading(false);
        };
    }, [map, startPoint, endPoint, color, weight, setIsPathLoading, onRoutingFailed]);

    return null;
};

// Component to update map center when user location changes
const MapController = ({ center, zoom }) => {
    const map = useMap();

    useEffect(() => {
        if (center) {
            map.setView(center, zoom || map.getZoom());
        }
    }, [map, center, zoom]);

    return null;
};

// Button to center on user's location
const LocationButton = ({ userLocation, onClick }) => {
    return (
        <button
            className="location-button"
            onClick={onClick}
            disabled={!userLocation}
            title="Center map on your location"
        >
            <i className="fas fa-location-arrow"></i> Your Current Location
        </button>
    );
};

const BusStopSearch = ({ userLocation, setUserLocation }) => {
    const [busStops, setBusStops] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [mapCenter, setMapCenter] = useState(userLocation || [22.3190, 87.3091]);
    const [zoom, setZoom] = useState(15);
    const [coordinates, setCoordinates] = useState({
        latitude: '',
        longitude: ''
    });
    const [searchMarker, setSearchMarker] = useState(userLocation || [22.3190, 87.3091]);
    const [searchResults, setSearchResults] = useState([]);
    const [hasSearched, setHasSearched] = useState(false);
    const [isPathLoading, setIsPathLoading] = useState(false);
    const [selectedStop, setSelectedStop] = useState(null);
    const [routingKey, setRoutingKey] = useState(0);
    const [routingFailMessage, setRoutingFailMessage] = useState(null);

    const mapRef = useRef(null);
    const markerRefs = useRef({});

    // Register marker references for later access
    const registerMarker = (id, markerRef) => {
        if (markerRef) {
            markerRefs.current[id] = markerRef;
        }
    };

    // Initialize coordinates with user's location as soon as it's available
    useEffect(() => {
        if (userLocation) {
            setCoordinates({
                latitude: userLocation[0].toFixed(6),
                longitude: userLocation[1].toFixed(6)
            });
            setMapCenter(userLocation);
            setSearchMarker(userLocation);
        }
    }, [userLocation]);

    // Fetch all bus stops directly from the database
    const fetchBusStops = async () => {
        try {
            console.log('Fetching bus stops from API');
            const response = await api.get('/bus_stops/getAllBusStops');
            console.log('Raw API response:', response.data);

            // Extract the actual bus stops array from the response
            if (response.data && response.data.data) {
                return response.data.data;
            } else {
                console.error('Invalid response format:', response.data);
                return [];
            }
        } catch (error) {
            console.error('Error fetching bus stops from database:', error);
            throw error;
        }
    };

    // Load bus stops on component mount
    useEffect(() => {
        const loadBusStops = async () => {
            try {
                setLoading(true);
                const data = await fetchBusStops();

                // Ensure data is an array before mapping
                const busStopsArray = Array.isArray(data) ? data : [];

                // Clean and format the data
                const cleanedData = busStopsArray.map(stop => ({
                    ...stop,
                    latitude: parseFloat(stop.latitude),
                    longitude: parseFloat(stop.longitude)
                }));

                setBusStops(cleanedData);
                setError(null);
            } catch (err) {
                console.error("Error loading bus stops:", err);
                setError("Failed to load bus stops. Please try again later.");
            } finally {
                setLoading(false);
            }
        };

        loadBusStops();
    }, []);

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCoordinates(prev => ({
            ...prev,
            [name]: value
        }));
    };

    // Center map on user location with maximum zoom
    const handleCenterOnUser = () => {
        if (userLocation) {
            setMapCenter(userLocation);
            setZoom(19); // Maximum zoom

            // Also update the coordinate inputs to match user location
            setCoordinates({
                latitude: userLocation[0].toFixed(6),
                longitude: userLocation[1].toFixed(6)
            });
        }
    };

    // Function to search for nearby bus stops
    const handleSearch = () => {
        try {
            const lat = parseFloat(coordinates.latitude);
            const lng = parseFloat(coordinates.longitude);

            // Validate coordinates
            if (isNaN(lat) || isNaN(lng) ||
                lat < -90 || lat > 90 ||
                lng < -180 || lng > 180) {
                setError("Please enter valid coordinates");
                return;
            }

            // First clear previous route
            setSelectedStop(null);
            setRoutingKey(Date.now());  // Use timestamp for unique key
            setRoutingFailMessage(null);

            setError(null);

            // Use a timeout to ensure previous route is cleared
            setTimeout(() => {
                setSearchMarker([lat, lng]);

                // Calculate distance from specified point to all bus stops
                const results = busStops.map(stop => {
                    // Calculate distance in meters using Haversine formula
                    const distance = calculateDistance(
                        lat, lng,
                        stop.latitude, stop.longitude
                    );

                    return {
                        ...stop,
                        distance: distance
                    };
                });

                // Sort by distance (closest first)
                const sortedResults = [...results].sort((a, b) => a.distance - b.distance);
                setSearchResults(sortedResults);
                setHasSearched(true);

                // Center map on search marker
                setMapCenter([lat, lng]);
                setZoom(16);
            }, 50);
        } catch (error) {
            console.error("Search error:", error);
            setError("An error occurred during search");
        }
    };

    // Handle click on search result item - FIXED VERSION
    const handleStopSelect = (stop) => {
        // First clear the previous route
        setSelectedStop(null);

        // Generate a new unique routing key using timestamp
        const newRoutingKey = Date.now();
        setRoutingKey(newRoutingKey);
        setRoutingFailMessage(null);

        // Use a timeout to ensure the previous route is completely cleared
        setTimeout(() => {
            // Set the map center and zoom
            setMapCenter([stop.latitude, stop.longitude]);
            setZoom(18);

            // Then set the new selected stop to create a new route
            setSelectedStop(stop);

            // Open the popup for this marker
            const marker = markerRefs.current[stop.id];
            if (marker) {
                marker.openPopup();
            }
        }, 50);
    };

    // Calculate distance between two coordinates using Haversine formula
    const calculateDistance = (lat1, lon1, lat2, lon2) => {
        const R = 6371e3; // Earth's radius in meters
        const φ1 = lat1 * Math.PI / 180;
        const φ2 = lat2 * Math.PI / 180;
        const Δφ = (lat2 - lat1) * Math.PI / 180;
        const Δλ = (lon1 - lon2) * Math.PI / 180;

        const a = Math.sin(Δφ / 2) * Math.sin(Δφ / 2) +
            Math.cos(φ1) * Math.cos(φ2) *
            Math.sin(Δλ / 2) * Math.sin(Δλ / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

        return R * c; // Distance in meters
    };

    // Format distance for display
    const formatDistance = (meters) => {
        if (meters < 1000) {
            return `${Math.round(meters)} meters`;
        } else {
            return `${(meters / 1000).toFixed(2)} km`;
        }
    };

    // Ensure loading screen doesn't stay forever
    useEffect(() => {
        if (isPathLoading) {
            const timeout = setTimeout(() => {
                setIsPathLoading(false);
            }, 5000); // 5 second timeout

            return () => clearTimeout(timeout);
        }
    }, [isPathLoading]);

    // Handle routing failure
    const handleRoutingFailed = () => {
        setRoutingFailMessage("Routing failed. Please try again later.");

        // Auto-hide the message after 5 seconds
        setTimeout(() => {
            setRoutingFailMessage(null);
        }, 5000);
    };

    // Pre-configure routing machine on component mount
    useEffect(() => {
        configureRoutingMachine();
    }, []);

    return (
        <div className="bus-stop-search">
            {isPathLoading && (
                <div className="full-page-loading-overlay">
                    <div className="spinner"></div>
                    <div>Drawing path. Please wait...</div>
                </div>
            )}

            {routingFailMessage && (
                <div className="routing-fail-message">
                    {routingFailMessage}
                </div>
            )}

            <div className="location-panel">
                <div className="location-panel-top">
                    <h2>Search Bus Stops</h2>
                    {error && <div className="error-message">{error}</div>}

                    <div className="search-form">
                        <div className="form-group">
                            <label htmlFor="latitude">Latitude:</label>
                            <input
                                type="text"
                                id="latitude"
                                name="latitude"
                                value={coordinates.latitude}
                                onChange={handleInputChange}
                                placeholder="e.g. 22.3190"
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="longitude">Longitude:</label>
                            <input
                                type="text"
                                id="longitude"
                                name="longitude"
                                value={coordinates.longitude}
                                onChange={handleInputChange}
                                placeholder="e.g. 87.3091"
                            />
                        </div>

                        <p className="help-text">Click on the map to set coordinates or use your current location</p>

                        <button
                            className="search-btn"
                            onClick={handleSearch}
                            disabled={loading}
                        >
                            {loading ? 'Loading...' : 'Find Nearest Bus Stops'}
                        </button>
                    </div>
                </div>

                <div className="location-panel-bottom">
                    {hasSearched && searchResults.length > 0 && (
                        <div className="search-results">
                            <h3>Nearest Bus Stops</h3>
                            <div className="results-list">
                                {searchResults.map((stop, index) => (
                                    <div
                                        key={stop.id}
                                        className="result-item"
                                        onClick={() => handleStopSelect(stop)}
                                    >
                                        <div className="result-index">{index + 1}</div>
                                        <div className="result-details">
                                            <h4>{stop.name}</h4>
                                            <p className="distance">Distance: {formatDistance(stop.distance)}</p>
                                            <p className="coordinates">
                                                {stop.latitude.toFixed(6)}, {stop.longitude.toFixed(6)}
                                            </p>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}

                    {hasSearched && searchResults.length === 0 && (
                        <div className="no-results">
                            <p>No bus stops found near the selected location.</p>
                        </div>
                    )}
                </div>
            </div>

            <div className="map-container">
                <MapContainer
                    center={mapCenter}
                    zoom={zoom}
                    style={{ height: "100%", width: "100%" }}
                    ref={mapRef}
                >
                    <TileLayer
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />

                    <MapController center={mapCenter} zoom={zoom} />
                    <MapEvents setCoordinates={setCoordinates} setSearchMarker={setSearchMarker} />

                    {userLocation && (
                        <Marker position={userLocation} icon={userIcon}>
                            <Popup>
                                <div>
                                    <strong>Your Location</strong><br />
                                    {userLocation[0].toFixed(6)}, {userLocation[1].toFixed(6)}
                                </div>
                            </Popup>
                        </Marker>
                    )}

                    {searchMarker && (
                        <Marker position={searchMarker} icon={searchMarkerIcon}>
                            <Popup>
                                <div>
                                    <strong>Search Location</strong><br />
                                    {searchMarker[0].toFixed(6)}, {searchMarker[1].toFixed(6)}
                                </div>
                            </Popup>
                        </Marker>
                    )}

                    {busStops.map(stop => (
                        <Marker
                            key={stop.id}
                            position={[stop.latitude, stop.longitude]}
                            icon={busStopIcon}
                            ref={(ref) => registerMarker(stop.id, ref)}
                        >
                            <Popup>
                                <div>
                                    <strong>{stop.name}</strong><br />
                                    {searchMarker && (
                                        <span>
                                            Distance: {formatDistance(
                                                calculateDistance(
                                                    searchMarker[0], searchMarker[1],
                                                    stop.latitude, stop.longitude
                                                )
                                            )}
                                        </span>
                                    )}
                                </div>
                            </Popup>
                        </Marker>
                    ))}

                    {/* Key route component with improved key generation to force re-renders */}
                    {searchMarker && selectedStop && (
                        <RoutingControl
                            key={`route-${routingKey}-${selectedStop.id}`}
                            startPoint={searchMarker}
                            endPoint={[selectedStop.latitude, selectedStop.longitude]}
                            color="#ff6b6b"
                            weight={4}
                            setIsPathLoading={setIsPathLoading}
                            onRoutingFailed={handleRoutingFailed}
                        />
                    )}

                    <div className="map-controls">
                        <LocationButton
                            userLocation={userLocation}
                            onClick={handleCenterOnUser}
                        />
                    </div>
                </MapContainer>
            </div>
        </div>
    );
};

// Component to handle map clicks
const MapEvents = ({ setCoordinates, setSearchMarker }) => {
    useMapEvents({
        click: (e) => {
            const { lat, lng } = e.latlng;
            setCoordinates({
                latitude: lat.toFixed(6),
                longitude: lng.toFixed(6)
            });
            setSearchMarker([lat, lng]);
        }
    });

    return null;
};

export default BusStopSearch;
