import React, { useState, useEffect } from 'react';
import '../../css/user.css';
import api from '../../utils/api.js';
import BusTracking from './BusTracking';
import BusStopsView from './BusStopsView';

const User = () => {
    const [userLocation, setUserLocation] = useState(null);
    const [authError, setAuthError] = useState(null);
    const [activeTab, setActiveTab] = useState('busStops');

    // Get user's location
    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    setUserLocation([latitude, longitude]);
                },
                (err) => {
                    console.error("Geolocation error:", err.message);
                    // Fall back to default location
                    setUserLocation([22.3149, 87.3104]);
                }
            );
        }
    }, []);

    // Check if user is logged in
    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        if (!token) {
            setAuthError('You must be logged in to access this feature');
        }
    }, []);

    return (
        <div className="map-interface">
            <div className="header-panel">
                <h2>KGP Bus Tracker</h2>

                {authError && (
                    <div className="auth-error-message">
                        <p>{authError}</p>
                        <button onClick={() => window.location.href = '/login'}>
                            Go to Login
                        </button>
                    </div>
                )}

                <div className="tab-navigation">
                    <button 
                        className={`tab-button ${activeTab === 'busStops' ? 'active' : ''}`}
                        onClick={() => setActiveTab('busStops')}
                    >
                        Bus Stops
                    </button>
                    <button 
                        className={`tab-button ${activeTab === 'trackBus' ? 'active' : ''}`}
                        onClick={() => setActiveTab('trackBus')}
                    >
                        Track Bus
                    </button>
                </div>
            </div>

            <div className="content-area">
                {activeTab === 'busStops' ? (
                    <BusStopsView userLocation={userLocation} />
                ) : (
                    <BusTracking userLocation={userLocation} />
                )}
            </div>
        </div>
    );
};

export default User;
