import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import apiConfig, { getApiUrl } from '../utils/api2.js';
import BusManagement from './admin/BusManagement';
import StopManagement from './admin/StopManagement';
import RouteManagement from './admin/RouteManagement';
import DriverManagement from './admin/DriverManagement';
import SystemStatistics from './admin/SystemStatistics';
import UserLocations from './admin/UserLocations';

function AdminDashboard() {
  const [activeTab, setActiveTab] = useState('overview');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();
  const { id } = useParams();

  // Create authenticated axios instance specifically for admin API calls
  const createAuthenticatedAxiosInstance = () => {
    const token = localStorage.getItem('jwtToken');
    if (!token) {
      console.error("No token found in localStorage!");
      // Navigate to login if no token
      navigate('/login');
      return null;
    }

    return axios.create({
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      withCredentials: true
    });
  };

  useEffect(() => {
    const fetchAdminData = async () => {
      try {
        // First try to get user from localStorage
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
          const parsedUser = JSON.parse(storedUser);
          if (parsedUser.role === 'admin') {
            console.log("Admin user found in localStorage:", parsedUser.id);
            setUser(parsedUser);
            setLoading(false);
            return;
          }
        }

        // If no stored user or not admin, fetch from API
        const token = localStorage.getItem('jwtToken');
        if (!token) {
          throw new Error("No authentication token found");
        }

        // Get user data from backend using the id param and authenticated request
        const response = await axios.get(`/logged_in/${id}/admin`, {
          headers: {
            'Authorization': `Bearer ${token}`
          },
          withCredentials: true
        });

        if (response.data && response.data.user) {
          const userData = response.data.user;
          // Add token to user data for easier access in child components
          userData.token = token;
          // Store user in localStorage for future requests
          localStorage.setItem('user', JSON.stringify(userData));
          setUser(userData);
          console.log("Admin user fetched from API:", userData.id);
        } else {
          throw new Error("Invalid user data received");
        }
      } catch (err) {
        console.error("Error fetching admin data:", err);
        setError("Failed to load admin dashboard. Please log in again.");

        // If there's an auth error, redirect to login
        if (err.response && (err.response.status === 401 || err.response.status === 403)) {
          setTimeout(() => navigate('/login'), 2000);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchAdminData();
  }, [id, navigate]);

  // Show loading state
  if (loading) {
    return <div className="loading">Loading admin dashboard...</div>;
  }

  // Show error message
  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => navigate('/login')}>Return to Login</button>
      </div>
    );
  }

  // Show unauthorized message if user is not an admin
  if (!user || user.role !== 'admin') {
    return (
      <div className="unauthorized">
        <h2>Unauthorized Access</h2>
        <p>You must be an administrator to access this page.</p>
        <button onClick={() => navigate('/login')}>Go to Login</button>
      </div>
    );
  }

  // Get token for API calls
  const token = localStorage.getItem('jwtToken');

  // Debug token format to help diagnose issues
  console.log("Token format check:", {
    exists: !!token,
    length: token ? token.length : 0,
    // Show only first 10 chars to avoid logging sensitive data
    preview: token ? token.substring(0, 10) + '...' : null
  });

  // Render content based on active tab
  const renderContent = () => {
    switch (activeTab) {
      case 'buses':
        return <BusManagement user={{...user, token}} />;
      case 'stops':
        return <StopManagement user={{...user, token}} />;
      case 'routes':
        return <RouteManagement user={{...user, token}} />;
      case 'drivers':
        return <DriverManagement user={{...user, token}} />;
      case 'stats':
        return <SystemStatistics user={{...user, token}} />;
      case 'locations':
        return <UserLocations user={{...user, token}} />;
      default:
        return <AdminOverview user={{...user, token}} navigate={navigate} />;
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="admin-header">
        <h1>KGP Bus Admin Dashboard</h1>
        <div className="user-info">
          <span>Welcome, {user.email}</span>
          <button onClick={() => {
            axios.post('/logout', {}, { 
              withCredentials: true,
              headers: { 'Authorization': `Bearer ${token}` }
            })
              .then(() => {
                localStorage.removeItem('jwtToken');
                localStorage.removeItem('user');
                navigate('/login');
              })
              .catch(err => {
                console.error("Logout error:", err);
                localStorage.removeItem('jwtToken');
                localStorage.removeItem('user');
                navigate('/login');
              });
          }}>Logout</button>
        </div>
      </div>

      <div className="admin-container">
        <div className="admin-sidebar">
          <nav>
            <ul>
              <li 
                className={activeTab === 'overview' ? 'active' : ''} 
                onClick={() => setActiveTab('overview')}>
                  Dashboard Overview
              </li>
              <li 
                className={activeTab === 'buses' ? 'active' : ''} 
                onClick={() => setActiveTab('buses')}>
                  Manage Buses
              </li>
              <li 
                className={activeTab === 'stops' ? 'active' : ''} 
                onClick={() => setActiveTab('stops')}>
                  Manage Bus Stops
              </li>
              <li 
                className={activeTab === 'routes' ? 'active' : ''} 
                onClick={() => setActiveTab('routes')}>
                  Manage Routes
              </li>
              <li 
                className={activeTab === 'drivers' ? 'active' : ''} 
                onClick={() => setActiveTab('drivers')}>
                  Manage Drivers
              </li>
              <li 
                className={activeTab === 'stats' ? 'active' : ''} 
                onClick={() => setActiveTab('stats')}>
                  System Statistics
              </li>
              <li 
                className={activeTab === 'locations' ? 'active' : ''} 
                onClick={() => setActiveTab('locations')}>
                  Track User Locations
              </li>
            </ul>
