import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import api from '../../utils/api.js';
import '../../css/busTracking.css';

// Custom bus icon
const busIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/30/30979.png',
  iconSize: [32, 32],
  iconAnchor: [16, 16],
  popupAnchor: [0, -16]
});

// User location icon
const userIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/1077/1077114.png',
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

// Bus stop icon
const busStopIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/1165/1165895.png',
  iconSize: [24, 24],
  iconAnchor: [12, 24],
  popupAnchor: [0, -24]
});

// Component to handle routing between points
const RoutingControl = ({ startPoint, endPoint, color = '#3388ff', weight = 4 }) => {
  const map = useMap();
  const routingControlRef = useRef(null);

  useEffect(() => {
    if (!startPoint || !endPoint) return;

    // Clear previous routing if it exists
    if (routingControlRef.current) {
      map.removeControl(routingControlRef.current);
      routingControlRef.current = null;
    }

    try {
      // Create new routing control
      const routingControl = L.Routing.control({
        waypoints: [
          L.latLng(startPoint[0], startPoint[1]),
          L.latLng(endPoint[0], endPoint[1])
        ],
        routeWhileDragging: false,
        showAlternatives: false,
        addWaypoints: false,
        fitSelectedRoutes: false,
        show: false,
        lineOptions: {
          styles: [{ color, opacity: 0.7, weight }],
          extendToWaypoints: true,
          missingRouteTolerance: 0
        },
        createMarker: () => null // No markers from routing
      });

      routingControl.addTo(map);
      routingControlRef.current = routingControl;

      // Set up cleanup function
      return () => {
        if (routingControlRef.current) {
          map.removeControl(routingControlRef.current);
        }
      };
    } catch (error) {
      console.error("Error setting up routing:", error);
    }
  }, [map, startPoint, endPoint, color, weight]);

  return null;
};

// Component to recenter map when needed
const MapViewController = ({ center, zoom, busLocation }) => {
  const map = useMap();

  useEffect(() => {
    if (center && zoom) {
      map.setView(center, zoom);
    } else if (busLocation) {
      map.setView(busLocation, map.getZoom());
    }
  }, [map, center, zoom, busLocation]);

  return null;
};

const BusTracking = ({ userLocation }) => {
  const [buses, setBuses] = useState([]);
  const [selectedBus, setSelectedBus] = useState(null);
  const [busLocation, setBusLocation] = useState(null);
  const [busStops, setBusStops] = useState([]);
  const [nextStop, setNextStop] = useState(null);
  const [currentStop, setCurrentStop] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [busInfo, setBusInfo] = useState(null);
  const [mapCenter, setMapCenter] = useState(userLocation || [22.3190, 87.3091]); // Default to IIT KGP
  const [searchTerm, setSearchTerm] = useState('');
  const [isInitialLoad, setIsInitialLoad] = useState(true);
  const intervalRef = useRef(null);

  // Fetch list of buses on mount
  useEffect(() => {
    fetchBuses();
  }, []);

  // Clean up interval on unmount
  useEffect(() => {
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  // Fetch bus data when selection changes
  useEffect(() => {
    if (selectedBus) {
      fetchBusData(selectedBus.id);
      setIsInitialLoad(false);

      // Set up interval for refreshing bus location
      intervalRef.current = setInterval(() => {
        fetchBusData(selectedBus.id, false);
      }, 30000); // Refresh every 30 seconds
    } else {
      // Clear interval when no bus is selected
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [selectedBus]);

  const fetchBuses = async () => {
    try {
      setLoading(true);
      const response = await api.get('/buses/getAllBuses');
      if (response.data && response.data.data) {
        setBuses(response.data.data);
      }
      setLoading(false);
    } catch (err) {
      console.error('Error fetching buses:', err);
      setError('Failed to load buses. Please try again later.');
      setLoading(false);
    }
  };

  const fetchBusData = async (busId, showLoading = true) => {
    try {
      if (showLoading) setLoading(true);

      // Clear error when fetching new data
      setError(null);

      // Fetch bus location
      const locationResponse = await api.get(`/buses/${busId}/location`);
      if (locationResponse.data && locationResponse.data.data) {
        const location = locationResponse.data.data;
        setBusLocation([parseFloat(location.latitude), parseFloat(location.longitude)]);
        setLastUpdated(new Date(location.timestamp));

        // If it's the first load or significant movement, center map on bus
        if (isInitialLoad || !busLocation) {
          setMapCenter([parseFloat(location.latitude), parseFloat(location.longitude)]);
        }
      } else {
        // Just set bus location to null without an error message
        setBusLocation(null);
      }

      // Fetch bus route information
      const routeResponse = await api.get(`/buses/${busId}/route`);
      if (routeResponse.data && routeResponse.data.data) {
        const routeData = routeResponse.data.data;
        setBusStops(routeData.stops);

        // Find current and next stop
        if (routeData.currentStop) {
          setCurrentStop(routeData.currentStop);
        }

        if (routeData.nextStop) {
          setNextStop(routeData.nextStop);
        }
      }

      // Fetch bus detailed info including driver
      const infoResponse = await api.get(`/buses/${busId}/info`);
      if (infoResponse.data && infoResponse.data.data) {
        setBusInfo(infoResponse.data.data);
      }

      if (showLoading) setLoading(false);
    } catch (err) {
      console.error('Error fetching bus data:', err);
      // Only set an error if there's a network/server issue, not for missing bus data
      setError('Server connection issue. Please try again later.');
      if (showLoading) setLoading(false);
    }
  };

  const handleBusSelect = (busId) => {
    const bus = buses.find(b => b.id === parseInt(busId));
    setSelectedBus(bus);
    setError(null);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredBuses = searchTerm 
    ? buses.filter(bus => bus.name.toLowerCase().includes(searchTerm.toLowerCase()))
    : buses;

  return (
    <div className="bus-tracking-container">
      <div className="bus-selection-panel">
        <h3>Track a Bus</h3>

        <div className="bus-search">
          <div className="search-input-group">
            <input
              type="text"
              placeholder="Search for a bus..."
              value={searchTerm}
              onChange={handleSearchChange}
              className="bus-search-input"
            />
            <div className="bus-dropdown">
              {filteredBuses.length > 0 ? (
                <select
                  value={selectedBus ? selectedBus.id : ''}
                  onChange={(e) => handleBusSelect(e.target.value)}
                  className="bus-select"
                >
                  <option value="">Select a bus</option>
                  {filteredBuses.map(bus => (
                    <option key={bus.id} value={bus.id}>{bus.name}</option>
                  ))}
                </select>
              ) : (
                <p className="no-buses-message">No buses found</p>
              )}
            </div>
          </div>

          <button 
            className="track-bus-btn"
            onClick={() => selectedBus && fetchBusData(selectedBus.id)}
            disabled={!selectedBus || loading}
          >
            {loading ? 'Loading...' : 'Track Bus'}
          </button>
        </div>

        {/* Only display network/server errors, not missing bus data errors */}
        {error && <div className="error-message">{error}</div>}

        {selectedBus && !busLocation && (
          <div className="selected-bus-info">
            <h4>{selectedBus.name}</h4>
            <div className="bus-status not-active">
              <p>This bus is currently not in service.</p>
            </div>
          </div>
        )}

        {selectedBus && busLocation && (
          <div className="selected-bus-info">
            <h4>{selectedBus.name}</h4>
            <div className="bus-status active">
              <p><strong>Status:</strong> Active</p>
              <p><strong>Last Updated:</strong> {lastUpdated ? lastUpdated.toLocaleTimeString() : 'N/A'}</p>

              {busInfo && (
                <>
                  <p><strong>Driver:</strong> {busInfo.driverName || 'Not assigned'}</p>
                  {currentStop && (
                    <p><strong>Last Stop:</strong> {currentStop.name}</p>
                  )}
                  {nextStop && (
                    <>
                      <p><strong>Next Stop:</strong> {nextStop.name}</p>
                      {busInfo.estimatedArrival && (
                        <p><strong>ETA:</strong> {busInfo.estimatedArrival} min</p>
                      )}
                    </>
                  )}
                </>
              )}
            </div>
          </div>
        )}

        {!selectedBus && (
          <div className="initial-message">
            <p>Please select a bus from the dropdown above to track its location.</p>
          </div>
        )}
      </div>

      {/* Always show the map container, even when no bus is selected */}
      <div className="bus-map-container">
        <MapContainer
          center={userLocation || [22.3190, 87.3091]} // Use user location or default to IIT KGP
          zoom={15}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Show user's current location on map */}
          {userLocation && (
            <Marker position={userLocation} icon={userIcon}>
              <Popup>Your location</Popup>
            </Marker>
          )}

          {/* Only show MapViewController when we have relevant locations */}
          {(selectedBus && busLocation) && (
            <MapViewController 
              center={mapCenter} 
              busLocation={busLocation}
            />
          )}

          {/* Show bus location only if available */}
          {(selectedBus && busLocation) && (
            <Marker position={busLocation} icon={busIcon}>
              <Popup>
                <div className="bus-popup">
                  <h3>{selectedBus.name}</h3>
                  {busInfo && (
                    <>
                      <p><strong>Driver:</strong> {busInfo.driverName || 'Not assigned'}</p>
                      <p><strong>Last Updated:</strong> {lastUpdated ? lastUpdated.toLocaleTimeString() : 'N/A'}</p>
                      {busInfo.estimatedArrival && nextStop && (
                        <p><strong>ETA to {nextStop.name}:</strong> {busInfo.estimatedArrival} min</p>
                      )}
                    </>
                  )}
                </div>
              </Popup>
            </Marker>
          )}

          {/* Show current and next bus stops if available */}
          {(selectedBus && currentStop) && (
            <Marker 
              position={[parseFloat(currentStop.latitude), parseFloat(currentStop.longitude)]} 
              icon={busStopIcon}
            >
              <Popup>
                <div className="stop-popup">
                  <h3>{currentStop.name}</h3>
                  <p>Last stop</p>
                </div>
              </Popup>
            </Marker>
          )}

          {(selectedBus && nextStop) && (
            <Marker 
              position={[parseFloat(nextStop.latitude), parseFloat(nextStop.longitude)]} 
              icon={busStopIcon}
            >
              <Popup>
                <div className="stop-popup">
                  <h3>{nextStop.name}</h3>
                  <p>Next stop</p>
                  {busInfo && busInfo.estimatedArrival && (
                    <p>Bus arrives in approx. {busInfo.estimatedArrival} min</p>
                  )}
                </div>
              </Popup>
            </Marker>
          )}

          {/* Only show routing if we have bus location and next stop */}
          {(selectedBus && busLocation && nextStop) && (
            <RoutingControl 
              startPoint={busLocation} 
              endPoint={[parseFloat(nextStop.latitude), parseFloat(nextStop.longitude)]}
              color="#3388ff"
            />
          )}

          {/* Show path from user location to next stop if both are available */}
          {(selectedBus && userLocation && nextStop) && (
            <RoutingControl 
              startPoint={userLocation} 
              endPoint={[parseFloat(nextStop.latitude), parseFloat(nextStop.longitude)]}
              color="#ff6b6b"
              weight={3}
            />
          )}
        </MapContainer>

        {/* Only show the map legend if we have a selected bus with routing */}
        {(selectedBus && busLocation && nextStop) && (
          <div className="map-legend">
            <div className="legend-item">
              <div className="legend-icon" style={{ backgroundColor: '#3388ff' }}></div>
              <span>Bus Route to Next Stop</span>
            </div>
            {userLocation && (
              <div className="legend-item">
                <div className="legend-icon" style={{ backgroundColor: '#ff6b6b' }}></div>
                <span>Your Path to Next Stop</span>
              </div>
            )}
          </div>
        )}

        {/* Add instructions overlay for initial map state */}
        {!selectedBus && (
          <div className="map-instructions-overlay">
            <p>Select a bus from the left panel to see its location and route</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default BusTracking;
