// frontend/components/busStopsView.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import '../../css/busStopsView.css'; // Use busStopsView.css for styles

// Custom bus icon
const busIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/30/30979.png',
  iconSize: [32, 32],
  iconAnchor: [16, 16],
  popupAnchor: [0, -16]
});

// Bus stop icon
const stopIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/484/484167.png',
  iconSize: [24, 24],
  iconAnchor: [12, 24],
  popupAnchor: [0, -24]
});

const BusStopsView = () => {
    const [busStops, setBusStops] = useState([]);
    const [fromStop, setFromStop] = useState('');
    const [toStop, setToStop] = useState('');
    const [buses, setBuses] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);
    const [selectedBusLocation, setSelectedBusLocation] = useState(null);
    const [mapCenter, setMapCenter] = useState([22.3190, 87.3091]); // Default center coordinates

    // Reference to map component
    const mapRef = useRef(null);

    // New states for custom dropdowns
    const [isFromDropdownOpen, setIsFromDropdownOpen] = useState(false);
    const [isToDropdownOpen, setIsToDropdownOpen] = useState(false);
    const [searchFrom, setSearchFrom] = useState('');
    const [searchTo, setSearchTo] = useState('');
    const fromDropdownRef = useRef(null);
    const toDropdownRef = useRef(null);

    // Fetch all bus stops on component mount
    useEffect(() => {
        const fetchBusStops = async () => {
            setLoading(true);
            try {
                console.log('Fetching bus stops...');
                const response = await axios.get('/api/busStopsView/bus-stops');
                console.log('Bus stops API response:', response.data);

                if(response.data && response.data.data) {
                    const stops = response.data.data;
                    console.log(`Received ${stops.length} bus stops`);
                    setBusStops(stops);

                    // If we have stops, set the map center to the first stop
                    if (stops.length > 0) {
                        setMapCenter([stops[0].latitude, stops[0].longitude]);
                    }
                } else {
                    console.error('Unexpected API response format:', response.data);
                    setError('Received an invalid response from the server.');
                }
            } catch (err) {
                console.error('Error fetching bus stops:', err);
                setError('Failed to load bus stops: ' + (err.response?.data?.message || err.message));
            } finally {
                setLoading(false);
            }
        };

        fetchBusStops();
    }, []);

    // Add click-outside handler to close dropdowns
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (fromDropdownRef.current && !fromDropdownRef.current.contains(event.target)) {
                setIsFromDropdownOpen(false);
            }
            if (toDropdownRef.current && !toDropdownRef.current.contains(event.target)) {
                setIsToDropdownOpen(false);
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => document.removeEventListener("mousedown", handleClickOutside);
    }, []);

    // Handle the search action for buses between stops
    const handleSearch = async () => {
        if (!fromStop || !toStop) {
            setError('Please select both From and To stops.');
            return;
        }

        if (fromStop === toStop) {
            setError('From and To stops cannot be the same.');
            return;
        }

        setLoading(true);
        try {
            const response = await axios.get(`/api/busStopsView/buses`, {
                params: { fromStopId: fromStop, toStopId: toStop }
            });

            if (response.data && response.data.data) {
                setBuses(response.data.data);

                // If there's at least one bus, pick the first bus location to show on the map
                const firstBus = response.data.data[0];
                if (firstBus && firstBus.location) {
                    setSelectedBusLocation(firstBus.location);
                    setMapCenter([firstBus.location.latitude, firstBus.location.longitude]);
                } else {
                    // If no bus location, center on the "from" stop
                    const fromStopObj = busStops.find(stop => stop.id === parseInt(fromStop));
                    if (fromStopObj) {
                        setMapCenter([fromStopObj.latitude, fromStopObj.longitude]);
                    }
                }
            }
            setError('');
        } catch (err) {
            console.error('Error fetching buses:', err);
            setError(err.response?.data?.message || 'No bus present on this route.');
            setBuses([]);
            setSelectedBusLocation(null);

            // On error, reset map center to the "from" stop
            const fromStopObj = busStops.find(stop => stop.id === parseInt(fromStop));
            if (fromStopObj) {
                setMapCenter([fromStopObj.latitude, fromStopObj.longitude]);
            }
        } finally {
            setLoading(false);
        }
    };

    // Helper function to find a stop by ID
    const findStopById = (id) => {
        return busStops.find(stop => stop.id === parseInt(id));
    };

    return (
        <div className="bus-stops-view-container">
            <div className="bus-stops-view-sidebar">
                <h2>Find Buses by Route</h2>
                <div className="stop-selectors">
                    <div>
                        <label>From:</label>
                        <div className="custom-dropdown" ref={fromDropdownRef}>
                            <input 
                                type="text" 
                                value={searchFrom}
                                onChange={(e) => {
                                    setSearchFrom(e.target.value);
                                    setIsFromDropdownOpen(true);
                                }}
                                onClick={() => setIsFromDropdownOpen(!isFromDropdownOpen)}
                                placeholder="Select From Stop"
                            />
                            {isFromDropdownOpen && (
                                <ul className="dropdown-list">
                                    {loading ? (
                                        <li className="loading-item">Loading bus stops...</li>
                                    ) : busStops.length > 0 ? (
                                        busStops
                                            .filter(stop => stop.name.toLowerCase().includes(searchFrom.toLowerCase()))
                                            .map(stop => (
                                                <li key={stop.id} onClick={() => {
                                                    setFromStop(stop.id);
                                                    setSearchFrom(stop.name);
                                                    setIsFromDropdownOpen(false);
                                                }}>
                                                    {stop.name}
                                                </li>
                                            ))
                                    ) : (
                                        <li className="error-item">No bus stops available</li>
                                    )}
                                </ul>
                            )}
                        </div>
                    </div>
                    <div>
                        <label>To:</label>
                        <div className="custom-dropdown" ref={toDropdownRef}>
                            <input 
                                type="text" 
                                value={searchTo}
                                onChange={(e) => {
                                    setSearchTo(e.target.value);
                                    setIsToDropdownOpen(true);
                                }}
                                onClick={() => setIsToDropdownOpen(!isToDropdownOpen)}
                                placeholder="Select To Stop"
                            />
                            {isToDropdownOpen && (
                                <ul className="dropdown-list">
                                    {loading ? (
                                        <li className="loading-item">Loading bus stops...</li>
                                    ) : busStops.length > 0 ? (
                                        busStops
                                            .filter(stop => stop.name.toLowerCase().includes(searchTo.toLowerCase()))
                                            .map(stop => (
                                                <li key={stop.id} onClick={() => {
                                                    setToStop(stop.id);
                                                    setSearchTo(stop.name);
                                                    setIsToDropdownOpen(false);
                                                }}>
                                                    {stop.name}
                                                </li>
                                            ))
                                    ) : (
                                        <li className="error-item">No bus stops available</li>
                                    )}
                                </ul>
                            )}
                        </div>
                    </div>
                    <button 
                        onClick={handleSearch} 
                        disabled={loading || !fromStop || !toStop}
                        className={loading ? "loading-button" : ""}
                    >
                        {loading ? "Loading..." : "Search"}
                    </button>
                </div>

                {error && <p className="error">{error}</p>}

                <div className="bus-results">
                    <h3>Available Buses</h3>
                    {buses.length > 0 ? (
                        buses.map((bus) => (
                            <div key={bus.id} className="bus-item">
                                <h4>{bus.name}</h4>
                                {bus.location ? (
                                    <p className="location-info">
                                        Last seen: {new Date(bus.location.timestamp).toLocaleTimeString()}
                                    </p>
                                ) : (
                                    <p className="location-info">Location: Unknown</p>
                                )}
                            </div>
                        ))
                    ) : (
                        <p className="no-results">No buses found for this route.</p>
                    )}
                </div>
            </div>

            <div className="bus-stops-view-map">
                <MapContainer
                    center={mapCenter}
                    zoom={15}
                    style={{ height: "100%", width: "100%" }}
                    ref={mapRef}
                >
                    <TileLayer
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />

                    {/* Show all bus stops on map */}
                    {busStops.map(stop => (
                        <Marker
                            key={stop.id}
                            position={[stop.latitude, stop.longitude]}
                            icon={stopIcon}
                        >
                            <Popup>
                                <strong>{stop.name}</strong>
                            </Popup>
                        </Marker>
                    ))}

                    {/* Show selected bus location if available */}
                    {selectedBusLocation && (
                        <Marker
                            position={[selectedBusLocation.latitude, selectedBusLocation.longitude]}
                            icon={busIcon}
                        >
                            <Popup>
                                <strong>Bus Location</strong><br />
                                Last seen: {new Date(selectedBusLocation.timestamp).toLocaleTimeString()}
                            </Popup>
                        </Marker>
                    )}
                </MapContainer>
            </div>
        </div>
    );
};

export default BusStopsView;
