import React, { useState, useEffect } from "react";
import { Container, Form, Button } from "react-bootstrap";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import api from '../utils/api';

const GOOGLE_CLIENT_ID = process.env.REACT_APP_GOOGLE_CLIENT_ID;

const LoginPage = ({ setUser }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [redirectCountdown, setRedirectCountdown] = useState(null);

  // Handle redirect countdown
  useEffect(() => {
    let timer;
    if (redirectCountdown !== null && redirectCountdown > 0) {
      timer = setTimeout(() => {
        setRedirectCountdown(redirectCountdown - 1);
      }, 1000);
    } else if (redirectCountdown === 0) {
      // Get destination from localStorage
      const destination = localStorage.getItem('redirectDestination');
      localStorage.removeItem('redirectDestination');

      // Redirect when countdown reaches zero
      window.location.replace(destination || '/');
    }

    return () => {
      if (timer) clearTimeout(timer);
    };
  }, [redirectCountdown]);

  const redirect_register = () => {
    try {
      window.location.href = "/signup";
    } catch (err) {
      console.log(err);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Basic validation
    if (!email.trim()) {
      alert("Email is required");
      setLoading(false);
      return;
    }

    if (!password.trim()) {
      alert("Password is required");
      setLoading(false);
      return;
    }

    try {
      const resp = await api.post('/login', {
        email: email,
        password: password,
      });

      if (resp.status === 200) {
        const token = resp.data.token;
        localStorage.setItem('jwtToken', token);
        localStorage.setItem('user', JSON.stringify(resp.data.user));

        // Update user state in parent component
        if (setUser) {
          setUser(resp.data.user);
        }

        // Get role and id for redirection
        const { role, id } = resp.data.user;

        if (!role || !id) {
          alert("Login successful but user role is missing. Please contact support.");
          setLoading(false);
          return;
        }

        // Set redirect destination
        const destination = `/logged_in/${role}/${id}`;
        localStorage.setItem('redirectDestination', destination);

        // Show success message with alert
        alert(`Login successful! Welcome ${resp.data.user.username}. Redirecting in 5 seconds...`);
        setRedirectCountdown(5);
      }
    } catch (err) {
      console.log(err);
      alert(err.response?.data?.message || "Invalid email or password. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const responseGoogle = async (response) => {
    setLoading(true);

    try {
      const decoded = jwtDecode(response.credential);
      const { email, name } = decoded;

      const resp = await api.post('/login_google', {
        email,
        username: name,
      });

      if (resp.status === 200) {
        const token = resp.data.token;
        localStorage.setItem('jwtToken', token);
        localStorage.setItem('user', JSON.stringify(resp.data.user));

        // Update user state in parent component
        if (setUser) {
          setUser(resp.data.user);
        }
        localStorage.setItem('user', JSON.stringify(resp.data.user));

        // Update user state in parent component
        if (setUser) {
          setUser(resp.data.user);
        }

        // Get role and id for redirection
        const { role, id } = resp.data.user;

        if (!role || !id) {
          setError("Login successful but user role is missing. Please contact support.");
          setLoading(false);
          return;
        }

        // Set redirect destination
        const destination = `/logged_in/${role}/${id}`;
        localStorage.setItem('redirectDestination', destination);

        // Show success message with countdown
        setSuccess(`Google login successful! Welcome ${resp.data.user.username}. Redirecting in 5 seconds...`);
        setRedirectCountdown(5);
      }
    } catch (err) {
      console.error("Google login error:", err);
      setError("Error signing in with Google. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="login-box">
      <h1>Welcome Back</h1>

      {/* Error message */}
      {error && <Alert variant="danger">{error}</Alert>}

      {/* Success message with countdown */}
      {success && (
        <Alert variant="success">
          {success}
          {redirectCountdown !== null && (
            <div className="countdown-timer mt-2">
              Redirecting in {redirectCountdown} seconds...
            </div>
          )}
        </Alert>
      )}

      <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
        <div className="google-loginbox">
          <GoogleLogin
            buttonText="Login with Google"
            onSuccess={responseGoogle}
            onError={(err) => {
              if (err.error === "popup_closed_by_user") {
                setError("Popup closed before completing login.");
              } else if (err.error === "idpiframe_initialization_failed") {
                setError("Google login initialization failed. Please check your internet connection.");
              } else {
                setError("Google login error. Please try again.");
                console.error("Google login error:", err);
              }
            }}
            disabled={loading || redirectCountdown !== null}
          />
        </div>
      </GoogleOAuthProvider>

      <div className="login-form">
        <h3 className="emailSignin">Sign in with your email</h3>
        <Form onSubmit={handleLogin}>
          <Form.Group controlId="formBasicEmail">
            <Form.Control
              className="email"
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              disabled={loading || redirectCountdown !== null}
            />
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Control
              className="password"
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              disabled={loading || redirectCountdown !== null}
            />
            <Button 
              variant="link" 
              onClick={() => setShowPassword(!showPassword)}
              className="show-password-toggle"
              disabled={loading || redirectCountdown !== null}
            >
              {showPassword ? "Hide" : "Show"} Password
            </Button>
          </Form.Group>

          <div className="forgot-password">
            <p>Forgot password?</p>
          </div>

          <Button 
            className="login-button" 
            type="submit" 
            variant="primary"
            disabled={loading || redirectCountdown !== null}
          >
            {loading ? "Logging in..." : "Login"}
          </Button>
        </Form>
      </div>

      <div className="noaccount">
        <p>
          Don't have an account?{" "}
          <span className="gotosignup" onClick={redirect_register} style={{ cursor: 'pointer' }}>
            Sign Up
          </span>
        </p>
      </div>
    </Container>
  );
};

export default LoginPage;
