import React, { useState, useEffect } from "react";
import { Container, Form, Button } from "react-bootstrap";
import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000';
const GOOGLE_API_KEY = process.env.REACT_APP_GOOGLE_API_KEY;

const SignUp = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  // Function to toggle password visibility
  const togglePasswordVisibility = () => setShowPassword(prev => !prev);
  const [googleApiLoaded, setGoogleApiLoaded] = useState(false);

  // We'll use this ref to access the google object once it's loaded
  const googleApiRef = React.useRef(null);

  useEffect(() => {
    // Define google as a property on window to prevent ESLint errors
    window.handleGoogleCredentialResponse = async (response) => {
      console.log("Google credential response:", response);

      try {
        // Send the ID token to your backend
        const result = await axios.post(`${BACKEND_URL}/google-signup`, {
          credential: response.credential
        }, { withCredentials: true });

        if (result.status === 201) {
          alert("Successfully Signed Up with Google! Please Sign In to Enjoy the Services!!");
          window.location.href = "/login";
        }
      } catch (err) {
        alert("Error signing up with Google");
        console.error("Error signing up with Google:", err);
      }
    };

    // Load Google API client
    const loadGoogleApi = () => {
      // Load the Google Identity Services script
      const script = document.createElement('script');
      script.src = "https://accounts.google.com/gsi/client";
      script.async = true;
      script.defer = true;

      script.onload = () => {
        console.log("Google Identity Services loaded successfully");
        googleApiRef.current = window.google;
        setGoogleApiLoaded(true);
      };

      script.onerror = () => {
        console.error("Failed to load Google Identity Services");
      };

      document.head.appendChild(script);
    };

    loadGoogleApi();

    // Cleanup function
    return () => {
      delete window.handleGoogleCredentialResponse;
    };
  }, []);

  const redirect_login = () => {
    window.location.href = "/login";
  };

  const handleSignUp = async (e) => {
    e.preventDefault();

    try {
      const resp = await axios.post(`${BACKEND_URL}/signup`, {
        username,
        email,
        password,
      },
        { withCredentials: true }
      );

      if (resp.status === 201) {
        alert("Successfully Signed Up! Please Sign In to Enjoy the Services!!");
        window.location.href = "/login";
      }
    } catch (err) {
      alert("Error signing up", err);
      console.log(err);
    }
  };

  const handleGoogleSignUp = () => {
    if (!googleApiLoaded || !googleApiRef.current) {
      alert("Google API is still loading. Please try again in a moment.");
      return;
    }

    // Initialize Google Sign-In
    googleApiRef.current.accounts.id.initialize({
      client_id: GOOGLE_API_KEY,
      callback: window.handleGoogleCredentialResponse,
      auto_select: false,
      cancel_on_tap_outside: true
    });

    // Display the One Tap UI
    googleApiRef.current.accounts.id.prompt((notification) => {
      if (notification.isNotDisplayed() || notification.isSkippedMoment()) {
        // Show the popup directly without rendering a second button
        googleApiRef.current.accounts.id.prompt();
      }
    });
  };

  return (
    <Container className="signup-box">
      <h1>Sign Up</h1>
      <div className="google-loginbox">
        {/* Container for Google's auto-rendered button - we'll hide this */}
        <div id="google-signin-button-container" style={{ display: 'none' }}></div>
        <Button
          className="google-signup-button"
          onClick={handleGoogleSignUp}
          disabled={!googleApiLoaded}
          variant="outline-primary"
        >
          Sign Up with Google
        </Button>
      </div>
      <div className="login-form">
        <Form onSubmit={handleSignUp}>
          <Form.Group controlId="formBasicName">
            <Form.Control
              className="username"
              placeholder="Enter User Name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicEmail">
            <Form.Control
              className="email"
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicPassword">
            <Form.Control
              className="password"
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicPasswordToggle">
            <Form.Check
              type="checkbox"
              label="Show Password"
              onChange={togglePasswordVisibility}
              checked={showPassword}
            />
          </Form.Group>
          <Button className="signup-button" type="submit" variant="primary">
            SignUp
          </Button>
        </Form>
      </div>
      <div className="noaccount">
        <p>
          Already have an account?{" "}
          <span className="gotosignup" onClick={redirect_login}>
            Sign In
          </span>
        </p>
      </div>
    </Container>
  );
};

export default SignUp;
