import React, { useState, useEffect } from 'react';
import axios from 'axios';
import api, { getApiUrl } from '../../utils/api2.js';

function SystemStatistics({ user }) {
  const [stats, setStats] = useState({
    totalUsers: 0,
    activeUsers: 0,
    totalBuses: 0,
    activeBuses: 0,
    totalRoutes: 0,
    totalStops: 0,
    totalDrivers: 0,
    recentLocations: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [timeRange, setTimeRange] = useState('day'); // day, week, month

  useEffect(() => {
    fetchStatistics();
  }, [timeRange, user.token]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchStatistics = async () => {
    try {
      setLoading(true);
      setError('');

      const response = await axios.get(
        `${getApiUrl(api.endpoints.adminStats)}?range=${timeRange}`,
        { headers: { Authorization: `Bearer ${user.token}` } }
      );

      if (response.data) {
        console.log('Statistics loaded from database:', response.data);
        setStats(response.data);
      } else {
        throw new Error("Invalid data format received");
      }
    } catch (err) {
      setError('Error loading statistics: ' + (err.response?.data?.message || err.message));
      console.error('Error fetching statistics:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Loading statistics...</div>;
  }

  return (
    <div className="system-statistics">
      <h2>System Statistics</h2>
      {error && <div className="error-message">{error}</div>}

      <div className="time-range-selector">
        <button
          className={timeRange === 'day' ? 'active' : ''}
          onClick={() => setTimeRange('day')}
        >
          Today
        </button>
        <button
          className={timeRange === 'week' ? 'active' : ''}
          onClick={() => setTimeRange('week')}
        >
          This Week
        </button>
        <button
          className={timeRange === 'month' ? 'active' : ''}
          onClick={() => setTimeRange('month')}
        >
          This Month
        </button>
      </div>

      <div className="stats-container">
        <div className="stat-cards">
          <div className="stat-card">
            <h3>Total Users</h3>
            <div className="stat-value">{stats.totalUsers || 0}</div>
            <div className="stat-detail">Active: {stats.activeUsers || 0}</div>
          </div>

          <div className="stat-card">
            <h3>Total Buses</h3>
            <div className="stat-value">{stats.totalBuses || 0}</div>
            <div className="stat-detail">Active: {stats.activeBuses || 0}</div>
          </div>

          <div className="stat-card">
            <h3>Routes</h3>
            <div className="stat-value">{stats.totalRoutes || 0}</div>
          </div>

          <div className="stat-card">
            <h3>Bus Stops</h3>
            <div className="stat-value">{stats.totalStops || 0}</div>
          </div>

          <div className="stat-card">
            <h3>Drivers</h3>
            <div className="stat-value">{stats.totalDrivers || 0}</div>
          </div>

          <div className="stat-card">
            <h3>Recent Locations</h3>
            <div className="stat-value">{stats.recentLocations || 0}</div>
            <div className="stat-detail">In the last hour</div>
          </div>
        </div>

        {stats.mostUsedRoutes && stats.mostUsedRoutes.length > 0 && (
          <div className="usage-section">
            <div className="usage-column">
              <h3>Most Used Routes</h3>
              <table>
                <thead>
                  <tr>
                    <th>Route</th>
                    <th>Usage</th>
                  </tr>
                </thead>
                <tbody>
                  {stats.mostUsedRoutes.map(route => (
                    <tr key={route.id}>
                      <td>{route.name}</td>
                      <td>{route.usage} trips</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {stats.leastUsedRoutes && stats.leastUsedRoutes.length > 0 && (
              <div className="usage-column">
                <h3>Least Used Routes</h3>
                <table>
                  <thead>
                    <tr>
                      <th>Route</th>
                      <th>Usage</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.leastUsedRoutes.map(route => (
                      <tr key={route.id}>
                        <td>{route.name}</td>
                        <td>{route.usage} trips</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        )}

        {stats.busUtilization && stats.busUtilization.length > 0 && (
          <div className="usage-charts">
            <h3>Bus Utilization by Time of Day</h3>
            <div className="bar-chart">
              {stats.busUtilization.map(item => (
                <div key={item.name} className="chart-bar-container">
                  <div className="chart-label">{item.name}</div>
                  <div className="chart-bar">
                    <div
                      className="chart-bar-fill"
                      style={{ width: `${item.value}%` }}
                    >
                      {item.value}%
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default SystemStatistics;
