import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import axios from 'axios';
import L from 'leaflet';

// Fix for default marker icon in Leaflet with React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

function MyLocation({ user }) {
  const [position, setPosition] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const mapRef = useRef();

  useEffect(() => {
    // Get current location
    if (navigator.geolocation) {
      const watchId = navigator.geolocation.watchPosition(
        (position) => {
          const newPosition = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          };
          setPosition(newPosition);
          setIsLoading(false);
          updateLocationToServer(newPosition);
        },
        (err) => {
          setError(`Error accessing location: ${err.message}`);
          setIsLoading(false);
        },
        { enableHighAccuracy: true, timeout: 15000, maximumAge: 10000 }
      );

      // Cleanup function to stop watching position
      return () => navigator.geolocation.clearWatch(watchId);
    } else {
      setError('Geolocation is not supported by your browser');
      setIsLoading(false);
    }
  }, []);

  const updateLocationToServer = async (position) => {
    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/location/update`,
        {
          latitude: position.latitude,
          longitude: position.longitude,
        },
        {
          headers: {
            Authorization: `Bearer ${user.token}`,
          },
        }
      );
    } catch (err) {
      console.error('Error updating location:', err);
    }
  };

  if (isLoading) {
    return <div>Loading your location...</div>;
  }

  if (error) {
    return <div style={{ color: 'red', padding: '20px' }}>{error}</div>;
  }

  return (
    <div>
      <h2 style={{ padding: '10px 20px' }}>My Current Location</h2>
      {position && (
        <MapContainer
          center={[position.latitude, position.longitude]}
          zoom={15}
          style={{ height: "calc(100vh - 100px)" }}
          ref={mapRef}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker position={[position.latitude, position.longitude]}>
            <Popup>
              You are here! <br />
              Lat: {position.latitude.toFixed(6)} <br />
              Lng: {position.longitude.toFixed(6)}
            </Popup>
          </Marker>
        </MapContainer>
      )}
    </div>
  );
}

export default MyLocation;
