import React, { useState, useEffect } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import api from '../utils/api';

const GOOGLE_CLIENT_ID = process.env.REACT_APP_GOOGLE_CLIENT_ID;

const LoginPage = ({ setUser }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const redirect_register = () => {
    try {
      window.location.href = "/signup";
    } catch (err) {
      console.log(err);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const resp = await api.post('/login', {
        email: email,
        password: password,
      });

      if (resp.status === 200) {
        const token = resp.data.token;
        localStorage.setItem('jwtToken', token);
        localStorage.setItem('user', JSON.stringify(resp.data.user));

        // Update user state in parent component if setUser is available
        if (setUser) {
          setUser(resp.data.user);
        }

        // Get role and id for redirection
        const { role, id } = resp.data.user;
        console.log('Login successful, redirecting to:', `/logged_in/${role}/${id}`);

        // Make sure role and id exist
        if (!role || !id) {
          console.error("Missing role or id in user data:", resp.data.user);
          setError("Login successful but couldn't determine where to redirect you. Please try again.");
          return;
        }

        // Use window.location.replace for cleaner navigation
        window.location.replace(`/logged_in/${role}/${id}`);
      }
    } catch (err) {
      console.log(err);
      setError(err.response?.data?.message || "Error signing in. Please check your credentials.");
    } finally {
      setLoading(false);
    }    
  };

  const responseGoogle = async (response) => {
    setError("");
    setLoading(true);

    try {
      const decoded = jwtDecode(response.credential);
      const { email, name } = decoded;

      const resp = await api.post('/login_google', {
        email,
        username: name,
      });

      if (resp.status === 200) {
        const token = resp.data.token;
        localStorage.setItem('jwtToken', token);
        localStorage.setItem('user', JSON.stringify(resp.data.user));

        // Update user state in parent component if setUser is available
        if (setUser) {
          setUser(resp.data.user);
        }

        // Get role and id for redirection
        const { role, id } = resp.data.user;
        console.log('Google login successful, redirecting to:', `/logged_in/${role}/${id}`);

        // Make sure role and id exist
        if (!role || !id) {
          console.error("Missing role or id in user data:", resp.data.user);
          setError("Login successful but couldn't determine where to redirect you. Please try again.");
          return;
        }

        // Use window.location.replace for cleaner navigation
        window.location.replace(`/logged_in/${role}/${id}`);
      }
    } catch (err) {
      console.error("Google login error:", err);
      setError("Error signing in with Google. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="login-box">
      <h1>Welcome Back</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
        <div className="google-loginbox">
          <GoogleLogin
            buttonText="Login with Google"
            onSuccess={responseGoogle}
            onError={(err) => {
              if (err.error === "popup_closed_by_user") {
                alert("Popup closed by user before completing login.");
              } else if (err.error === "idpiframe_initialization_failed") {
                alert("Initialization failed. Please check your Client ID and authorized origins.");
              } else {
                console.error("Google login error:", err);
              }
            }}
          />
        </div>
      </GoogleOAuthProvider>
      <div className="login-form">
        <h3 className="emailSignin">Sign in with your email</h3>
        <Form onSubmit={handleLogin}>
          <Form.Group controlId="formBasicEmail">
            <Form.Control
              className="email"
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Control
              className="password"
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button 
              variant="link" 
              onClick={() => setShowPassword(!showPassword)}
              className="show-password-toggle"
            >
              {showPassword ? "Hide" : "Show"} Password
            </Button>
          </Form.Group>
          <div className="forgot-password">
            <p>Forgot password?</p>
          </div>
          <Button 
            className="login-button" 
            type="submit" 
            variant="primary"
            disabled={loading}
          >
            {loading ? "Logging in..." : "Login"}
          </Button>
        </Form>
      </div>
      <div className="noaccount">
        <p>
          Don't have an account ?{" "}
          <span className="gotosignup" onClick={redirect_register}>
            Sign Up
          </span>
        </p>
      </div>
    </Container>
  );
};

export default LoginPage;
