import React, { useState, useEffect } from "react";
import { Container, Form, Button } from "react-bootstrap";
import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000';
const GOOGLE_API_KEY = process.env.REACT_APP_GOOGLE_API_KEY;

const SignUp = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [googleApiLoaded, setGoogleApiLoaded] = useState(false);

  // Function to toggle password visibility
  const togglePasswordVisibility = () => setShowPassword(prev => !prev);

  useEffect(() => {
    // Define the callback function for Google Auth
    window.handleGoogleCredentialResponse = async (response) => {
      console.log("Google credential response received");

      try {
        // Send the ID token to your backend
        const result = await axios.post(`${BACKEND_URL}/google-signup`, {
          credential: response.credential
        }, { withCredentials: true });

        if (result.status === 201) {
          alert("Successfully Signed Up with Google! Please Sign In to Enjoy the Services!!");
          window.location.href = "/login";
        }
      } catch (err) {
        alert("Error signing up with Google");
        console.error("Error signing up with Google:", err);
      }
    };

    // Load Google Identity Services script
    const loadGoogleApi = () => {
      // Check if script is already loaded
      if (document.querySelector('script[src="https://accounts.google.com/gsi/client"]')) {
        console.log("Google Identity Services already loaded");
        setGoogleApiLoaded(true);
        return;
      }

      const script = document.createElement('script');
      script.src = "https://accounts.google.com/gsi/client";
      script.async = true;
      script.defer = true;
      script.id = "google-client-script";

      script.onload = () => {
        console.log("Google Identity Services loaded successfully");
        setGoogleApiLoaded(true);
      };

      script.onerror = () => {
        console.error("Failed to load Google Identity Services");
      };

      document.head.appendChild(script);
    };

    loadGoogleApi();

    // Cleanup function
    return () => {
      delete window.handleGoogleCredentialResponse;

      // Optionally remove the script when component unmounts
      // const scriptElement = document.getElementById("google-client-script");
      // if (scriptElement) scriptElement.remove();
    };
  }, []);

  const redirect_login = () => {
    window.location.href = "/login";
  };

  const handleSignUp = async (e) => {
    e.preventDefault();

    try {
      const resp = await axios.post(`${BACKEND_URL}/signup`, {
        username,
        email,
        password,
      }, { withCredentials: true });

      if (resp.status === 201) {
        alert("Successfully Signed Up! Please Sign In to Enjoy the Services!!");
        window.location.href = "/login";
      }
    } catch (err) {
      alert("Error signing up");
      console.error("Error signing up:", err);
    }
  };

  const handleGoogleSignUp = () => {
    if (!googleApiLoaded) {
      alert("Google API is still loading. Please try again in a moment.");
      return;
    }

    if (!GOOGLE_API_KEY) {
      console.error("Google API Key is missing. Please check your environment variables.");
      alert("Google Sign-Up is not configured properly. Please try regular sign up.");
      return;
    }

    try {
      // Initialize Google Sign-In
      window.google.accounts.id.initialize({
        client_id: GOOGLE_API_KEY,
        callback: window.handleGoogleCredentialResponse,
        auto_select: false,
        cancel_on_tap_outside: true
      });

      // Create a customized button container if needed
      const googleButtonContainer = document.getElementById('google-signin-button');

      // Render a Google button (optional)
      // window.google.accounts.id.renderButton(googleButtonContainer, {
      //   type: 'standard',
      //   theme: 'outline',
      //   size: 'large'
      // });

      // Display the One Tap UI
      window.google.accounts.id.prompt((notification) => {
        if (notification.isNotDisplayed()) {
          console.log("One Tap not displayed", notification.getNotDisplayedReason());
        } else if (notification.isSkippedMoment()) {
          console.log("One Tap skipped", notification.getSkippedReason());
        } else if (notification.isDismissedMoment()) {
          console.log("One Tap dismissed", notification.getDismissedReason());
        }
      });
    } catch (error) {
      console.error("Error initializing Google Sign-In:", error);
      alert("Failed to initialize Google Sign-Up. Please try again later.");
    }
  };

  return (
    <Container className="signup-box">
      <h1>Sign Up</h1>
      <div className="google-loginbox">
        {/* Container for Google's button (if needed) */}
        <div id="google-signin-button"></div>
        <Button
          className="google-signup-button"
          onClick={handleGoogleSignUp}
          disabled={!googleApiLoaded}
          variant="outline-primary"
        >
          Sign Up with Google
        </Button>
      </div>
      <div className="login-form">
        <Form onSubmit={handleSignUp}>
          <Form.Group controlId="formBasicName">
            <Form.Control
              className="username"
              placeholder="Enter User Name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicEmail">
            <Form.Control
              className="email"
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicPassword">
            <Form.Control
              className="password"
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicPasswordToggle">
            <Form.Check
              type="checkbox"
              label="Show Password"
              onChange={togglePasswordVisibility}
              checked={showPassword}
            />
          </Form.Group>
          <Button className="signup-button" type="submit" variant="primary">
            SignUp
          </Button>
        </Form>
      </div>
      <div className="noaccount">
        <p>
          Already have an account?{" "}
          <span className="gotosignup" onClick={redirect_login}>
            Sign In
          </span>
        </p>
      </div>
    </Container>
  );
};

export default SignUp;
