import React, { useState, useEffect } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import api from '../utils/api';

const GOOGLE_CLIENT_ID = process.env.REACT_APP_GOOGLE_CLIENT_ID;

const SignUp = ({ setUser }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);
  const redirect_login = () => {
    window.location.href = "/login";
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const resp = await api.post('/signup', {
        username,
        email,
        password,
      });

      if (resp.status === 201) {
        alert("Successfully Signed Up! Please Sign In to Enjoy the Services!!");
        window.location.href = "/login";
      }
    } catch (err) {
      console.log(err);
      setError(err.response?.data?.message || "Error signing up. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const responseGoogle = async (response) => {
    try {
      const decoded = jwtDecode(response.credential);
      const { email, name } = decoded;

      try {
        const registerResp = await api.post('/login_google', {
          email,
          username: name,
        },
        { withCredentials: true });

        if (registerResp.status === 200) {
          const token = registerResp.data.token;
          localStorage.setItem('jwtToken', token);
          alert("Successfully logged in with Google!");

          // Consistent URL format
          const { role, id } = registerResp.data.user;
          window.location.href = `/logged_in/${role}/${id}`;
        }
      } catch (registerErr) {
        alert("Error sign up with Google", registerErr);
        console.log(registerErr);
      }
    } catch (decodeErr) {
      console.error("JWT decode error:", decodeErr);
    }
  };

  return (
    <Container className="signup-box">
      <h1>Sign Up</h1>
      {/* Display error message if present */}
      {error && <div className="alert alert-danger">{error}</div>}
      <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
        <div className="google-loginbox">
          <GoogleLogin
            onSuccess={responseGoogle}
            buttonText="Signin with Google"
            onError={(err) => {
              if (err.error === "popup_closed_by_user") {
                alert("Popup closed by user before completing login.");
              } else if (err.error === "idpiframe_initialization_failed") {
                alert("Initialization failed. Please check your Client ID and authorized origins.");
              } else {
                console.error("Google login error:", err);
              }
            }}
          />
        </div>
      </GoogleOAuthProvider>
      <div className="login-form">
        <Form onSubmit={handleSignUp}>
          <Form.Group controlId="formBasicName">
            <Form.Control
              className="username"
              placeholder="Enter User Name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicEmail">
            <Form.Control
              className="email"
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicPassword">
            <Form.Control
              className="password"
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button 
              variant="link" 
              onClick={() => setShowPassword(!showPassword)}
              className="show-password-toggle"
            >
              {showPassword ? "Hide" : "Show"} Password
            </Button>
          </Form.Group>
          <Button 
            className="signup-button" 
            type="submit" 
            variant="primary"
            disabled={loading} // Use loading state to disable button while submitting
          >
            {loading ? "Signing up..." : "SignUp"}
          </Button>
        </Form>
      </div>
      <div className="noaccount">
        <p>
          Already have an account?{" "}
          <span className="gotosignup" onClick={redirect_login}>
            Sign In
          </span>
        </p>
      </div>
    </Container>
  );
};

export default SignUp;
