import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import api, { getApiUrl } from '../../utils/api2.js';

// Fix for default marker icon in Leaflet with React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
    iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
    shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});


function configureRoutingMachine() {
    if (typeof L !== 'undefined' && L.Routing) {
        // Modify the global routing options
        L.Routing.Itinerary.prototype.options.createGeocoderPane = false;
        L.Routing.timeout = 30 * 1000; // 30 seconds timeout

        // Override the error handling globally
        if (L.Routing.ErrorControl && L.Routing.ErrorControl.prototype) {
            L.Routing.ErrorControl.prototype._routingErrorHandler = function (e) {
                console.warn("Handled routing error:", e);
                // No UI updates or errors thrown
            };
        }

        // Fix issues with the layer removal
        if (L.Routing.Line && L.Routing.Line.prototype) {
            const originalClearLines = L.Routing.Line.prototype._clearLines;
            L.Routing.Line.prototype._clearLines = function () {
                try {
                    // Safety check before calling the original function
                    if (this._map && this._route && this._route._layers) {
                        originalClearLines.call(this);
                    }
                } catch (e) {
                    console.warn("Protected from _clearLines error:", e);
                    // Manual cleanup as fallback
                    if (this._map && this._route) {
                        try {
                            this._map.removeLayer(this._route);
                        } catch (e) {
                            console.warn("Also failed with manual cleanup:", e);
                        }
                    }
                }
            };
        }
    }
}


// Component to recenter map when needed
function MapUpdater({ center, zoom }) {
    const map = useMap();

    useEffect(() => {
        if (center) {
            map.setView(center, zoom || map.getZoom());
        }
    }, [center, zoom, map]);

    return null;
}


// Custom component to create routes that follow roads
function RoadBasedRoutes({ stops }) { // Removed setDirections parameter
    const map = useMap();
    const controlRef = useRef(null);
    
    // A safer cleanup function we can call anytime
    const cleanup = useCallback(() => {
        if (controlRef.current) {
            try {
                // Always remove from map first
                if (map) map.removeControl(controlRef.current);
            } catch (e) {
                console.warn("Error removing control:", e);
            }
            controlRef.current = null;
        }
    }, [map]);

    // Set up once - clean up on unmount
    useEffect(() => {
        return cleanup;
    }, [cleanup]);

    // Handle route creation/updates
    useEffect(() => {
        // Skip if no map or insufficient stops
        if (!map || !stops || stops.length < 2) {
            console.log("Not enough conditions for route drawing:", {
                hasMap: !!map,
                stopsCount: stops?.length || 0
            });
            return;
        }

        // Clear any existing routes first
        cleanup();

        // Get waypoints from stops
        const waypoints = [];
        const orderedStops = [...stops].sort((a, b) => a.stop_order - b.stop_order);

        console.log("Drawing routes for ordered stops:", orderedStops);

        // Find the actual stop data using the global stops data
        for (const routeStop of orderedStops) {
            const stopData = window.stopsData?.find(s => s.id === routeStop.bus_stop_id);
            if (stopData) {
                const lat = parseFloat(stopData.latitude);
                const lng = parseFloat(stopData.longitude);
                if (!isNaN(lat) && !isNaN(lng)) {
                    waypoints.push(L.latLng(lat, lng));
                }
            }
        }

        // Add the first waypoint again for circular route
        if (waypoints.length >= 2) {
            waypoints.push(waypoints[0]);
        }

        console.log("Generated waypoints for circular route:", waypoints.length);

        // Only create route if we have at least 2 valid waypoints
        if (waypoints.length < 3) { // Now we need at least 3 (including repeat of first)
            console.log("Not enough valid waypoints for circular route");
            return;
        }

        // Create the OSRM routing control
        try {
            const control = L.Routing.control({
                lineOptions: {
                    styles: [{ color: '#3388ff', opacity: 0.7, weight: 5 }],
                    extendToWaypoints: true,
                    missingRouteTolerance: 10
                },
                createMarker: () => null, // No markers from routing
                addWaypoints: false,
                serviceUrl: 'https://router.project-osrm.org/route/v1'
            });

            // Handle errors and update directions
            control.on('routingerror', function (e) {
                console.warn("Routing error:", e);
                setDirections([{ 
                    text: "Could not calculate route directions. Please try again later.", 
                    type: "error" 
                }]);
            });

            control.on('routesfound', function (e) {
                console.log("Routes found via OSRM:", e.routes.length);
                
                if (e.routes && e.routes.length > 0) {
                    const route = e.routes[0]; // Get the first route
                    
                    // Extract instructions from the route
                    const directionsList = route.instructions.map((instruction, index) => {
                        return {
                            index: index + 1,
                            text: instruction.text,
                            distance: instruction.distance,
                            time: instruction.time,
                            direction: instruction.direction,
                            type: instruction.type
                        };
                    });
                    
                    // Update the directions state
                    setDirections(directionsList);
                }
            });

            // Store the reference
            controlRef.current = control;

            // Add the control to the map
            setTimeout(() => {
                if (map && controlRef.current) {
                    try {
                        controlRef.current.addTo(map);
                    } catch (e) {
                        console.warn("Error adding route control:", e);
                    }
                }
            }, 200);
        } catch (error) {
            console.error("Error creating routing control:", error);
            setDirections([{ 
                text: "Error creating route: " + error.message, 
                type: "error" 
            }]);
        }
    }, [map, stops, cleanup, setDirections]);

    return null;
}



function RouteManagement({ user }) {
    const [buses, setBuses] = useState([]);
    const [stops, setStops] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedBus, setSelectedBus] = useState(null);
    const [mapCenter, setMapCenter] = useState([22.3190, 87.3091]); // IIT KGP coordinates
    const [zoom, setZoom] = useState(15);
    const mapRef = useRef(null);
    const [refreshTrigger, setRefreshTrigger] = useState(0);
    const [routes, setRoutes] = useState([]);
    const [showMapDebug, setShowMapDebug] = useState(false); // State for debugging
    const [directions, setDirections] = useState([]); // New state for route directions
    const [mapLoading, setMapLoading] = useState(false); // New state for map loading

    const [formData, setFormData] = useState({
        bus_id: '',
        stops: []
    });

    const [isAddingStop, setIsAddingStop] = useState(false);
    const [currentStopOrder, setCurrentStopOrder] = useState(1);

    useEffect(() => {
        // Initialize window-level stopsData and configuration flag
        window.enableOSRM = true; // Always use OSRM
        configureRoutingMachine();

        // Add a global diagnostics object to help with debugging
        window.routeDiagnostics = {
            stops: [],
            waypoints: [],
            logState: () => {
                console.log("Current Map State:", {
                    stops: window.stopsData?.length || 0,
                    selectedBus,
                    routeStops: formData.stops.length,
                    mapInitialized: !!mapRef.current
                });
            }
        };
    }, []);

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                setError('');

                // Fetch buses
                try {
                    const busesResponse = await axios.get(getApiUrl(api.endpoints.adminBuses), {
                        headers: { Authorization: `Bearer ${user.token}` },
                        timeout: 10000
                    });
                    setBuses(busesResponse.data);
                    console.log("Buses loaded for routes:", busesResponse.data.length);
                } catch (err) {
                    setError('Failed to fetch buses: ' + err.message);
                    console.error("Error fetching buses for routes:", err);
                    setBuses([{ id: 'mock1', name: 'Demo Bus 1' }]); // Fallback data
                }

                // Fetch stops
                try {
                    const stopsResponse = await axios.get(getApiUrl(api.endpoints.adminStops), {
                        headers: { Authorization: `Bearer ${user.token}` },
                        timeout: 10000
                    });
                    setStops(stopsResponse.data);
                    console.log("Stops loaded for routes:", stopsResponse.data.length);
                } catch (err) {
                    setError('Failed to fetch stops: ' + err.message);
                    console.error("Error fetching stops for routes:", err);
                    setStops([{
                        id: 'mock1',
                        name: 'Demo Stop',
                        latitude: 22.3190,
                        longitude: 87.3091
                    }]); // Fallback data
                }

                // Fetch routes and update your state
                try {
                    const routesResponse = await axios.get(getApiUrl(api.endpoints.adminRoutes), {
                        headers: { Authorization: `Bearer ${user.token}` },
                        timeout: 10000
                    });
                    // Using the fetched routes data
                    const fetchedRoutes = routesResponse.data;
                    setRoutes(fetchedRoutes);
                    console.log("Routes loaded:", fetchedRoutes.length);
                } catch (err) {
                    setError('Failed to fetch routes: ' + err.message);
                    console.error("Error fetching routes:", err);
                    // Using default empty array for routes
                    setRoutes([]);
                }
            } catch (err) {
                setError('Error fetching data: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [user.token, refreshTrigger]);

    useEffect(() => {
        if (stops && stops.length > 0) {
            window.stopsData = stops;
        }
    }, [stops]);


    const handleBusSelect = async (busId) => {
        try {
            setSelectedBus(busId);
            setLoading(true);
            setMapLoading(true); // Start map loading
            setError('');
            setDirections([]); // Clear directions when changing bus

            try {
                const response = await axios.get(
                    getApiUrl(api.endpoints.adminRouteById(busId)),
                    { headers: { Authorization: `Bearer ${user.token}` } }
                );

                const busRoutes = response.data;
                console.log('Routes loaded for bus', busId, busRoutes);

                setFormData({
                    bus_id: busId,
                    stops: busRoutes
                });

                // Set current stop order for adding new stops
                setCurrentStopOrder(
                    busRoutes && busRoutes.length > 0
                        ? Math.max(...busRoutes.map(stop => stop.stop_order)) + 1
                        : 1
                );

                // Find stop coordinates to center map
                if (busRoutes && busRoutes.length > 0) {
                    fitRouteOnMap(busRoutes);
                }
                
                // Set a timer to ensure map loading state shows for at least a moment
                // (in case the map loads very quickly)
                setTimeout(() => {
                    setMapLoading(false);
                }, 1000);
            } catch (err) {
                setError('Error fetching route data: ' + err.message);
                console.error("Error fetching routes for bus:", err);
                setMapLoading(false); // End map loading on error
            }
        } catch (err) {
            setError('Error preparing bus route view: ' + err.message);
            setMapLoading(false); // End map loading on error
        } finally {
            setLoading(false);
        }
    };

    const handleAddStop = async (stopId) => {
        try {
            setError('');
            setMapLoading(true); // Start map loading for route update
            const stop = stops.find(s => s.id === stopId);
            if (!stop) {
                throw new Error("Stop not found");
            }

            // Create new route stop entry
            const newRoute = {
                bus_id: selectedBus,
                bus_stop_id: stopId,
                stop_order: currentStopOrder
            };

            const response = await axios.post(
                getApiUrl(api.endpoints.adminAddRoute),
                newRoute,
                { headers: { Authorization: `Bearer ${user.token}` } }
            );

            if (response.data && response.data.id) {
                // Use the complete object returned from API
                const updatedStops = [...formData.stops, response.data];
                setFormData(prev => ({
                    ...prev,
                    stops: updatedStops
                }));

                setRoutes(prev => [...prev, response.data]);
                setCurrentStopOrder(currentStopOrder + 1);
                setDirections([]); // Clear directions as they'll be recalculated

                // Update the map to show the new route
                fitRouteOnMap(updatedStops);
            }

            setIsAddingStop(false);
            setTimeout(() => {
                setMapLoading(false);
            }, 1000);
        } catch (err) {
            setError('Failed to add stop to route: ' + err.message);
            console.error("Error adding stop to route:", err);
            setMapLoading(false);
        }
    };

    const handleRemoveStop = async (routeId) => {
        if (!window.confirm('Are you sure you want to remove this stop from the route?')) {
            return;
        }

        try {
            setMapLoading(true); // Start map loading for route update
            await axios.delete(
                getApiUrl(api.endpoints.adminDeleteRoute(routeId)),
                { headers: { Authorization: `Bearer ${user.token}` } }
            );

            // Update local state
            const updatedStops = formData.stops.filter(stop => stop.id !== routeId);

            setFormData(prev => ({
                ...prev,
                stops: updatedStops
            }));

            setRoutes(prev => prev.filter(route => route.id !== routeId));
            setDirections([]); // Clear directions as they'll be recalculated

            // Update the map
            fitRouteOnMap(updatedStops);
            
            setTimeout(() => {
                setMapLoading(false);
            }, 1000);
        } catch (err) {
            setError('Error removing stop: ' + err.message);
            console.error("Error removing stop from route:", err);
            setMapLoading(false);
        }
    };

    const reorderStop = async (routeId, currentOrder, newOrder) => {
        if (currentOrder === newOrder) return;

        try {
            setMapLoading(true); // Start map loading for route update
            const reorderedStops = [...formData.stops];

            // Find stops that need order changes
            const stopToMove = reorderedStops.find(s => s.id === routeId);

            // Skip if stop not found
            if (!stopToMove) {
                setMapLoading(false);
                return;
            }

            // Update the order
            stopToMove.stop_order = newOrder;

            // Reorder other stops to maintain sequence
            reorderedStops.forEach(stop => {
                if (stop.id !== routeId) {
                    if (currentOrder < newOrder && stop.stop_order > currentOrder && stop.stop_order <= newOrder) {
                        // Moving down - shift others up
                        stop.stop_order -= 1;
                    } else if (currentOrder > newOrder && stop.stop_order >= newOrder && stop.stop_order < currentOrder) {
                        // Moving up - shift others down
                        stop.stop_order += 1;
                    }
                }
            });

            // Update in the database
            await axios.put(
                getApiUrl(api.endpoints.adminUpdateRoute(routeId)),
                { stop_order: newOrder },
                { headers: { Authorization: `Bearer ${user.token}` } }
            );

            // Update other affected stops in the database
            for (const stop of reorderedStops) {
                if (stop.id !== routeId &&
                    ((currentOrder < newOrder && stop.stop_order > currentOrder && stop.stop_order <= newOrder) ||
                        (currentOrder > newOrder && stop.stop_order >= newOrder && stop.stop_order < currentOrder))) {
                    await axios.put(
                        getApiUrl(api.endpoints.adminUpdateRoute(stop.id)),
                        { stop_order: stop.stop_order },
                        { headers: { Authorization: `Bearer ${user.token}` } }
                    );
                }
            }

            // Update local state with reordered stops
            setFormData(prev => ({
                ...prev,
                stops: reorderedStops
            }));

            setDirections([]); // Clear directions as they'll be recalculated

            // Update routes state
            setRoutes(prev => {
                const updatedRoutes = prev.filter(r => !reorderedStops.some(s => s.id === r.id));
                return [...updatedRoutes, ...reorderedStops];
            });
            
            // Force a re-render of the table by triggering a refresh
            setRefreshTrigger(prev => prev + 1);
            
            setTimeout(() => {
                setMapLoading(false);
            }, 1000);

        } catch (err) {
            setError('Error reordering stops: ' + err.message);
            console.error("Error reordering stops:", err);
            setMapLoading(false);
        }
    };

    const fitRouteOnMap = (routeStops) => {
        if (!routeStops || routeStops.length === 0 || !mapRef.current) return;

        // Get all stop coordinates, ensuring we have proper numeric values
        const stopPositions = routeStops.map(routeStop => {
            const stop = stops.find(s => s.id === routeStop.bus_stop_id);
            if (!stop) return null;

            const lat = parseFloat(stop.latitude);
            const lng = parseFloat(stop.longitude);
            if (isNaN(lat) || isNaN(lng)) return null;

            return [lat, lng];
        }).filter(Boolean);

        if (stopPositions.length === 0) return;

        if (stopPositions.length === 1) {
            // If only one stop, center on it
            setMapCenter(stopPositions[0]);
            setZoom(16);
        } else {
            // Calculate bounds for multiple stops
            const minLat = Math.min(...stopPositions.map(pos => pos[0]));
            const maxLat = Math.max(...stopPositions.map(pos => pos[0]));
            const minLng = Math.min(...stopPositions.map(pos => pos[1]));
            const maxLng = Math.max(...stopPositions.map(pos => pos[1]));

            // Center the map on the middle of the route
            setMapCenter([
                minLat + (maxLat - minLat) / 2,
                minLng + (maxLng - minLng) / 2
            ]);

            // Calculate zoom level based on distance
            const latDiff = maxLat - minLat;
            const lngDiff = maxLng - minLng;
            const maxDiff = Math.max(latDiff, lngDiff);

            // Simple zoom calculation, adjust as needed
            if (maxDiff < 0.005) setZoom(16);
            else if (maxDiff < 0.01) setZoom(15);
            else if (maxDiff < 0.02) setZoom(14);
            else setZoom(13);
        }
    };

    const getStopById = (stopId) => {
        return stops.find(stop => stop.id === stopId);
    };

    const getBusById = (busId) => {
        return buses.find(bus => bus.id === busId);
    };

    // Add stop marker icon based on whether it's start, middle, or end
    const getStopIcon = (stopOrder, totalStops) => {
        const iconUrl = stopOrder === 1 ?
            'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-green.png' :
            (stopOrder === totalStops ?
                'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png' :
                'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png');

        return L.icon({
            iconUrl,
            shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
            iconSize: [25, 41],
            iconAnchor: [12, 41],
            popupAnchor: [1, -34],
            shadowSize: [41, 41]
        });
    };

    if (loading && !buses.length && !stops.length) {
        return <div>Loading route data...</div>;
    }

    return (
        <div className="route-management">
            <h2>Route Management</h2>
            {error && <div className="error-message">{error}</div>}

            <div className="action-buttons">
                <button
                    className="refresh-button"
                    onClick={() => setRefreshTrigger(prev => prev + 1)}
                    disabled={loading}
                >
                    {loading ? 'Refreshing...' : 'Refresh Data'}
                </button>

                <button onClick={() => setShowMapDebug(!showMapDebug)}>
                    {showMapDebug ? "Hide Debug Info" : "Show Debug Info"}
                </button>
            </div>

            {showMapDebug && (
                <div className="debug-info" style={{ padding: '10px', backgroundColor: '#f0f0f0', margin: '10px 0', borderRadius: '5px' }}>
                    <h4>Route Debug Information</h4>
                    <p>Buses Loaded: {buses.length}</p>
                    <p>Stops Loaded: {stops.length}</p>
                    <p>Selected Bus: {selectedBus ? getBusById(selectedBus)?.name : 'None'}</p>
                    <p>Route Stops: {formData.stops.length}</p>
                    <p>Map Center: {mapCenter.join(', ')}</p>
                    <button onClick={() => {
                        window.routeDiagnostics.logState();
                        console.log("Current formData:", formData);
                    }}>
                        Log Debug Info to Console
                    </button>
                </div>
            )}

            <div className="route-management-content">
                <div className="bus-selector">
                    <h3>Select Bus to Manage Route</h3>
                    <div className="bus-list">
                        {buses.map(bus => (
                            <button
                                key={bus.id}
                                className={selectedBus === bus.id ? 'selected' : ''}
                                onClick={() => handleBusSelect(bus.id)}
                            >
                                {bus.name}
                            </button>
                        ))}
                    </div>
                </div>

                {selectedBus && (
                    <div className="route-editor">
                        <h3>Route for {getBusById(selectedBus)?.name}</h3>

                        {formData.stops.length === 0 ? (
                            <p>No stops assigned to this route. Add stops below.</p>
                        ) : (
                            <table className="route-stops-table">
                                <thead>
                                    <tr>
                                        <th>Order</th>
                                        <th>Stop Name</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {[...formData.stops]
                                        .sort((a, b) => a.stop_order - b.stop_order)
                                        .map(routeStop => {
                                            const stop = getStopById(routeStop.bus_stop_id);
                                            return (
                                                <tr key={`${routeStop.id}-${refreshTrigger}`}>
                                                    <td>{routeStop.stop_order}</td>
                                                    <td>{stop ? stop.name : 'Unknown Stop'}</td>
                                                    <td>
                                                        <button
                                                            onClick={() => reorderStop(
                                                                routeStop.id,
                                                                routeStop.stop_order,
                                                                routeStop.stop_order - 1
                                                            )}
                                                            disabled={routeStop.stop_order === 1}
                                                        >
                                                            ↑
                                                        </button>
                                                        <button
                                                            onClick={() => reorderStop(
                                                                routeStop.id,
                                                                routeStop.stop_order,
                                                                routeStop.stop_order + 1
                                                            )}
                                                            disabled={routeStop.stop_order === formData.stops.length}
                                                        >
                                                            ↓
                                                        </button>
                                                        <button onClick={() => handleRemoveStop(routeStop.id)}>
                                                            Remove
                                                        </button>
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                </tbody>
                            </table>
                        )}

                        <div className="add-stop-section">
                            <button
                                onClick={() => setIsAddingStop(!isAddingStop)}
                                className="add-stop-button"
                            >
                                {isAddingStop ? 'Cancel Adding Stop' : 'Add Stop to Route'}
                            </button>

                            {isAddingStop && (
                                <div className="available-stops">
                                    <h4>Select a stop to add:</h4>
                                    <div className="stops-grid">
                                        {stops
                                            .filter(stop => !formData.stops.some(s => s.bus_stop_id === stop.id))
                                            .map(stop => (
                                                <button
                                                    key={stop.id}
                                                    onClick={() => handleAddStop(stop.id)}
                                                    className="stop-option"
                                                >
                                                    {stop.name}
                                                </button>
                                            ))}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                )}

                <div className="route-map-container">
                    {/* Loading overlay for the map */}
                    {mapLoading && (
                        <div className="map-loading-overlay" style={{
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            width: '100%',
                            height: '100%',
                            backgroundColor: 'rgba(255, 255, 255, 0.8)',
                            display: 'flex',
                            justifyContent: 'center',
                            alignItems: 'center',
                            zIndex: 1000,
                            fontSize: '1.2rem',
                            fontWeight: 'bold'
                        }}>
                            <div>Loading route map...</div>
                        </div>
                    )}
                    
                    <MapContainer
                        center={mapCenter}
                        zoom={zoom}
                        className="map-container map-container-large"
                        ref={mapRef}
                        key={`map-${selectedBus || 'default'}-${refreshTrigger}`}
                        style={{ position: 'relative' }}
                    >
                        <TileLayer
                            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />

                        <MapUpdater center={mapCenter} zoom={zoom} />

                        {/* Add our road-based routes component */}
                        {selectedBus && formData.stops && formData.stops.length >= 2 && (
                            <RoadBasedRoutes stops={formData.stops} setDirections={setDirections} />
                        )}

                        {/* Display all stops on the map */}
                        {stops.map(stop => {
                            // Check if this stop is in the selected bus's route
                            const routeStopInfo = selectedBus && formData.stops.find(s => s.bus_stop_id === stop.id);
                            const isInRoute = !!routeStopInfo;
                            const stopOrder = isInRoute ? routeStopInfo.stop_order : null;
                            const totalStops = formData.stops.length;

                            return (
                                <Marker
                                    key={stop.id}
                                    position={[stop.latitude, stop.longitude]}
                                    icon={isInRoute ? getStopIcon(stopOrder, totalStops) : new L.Icon.Default()}
                                >
                                    <Popup>
                                        <div>
                                            <strong>{stop.name}</strong><br />

                                            {isInRoute ? (
                                                <span className="in-route">
                                                    Stop #{stopOrder} in route<br />
                                                    <button onClick={() => handleRemoveStop(routeStopInfo.id)}>
                                                        Remove from Route
                                                    </button>
                                                </span>
                                            ) : (
                                                selectedBus && isAddingStop && (
                                                    <button onClick={() => handleAddStop(stop.id)}>
                                                        Add to Route
                                                    </button>
                                                )
                                            )}
                                        </div>
                                    </Popup>
                                </Marker>
                            );
                        })}
                    </MapContainer>

                    {/* Route status indicator */}
                    {selectedBus && (
                        <div className="route-status" style={{ textAlign: 'center', marginTop: '10px' }}>
                            <p>
                                <strong>Route Status:</strong> {formData.stops.length < 2
                                    ? "Add at least 2 stops to create a route"
                                    : `Route with ${formData.stops.length} stops`}
                            </p>
                        </div>
                    )}

                    {/* Display route directions */}
                    {directions.length > 0 && (
                        <div className="route-directions" style={{ marginTop: '10px' }}>
                            <h4>Route Directions</h4>
                            <ul>
                                {directions.map((direction, index) => (
                                    <li key={index}>
                                        {direction.index}. {direction.text} ({direction.distance} meters, {direction.time} seconds)
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default RouteManagement;
