import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import axios from 'axios';
import L from 'leaflet';

// Fix for default marker icon in Leaflet with React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

function AdminDashboard({ user }) {
  const [userLocations, setUserLocations] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  // Default center position for India
  const defaultCenter = [20.5937, 78.9629];

  useEffect(() => {
    const fetchUserLocations = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/location/all`,
          {
            headers: {
              Authorization: `Bearer ${user.token}`,
            },
          }
        );
        setUserLocations(response.data);
        setIsLoading(false);
      } catch (err) {
        setError('Error fetching user locations');
        setIsLoading(false);
      }
    };

    // Fetch initially
    fetchUserLocations();

    // Set up interval to fetch locations every 5 seconds
    const interval = setInterval(fetchUserLocations, 5000);

    // Cleanup interval
    return () => clearInterval(interval);
  }, [user.token]);

  // Find map center - use first user location or default
  const mapCenter = userLocations.length > 0 
    ? [userLocations[0].coordinates.latitude, userLocations[0].coordinates.longitude]
    : defaultCenter;

  if (isLoading) {
    return <div>Loading user locations...</div>;
  }

  return (
    <div>
      <h2 style={{ padding: '10px 20px' }}>Admin Dashboard - All User Locations</h2>
      {error && <div style={{ color: 'red', padding: '0 20px' }}>{error}</div>}

      {userLocations.length === 0 ? (
        <div style={{ padding: '0 20px' }}>No user locations available</div>
      ) : (
        <div>
          <div style={{ padding: '0 20px', marginBottom: '10px' }}>
            <h3>Users tracked: {userLocations.length}</h3>
          </div>
          <MapContainer
            center={mapCenter}
            zoom={5}
            style={{ height: "calc(100vh - 150px)" }}
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {userLocations.map((location) => (
              <Marker
                key={location._id}
                position={[location.coordinates.latitude, location.coordinates.longitude]}
              >
                <Popup>
                  <div>
                    <strong>User:</strong> {location.username}<br />
                    <strong>Last updated:</strong>{' '}
                    {new Date(location.timestamp).toLocaleString()}<br />
                    <strong>Location:</strong> {location.coordinates.latitude.toFixed(6)}, {location.coordinates.longitude.toFixed(6)}
                  </div>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
