import React, { useState, useEffect } from "react";
import { Container, Form, Button } from "react-bootstrap";
import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000';
const GOOGLE_API_KEY = process.env.REACT_APP_GOOGLE_API_KEY;

const SignUp = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [googleApiLoaded, setGoogleApiLoaded] = useState(false);

  useEffect(() => {
    // Load Google API client
    const loadGoogleApi = () => {
      // Load the Google Identity Services script
      const script = document.createElement('script');
      script.src = "https://accounts.google.com/gsi/client";
      script.async = true;
      script.defer = true;

      script.onload = () => {
        console.log("Google Identity Services loaded successfully");
        setGoogleApiLoaded(true);
      };

      script.onerror = () => {
        console.error("Failed to load Google Identity Services");
      };

      document.head.appendChild(script);
    };

    loadGoogleApi();
  }, []);

  const redirect_login = () => {
    window.location.href = "/login";
  };

  const handleSignUp = async (e) => {
    e.preventDefault();

    try {
      const resp = await axios.post(`${BACKEND_URL}/signup`, {
        username,
        email,
        password,
      },
        { withCredentials: true }
      );

      if (resp.status === 201) {
        alert("Successfully Signed Up! Please Sign In to Enjoy the Services!!");
        window.location.href = "/login";
      }
    } catch (err) {
      alert("Error signing up", err);
      console.log(err);
    }
  };

  const handleGoogleSignUp = () => {
    if (!googleApiLoaded) {
      alert("Google API is still loading. Please try again in a moment.");
      return;
    }

    // Initialize Google Sign-In
    const client = google.accounts.id.initialize({
      client_id: GOOGLE_API_KEY,
      callback: handleGoogleCredentialResponse,
      auto_select: false,
      cancel_on_tap_outside: true
    });

    // Display the One Tap UI
    google.accounts.id.prompt((notification) => {
      if (notification.isNotDisplayed() || notification.isSkippedMoment()) {
        // One Tap is not displayed, fallback to popup
        google.accounts.id.renderButton(
          document.getElementById("google-signin-button-container"),
          {
            theme: "outline",
            size: "large",
            type: "standard",
            text: "signup_with",
            shape: "rectangular",
            width: 250
          }
        );

        // Show the popup directly
        google.accounts.id.prompt();
      }
    });
  };

  const handleGoogleCredentialResponse = async (response) => {
    console.log("Google credential response:", response);

    try {
      // Send the ID token to your backend
      const result = await axios.post(`${BACKEND_URL}/google-signup`, {
        credential: response.credential
      }, { withCredentials: true });

      if (result.status === 201) {
        alert("Successfully Signed Up with Google! Please Sign In to Enjoy the Services!!");
        window.location.href = "/login";
      }
    } catch (err) {
      alert("Error signing up with Google");
      console.error("Error signing up with Google:", err);
    }
  };

  return (
    <Container className="signup-box">
      <h1>Sign Up</h1>
      <div className="google-loginbox">
        <div id="google-signin-button-container"></div>
        <Button
          className="google-signup-button"
          onClick={handleGoogleSignUp}
          disabled={!googleApiLoaded}
          variant="outline-primary"
        >
          Sign Up with Google
        </Button>
      </div>
      <div className="login-form">
        <Form onSubmit={handleSignUp}>
          <Form.Group controlId="formBasicName">
            <Form.Control
              className="username"
              placeholder="Enter User Name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicEmail">
            <Form.Control
              className="email"
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicPassword">
            <Form.Control
              className="password"
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          <Button className="signup-button" type="submit" variant="primary">
            SignUp
          </Button>
        </Form>
      </div>
      <div className="noaccount">
        <p>
          Already have an account?{" "}
          <span className="gotosignup" onClick={redirect_login}>
            Sign In
          </span>
        </p>
      </div>
    </Container>
  );
};

export default SignUp;
